INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('DSSGN820-014-120823FWT', NULL, NULL, 'hội trường thống nhất, nhà thờ đức bà, bưu điện trung tâm HCM, nhà hát thành phố, bảo tàng HCM, bến nhà rồng, bến bạch đằng', 'Cặp đôi, Gia đình nhiều thế hệ, Thanh niên', NULL, 0, NULL, NULL, 'DSSGN820-014-120823FWT.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'NDSGN846-108-100823XE-D.jpg', 'DSSGN819-013-130823XE-V-F.jpg', NULL, '<h3>Ng&agrave;y 1 - FREE WALKING TOUR - K&Yacute; ỨC S&Agrave;I G&Ograve;N XƯA</h3>\r\n<div>\r\n    <div><strong>16g00:&nbsp;</strong>Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<strong>Diamond Plaza&nbsp;</strong>(34 L&ecirc; Duẩn, Phường Bến Ngh&eacute;, Quận 1, TPHCM) bắt đầu chuyến h&agrave;nh tr&igrave;nh<br>- Chi&ecirc;m ngưỡng <strong>Hội trường Thống Nhất:</strong> c&oacute; t&ecirc;n gọi cũ l&agrave; Dinh Độc Lập, một trong những nơi lưu giữ k&yacute; ức S&agrave;i G&ograve;n mang vẻ đẹp tr&aacute;ng lệ hợp thời với kiến tr&uacute;c được thiết kế th&ocirc;ng minh, tinh xảo từ Kiến tr&uacute;c sư t&agrave;i ba Ng&ocirc; Viết Thụ.&nbsp;</div>\r\n    <div><br>- Chụp h&igrave;nh b&ecirc;n ngo&agrave;i <strong>Nh&agrave; thờ Đức B&agrave;:&nbsp;</strong>nổi bật giữa l&ograve;ng th&agrave;nh phố với m&agrave;u cam gạch cổ điển c&ugrave;ng kiến tr&uacute;c th&aacute;p chu&ocirc;ng được thiết kế đặc biệt,<strong>&nbsp;nh&agrave; thờ Đức B&agrave; -</strong> Vương cung th&aacute;nh đường ch&iacute;nh t&ograve;a Đức Mẹ V&ocirc; nhiễm Nguy&ecirc;n tội được x&acirc;y dựng v&agrave;o năm 1877, l&agrave; c&ocirc;ng tr&igrave;nh kiến tr&uacute;c c&ocirc;ng gi&aacute;o độc đ&aacute;o bậc nhất S&agrave;i Th&agrave;nh v&agrave; h&igrave;nh mẫu x&acirc;y dựng theo Nh&agrave; thờ Đức b&agrave; ở Paris.</div>\r\n    <div><br>-&nbsp;<strong>Bưu điện trung t&acirc;m Th&agrave;nh Phố:</strong> Một trong những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c được đ&aacute;nh gi&aacute; tuyệt nhất Th&agrave;nh phố Hồ Ch&iacute; Minh với &nbsp;những n&eacute;t hoa văn tinh tế đặt để tr&ecirc;n từng chi tiết của kiến tr&uacute;c sư người Ph&aacute;p đ&atilde; khiến cho bất cứ du kh&aacute;ch n&agrave;o gh&eacute; qua đ&acirc;y đều kh&ocirc;ng muốn về.<strong>&nbsp;<br>Check-in Kh&ocirc;ng gian đường s&aacute;ch:&nbsp;</strong>hơn 20 gian h&agrave;ng s&aacute;ch với m&agrave;u sắc sặc sỡ được thiết kế phong c&aacute;ch kh&aacute;c nhau, hứa hẹn sẽ đem đến cho du kh&aacute;ch những bức h&igrave;nh mới lạ.</div>\r\n    <div><br>- Check-in&nbsp;<strong>Nh&agrave; h&aacute;t Th&agrave;nh Phố:</strong> l&agrave; một địa danh văn ho&aacute; lịch sử l&acirc;u đời, được khởi c&ocirc;ng v&agrave;o năm 1898, nổi bật với những tượng ph&ugrave; đi&ecirc;u v&agrave; tượng nổi được đi&ecirc;u khắc tỉ mỉ. Qu&yacute; kh&aacute;ch tham quan v&agrave; chụp h&igrave;nh b&ecirc;n ngo&agrave;i.<br>Đo&agrave;n c&oacute; dịp ngang qua 1 loạt c&aacute;c kh&aacute;ch sạn nổi tiếng bậc nhất tại S&agrave;i G&ograve;n như&nbsp;<strong>Continental Saigon, Caravelle, Grand hotel, Majetis&hellip;&hellip;&nbsp;</strong>l&agrave; những kh&aacute;ch sạn c&oacute; lịch sử l&acirc;u đời</div>\r\n    <div><br>-&nbsp;<strong>C&ocirc;ng vi&ecirc;n Bến Bạch Đằng</strong>: tiếp gi&aacute;p với s&ocirc;ng S&agrave;i G&ograve;n, c&oacute; hướng nh&igrave;n sang bờ s&ocirc;ng Thủ Thi&ecirc;m, TP.Thủ Đức.</div>\r\n    <div><br>-&nbsp;<strong>Cột cờ Thủ Ngữ</strong>: mang trong m&igrave;nh gi&aacute; trị lịch sử, l&agrave; nh&acirc;n chứng cho sự ph&aacute;t triển li&ecirc;n tục của v&ugrave;ng đất Gia Định-S&agrave;i G&ograve;n.<br><strong>Dọc theo bến Bạch Đằng,&nbsp;</strong>Qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng vẻ đẹp của&nbsp;<strong>ng&acirc;n h&agrave;ng nh&agrave; nước Việt Nam</strong> - ng&acirc;n h&agrave;ng&nbsp;<strong>được s&aacute;ng lập bởi một trong &ldquo;Tứ đại ph&uacute; h&agrave;o&rdquo;</strong> S&agrave;i G&ograve;n xưa v&agrave; đi qua&nbsp;<strong>Cầu Mống</strong>: chi&ecirc;m ngưỡng khung cảnh l&atilde;ng mạn b&ecirc;n s&ocirc;ng S&agrave;i G&ograve;n với h&agrave;ng c&acirc;y xanh rũ b&oacute;ng, khẽ đưa theo từng đợt gi&oacute; dẫn lối đến&nbsp;<strong>Bến Nh&agrave; Rồng - Bảo T&agrave;ng Hồ Ch&iacute; Minh</strong>: một di t&iacute;ch lịch sử nổi bật gắn liền với Cố Chủ Tịch Hồ Ch&iacute; Minh.</div>\r\n    <div><br><strong>-&nbsp;</strong>Dạo bước dọc&nbsp;<strong>Phố đi bộ Nguyễn Huệ</strong>:&nbsp;chi&ecirc;m ngưỡng những c&ocirc;ng tr&igrave;nh biểu tượng của TP.HCM như&nbsp;<strong>Bitexco, chung cư 42 Nguyễn Huệ, tượng Đ&agrave;i Chủ tịch Hồ ch&iacute; Minh, Ủy Ban Nh&acirc;n D&acirc;n Th&agrave;nh Phố -</strong> được thiết kế theo dạng lầu chu&ocirc;ng đ&uacute;c cao, cổ k&iacute;nh c&oacute; tuổi đời hơn trăm tuổi.<br>Tự do thưởng thức ẩm thực từ đường phố đến sang trọng tại phố đi bộ nhộn nhịp bậc nhất TP.HCM.</div>\r\n    <div>&nbsp;</div>\r\n    <div><em><strong>Lưu &yacute;:&nbsp;</strong>Chương tr&igrave;nh của&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> l&agrave; chương tr&igrave;nh trọn g&oacute;i, kh&ocirc;ng được &aacute;p dụng ch&iacute;nh s&aacute;ch miễn giảm, ưu ti&ecirc;n v&agrave;o điểm tham quan (nếu c&oacute;) d&agrave;nh cho trẻ em, người cao tuổi, người c&oacute; c&ocirc;ng với c&aacute;ch mạng v&igrave; vậy Vietravel sẽ kh&ocirc;ng ho&agrave;n lại phần ch&ecirc;nh lệch cho c&aacute;c kh&aacute;ch h&agrave;ng thuộc diện ưu ti&ecirc;n n&agrave;y.</em></div>\r\n</div>', 199, '2023-10-10 00:00:00.000000', 'TP. Hồ Chí Minh', NULL, 6, 'Walking City Tour - Ký Ức Sài Gòn Xưa', '1 ngày', 'Quanh năm', 'Ưu đãi trực tiếp vào giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN133-061-160823VN-F', 'Buffet sáng, Bữa sáng theo thực đơn, Theo thực đơn, Đặc sản địa phương, Hải sản', NULL, 'Hà Nội - Vịnh Hạ Long - Sapa - Bản Cát Cát - Fansipan Legend', 'Gia đình nhiều thế hệ', NULL, 9990000, NULL, NULL, 'NDSGN133-061-160823VN-F.jpg', 'NDSGN846-108-100823XE-D.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'NDSGN548-084-120823XE-V-F.jpg', 'Khách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP.HCM &ndash; SB NỘI B&Agrave;I &ndash; H&Agrave; NỘI 02 bữa ăn: (Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <div>Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất (Ga nội địa), hướng dẫn vi&ecirc;n hỗ trợ kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay đi H&agrave; Nội. Đến s&acirc;n bay Nội B&agrave;i, xe v&agrave; HDV&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> đ&oacute;n Qu&yacute; kh&aacute;ch đi H&agrave; Nội. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:</div>\r\n                <ul>\r\n                    <li>\r\n                        <div><strong>Ho&agrave;ng th&agrave;nh Thăng Long:</strong> quần thể c&ocirc;ng tr&igrave;nh kiến tr&uacute;c đồ sộ được c&aacute;c triều vua x&acirc;y dựng trong nhiều giai đoạn lịch sử v&agrave; trở th&agrave;nh di t&iacute;ch quan trọng bậc nhất trong hệ thống c&aacute;c di t&iacute;ch Việt Nam.</div>\r\n                    </li>\r\n                    <li>\r\n                        <div><strong>Văn Miếu</strong>: nơi thờ Khổng Tử v&agrave; c&aacute;c bậc hiền triết của Nho Gi&aacute;o,&nbsp;<strong>Quốc Tử Gi&aacute;m</strong> - trường đại học đầu ti&ecirc;n của Việt Nam, t&igrave;m về cội nguồn lịch sử của c&aacute;c vị Nho học.</div>\r\n                    </li>\r\n                    <li>\r\n                        <div><strong>Hồ Ho&agrave;n Kiếm</strong> ngắm b&ecirc;n ngo&agrave;i&nbsp;<strong>Th&aacute;p R&ugrave;a, Đền Ngọc Sơn, Cầu Th&ecirc; H&uacute;c</strong>.</div>\r\n                    </li>\r\n                </ul>\r\n                <div>Qu&yacute; kh&aacute;ch nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi hoặc tự do đi tham quan phố cổ H&agrave; Nội.</div>\r\n                <div><strong>Nghỉ đ&ecirc;m tại H&agrave; Nội. &nbsp;</strong></div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - H&Agrave; NỘI &ndash; HẠ LONG 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <div>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng. &nbsp;Xe khởi h&agrave;nh đi Hạ Long theo đường cao tốc Hải Ph&ograve;ng &ndash; Hạ Long, tr&ecirc;n đường ngắm cảnh Bạch Đằng Giang.</div>\r\n                <div>Đến nơi, Qu&yacute; kh&aacute;ch d&ugrave;ng cơm trưa v&agrave; xuống thuyền đi du ngoạn&nbsp;<strong><a href="https://travel.com.vn/ha-long/tour-vinh-ha-long.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vịnh Hạ Long</a></strong> - Thắng cảnh thi&ecirc;n nhi&ecirc;n tuyệt đẹp v&agrave; v&ocirc; c&ugrave;ng sống động, được UNESCO c&ocirc;ng nhận l&agrave; di sản thi&ecirc;n nhi&ecirc;n Thế giới năm 1994.</div>\r\n                <ul>\r\n                    <li>\r\n                        <div><strong>Động Thi&ecirc;n Cung</strong> l&agrave; một trong những động đẹp nhất ở Hạ Long. Vẻ đẹp nguy nga v&agrave; lộng lẫy bởi những lớp thạch nhũ v&agrave; những luồng &aacute;nh s&aacute;ng lung linh.</div>\r\n                    </li>\r\n                    <li>\r\n                        <div>Từ tr&ecirc;n t&agrave;u ngắm nh&igrave;n c&aacute;c h&ograve;n đảo lớn nhỏ trong Vịnh Hạ Long:&nbsp;<strong>H&ograve;n G&agrave; Chọi, H&ograve;n Lư Hương</strong>.</div>\r\n                    </li>\r\n                    <li>\r\n                        <div>Tham quan mua sắm đặc sản tại Hải Sản Hương Đ&agrave; Hạ Long với nhiều mặt h&agrave;ng nổi tiếng chả mực gi&atilde; tay Hạ Long, thịt chưng mắm t&eacute;p, ruốc t&ocirc;m, s&aacute; s&ugrave;ng kh&ocirc;, c&aacute; cơm, c&aacute; chỉ v&agrave;ng,..</div>\r\n                    </li>\r\n                </ul>\r\n                <div>\r\n                    <p>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi. Buổi tối, Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; nhiều hoạt động dịch vụ giải tr&iacute; s&ocirc;i nổi tại &ldquo;phố cổ&rdquo; B&atilde;i Ch&aacute;y - nằm cạnh c&ocirc;ng vi&ecirc;n Sun World Hạ Long từ những ẩm thực đường phố đến c&aacute;c qu&aacute;n c&agrave; ph&ecirc; si&ecirc;u dễ thương như H&ograve;n Gai Coffee &amp; Lounge hay thoải m&aacute;i bung x&otilde;a tại The Mini Bar, Brothers Pub,..</p>\r\n                    <p><br><strong>Nghỉ đ&ecirc;m tại Hạ Long.</strong></p>\r\n                    <p>Hoặc lựa chọn một số dịch vụ kh&aacute;m ph&aacute; Hạ Long về đ&ecirc;m (tự t&uacute;c phương tiện v&agrave; chi ph&iacute; tham quan):<br>- Trải nghiệm dịch vụ C&aacute;p Treo Nữ Ho&agrave;ng tại<strong>&nbsp;Sun World Hạ Long Complex tr&ecirc;n N&uacute;i Ba Đ&egrave;o,&nbsp;</strong>chi&ecirc;m ngưỡng cảnh đẹp về đ&ecirc;m của th&agrave;nh phố Hạ Long dưới &aacute;nh đ&egrave;n lung linh (thời gian hoạt động c&aacute;p treo dự kiến từ 10h đến 18h c&aacute;c ng&agrave;y thứ 7 v&agrave; CN)<br>&nbsp;</p>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - HẠ LONG &ndash; L&Agrave;O CAI &ndash; SAPA 03 Bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <div>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe khởi h&agrave;nh đưa Qu&yacute; kh&aacute;ch đi Sapa, tr&ecirc;n đường dừng tham quan&nbsp;<strong>mua sắm tại Si&ecirc;u thị Du lịch n&ocirc;ng nghiệp Ocop Ph&uacute; Thọ - chợ Tp Việt Tr&igrave;&nbsp;</strong>v&agrave; d&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng địa phương. &nbsp;<br><br>Đến nơi, Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối v&agrave; nhận ph&ograve;ng nghỉ ngơi hoặc tự do dạo phố<strong>&nbsp;ngắm nh&agrave; thờ Đ&aacute; Sapa</strong>, tự do thưởng thức đặc sản v&ugrave;ng cao như:&nbsp;<strong>thịt lợn cắp n&aacute;ch nướng, trứng nướng, rượu t&aacute;o m&egrave;o, giao lưu với người d&acirc;n tộc v&ugrave;ng cao.</strong></div>\r\n                <div><br><strong>Nghỉ đ&ecirc;m tại Sapa.&nbsp;</strong><br>&nbsp;</div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - SAPA &ndash; FANSIPAN LEGEND &ndash; BẢN C&Aacute;T C&Aacute;T 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn. xe đưa đo&agrave;n ra ga Sapa, Qu&yacute; kh&aacute;ch trải nghiệm đến khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> <strong>Fansipan Legend</strong> bằng&nbsp;<strong>T&agrave;u hỏa leo n&uacute;i Mường Hoa</strong> hiện đại nhất Việt Nam với tổng chiều d&agrave;i gần 2000m, thưởng ngoạn bức tranh phong cảnh đầy m&agrave;u sắc của c&aacute;nh rừng nguy&ecirc;n sinh, thung lũng Mường Hoa.</div>\r\n            <ul>\r\n                <li>\r\n                    <div>Chinh phục đỉnh n&uacute;i Fansipan với độ cao 3.143m h&ugrave;ng vĩ bằng c&aacute;p treo (chi ph&iacute; tự t&uacute;c).</div>\r\n                </li>\r\n                <li>\r\n                    <div>Lễ Phật tại&nbsp;<strong>c</strong><strong>h&ugrave;a Tr&igrave;nh</strong> hay cầu ph&uacute;c lộc, b&igrave;nh an cho gia đ&igrave;nh tại&nbsp;<strong>B&iacute;ch V&acirc;n Thiền Tự</strong> trong hệ thống cảnh quan t&acirc;m linh tr&ecirc;n đỉnh Fansipan.</div>\r\n                </li>\r\n            </ul>\r\n            <div>Tiếp tục h&agrave;nh tr&igrave;nh, Qu&yacute; kh&aacute;ch d&ugrave;ng cơm trưa v&agrave; tham quan:</div>\r\n            <ul>\r\n                <li>\r\n                    <div><strong>Bản C&aacute;t C&aacute;t</strong> - đẹp như một bức tranh giữa v&ugrave;ng phố cổ Sapa, nơi đ&acirc;y thu h&uacute;t du kh&aacute;ch bởi cầu treo, th&aacute;c nước, guồng nước v&agrave; những mảng m&agrave;u hoa m&ecirc; hoặc du kh&aacute;ch khi lạc bước đến đ&acirc;y. Thăm những nếp nh&agrave; của người M&ocirc;ng, Dao, Gi&aacute;y trong bản, du kh&aacute;ch sẽ kh&ocirc;ng khỏi ngỡ ng&agrave;ng trước vẻ đẹp mộng mị của một trong những ng&ocirc;i l&agrave;ng cổ đẹp nhất Sapa.</div>\r\n                </li>\r\n            </ul>\r\n            <div>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối tại Sapa v&agrave; tự do nghỉ ngơi.</div>\r\n            <div><strong>Nghỉ đ&ecirc;m tại Sapa.</strong></div>\r\n            <div><strong><em>Lưu &yacute;:</em></strong><em>&nbsp;Trong trường hợp, Fansipan kh&ocirc;ng hoạt động chương tr&igrave;nh sẽ được thay thế bằng điểm tham quan Cầu K&iacute;nh Rồng M&acirc;y (kh&ocirc;ng bao gồm v&eacute; tham quan).</em></div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 5 - SAPA &ndash; L&Agrave;O CAI &ndash; SB NỘI B&Agrave;I (H&Agrave; NỘI) 02 Bữa ăn: (S&aacute;ng, Trưa)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Theo cung đường cao tốc ra s&acirc;n bay Nội B&agrave;i. Tr&ecirc;n đường qu&yacute; kh&aacute;ch dừng ch&acirc;n tại Tp L&agrave;o Cai ăn trưa v&agrave; chụp ảnh kỷ niệm tại Cửa khẩu bi&ecirc;n giới&nbsp;<strong>Việt - Trung</strong> <strong>&ldquo;L&agrave;o Cai- H&agrave; Khẩu&rdquo;</strong>. &nbsp;Đến nơi, Hướng dẫn vi&ecirc;n hỗ trợ Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&oacute;n chuyến bay về Tp.HCM. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất.</div>\r\n        </div>\r\n    </div>\r\n</div>', 143, '2023-09-16 00:00:00.000000', 'TP.Đà Nẵng', 'Máy bay, Xe du lịch', 3, 'Hà Nội - Vịnh Hạ Long - Sapa - Bản Cát Cát – Fansipan', '5 ngày 4 đêm', 'Quanh năm', 'Ưu đãi trực tiếp vào giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN1731-095-110823VU-V-7', 'Buffet sáng, Theo thực đơn, Đặc sản địa phương', 'todo', 'Hà Nội, Yên Bái, Sapa, Fansipan, Hàm Rồng, Mù Cang Chải, Yên Bái, Điện Biên, Sơn La, Mộc Châu', 'Gia đình nhiều thế hệ, Thanh niên', NULL, 10990000, NULL, NULL, 'NDSGN1731-095-110823VU-V-7.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'NDSGN303-031-100823VN-F-7.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'Khách sạn 2 sao\r\n\r\nKhách sạn 3 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP.HCM - NỘI B&Agrave;I (H&Agrave; NỘI) (tự t&uacute;c ăn ng&agrave;y đầu ti&ecirc;n)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch tập trung tại s&acirc;n bay T&acirc;n Sơn Nhất (ga trong nước), hướng dẫn l&agrave;m thủ tục cho Qu&yacute; kh&aacute;ch đ&aacute;p chuyến bay đi H&agrave; Nội. Đến s&acirc;n bay Nội B&agrave;i, xe đưa qu&yacute; kh&aacute;ch về trung t&acirc;m H&agrave; Nội nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.&nbsp;</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại H&agrave; Nội.</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - H&Agrave; NỘI - Y&Ecirc;N B&Aacute;I - NGHĨA LỘ 03 bữa ăn: (S&aacute;ng, Trưa, Tối)</h3>\r\n    <div>\r\n        <p>Ăn s&aacute;ng, đo&agrave;n khởi h&agrave;nh đi Y&ecirc;n B&aacute;i. Tr&ecirc;n đường đi đo&agrave;n tham quan:&nbsp;<br><strong>- Đền H&ugrave;ng</strong>: nơi thờ c&aacute;c vua H&ugrave;ng c&oacute; c&ocirc;ng dựng nước, tổ ti&ecirc;n của d&acirc;n tộc Việt Nam, một quần thể kiến tr&uacute;c th&acirc;m nghi&ecirc;m tr&ecirc;n n&uacute;i Nghĩa Lĩnh. Đo&agrave;n d&acirc;ng hương đất tổ, thăm quan&nbsp;<strong>đền Thượng, đền Trung, đền Hạ, Giếng Ngọc, Lăng vua H&ugrave;ng.</strong><br>- Buổi tối, thưởng thức chương tr&igrave;nh biểu diễn&nbsp;<strong>m&uacute;a X&ograve;e, giao lưu v&agrave; t&igrave;m hiểu n&eacute;t văn h&oacute;a đặc sắc của d&acirc;n tộc Th&aacute;i.&nbsp;</strong></p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại&nbsp;<a href="https://travel.com.vn/yen-bai/tour-nghia-lo.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Nghĩa Lộ</a>.</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - NGHĨA LỘ - M&Ugrave; CANG CHẢI - SAPA 03 bữa ăn: (S&aacute;ng, Trưa, Tối)</h3>\r\n    <div>\r\n        <p>Trả ph&ograve;ng kh&aacute;ch sạn, đo&agrave;n khởi h&agrave;nh đi M&ugrave; Cang Chải, ngang qua:&nbsp;<br><strong>- T&uacute; Lệ</strong>, Qu&yacute; kh&aacute;ch sẽ ngửi được m&ugrave;i hương thoang thoảng theo gi&oacute; bảng lảng tr&ecirc;n m&aacute;i nh&agrave; của &ldquo;cơm mới&rdquo;, nơi đ&acirc;y nổi tiếng với x&ocirc;i nếp, cốm kh&ocirc;.&nbsp;<br><strong>- Đ&egrave;o Khau Phạ:</strong> đ&egrave;o hiểm trở v&agrave; d&agrave;i nhất tr&ecirc;n tuyến quốc lộ 32 được mệnh danh l&agrave; một trong &ldquo;tứ đại đ&egrave;o&rdquo; với độ d&agrave;i tr&ecirc;n 30km.<br><strong>- Bản L&igrave;m M&ocirc;ng</strong>: đ&acirc;y l&agrave; Bản của d&acirc;n tộc M&ocirc;ng v&agrave; l&agrave; nơi c&oacute; ruộng l&uacute;a đẹp nhất M&ugrave; Cang Chải. Qu&yacute; kh&aacute;ch sẽ bị m&ecirc; hoặc bởi vẻ đẹp h&uacute;t hồn của cung đường ruộng bậc thang nổi tiếng tại 3 x&atilde;: La P&aacute;n Tẩn, Chế Cu Nha v&agrave; Zế Xu Ph&igrave;nh. Đo&agrave;n chi&ecirc;m ngưỡng những thung lũng rộng h&uacute;t tầm mắt, c&aacute;c thửa ruộng tầng tầng lớp lớp lượn s&oacute;ng theo sườn n&uacute;i, ngọn n&uacute;i n&agrave;y nối tiếp ngọn n&uacute;i kh&aacute;c.<br>Buổi tối Qu&yacute; kh&aacute;ch dạo phố, ngắm nh&agrave; thờ Đ&aacute;&nbsp;<a href="https://travel.com.vn/lao-cai/tour-sapa.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Sapa</a>, tự do thưởng thức đặc sản v&ugrave;ng cao như: thịt lợn cắp n&aacute;ch nướng, trứng nướng, rượu t&aacute;o m&egrave;o, giao lưu với người d&acirc;n tộc v&ugrave;ng cao.&nbsp;</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Sa Pa.</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - SAPA - LAI CH&Acirc;U 03 bữa ăn: (S&aacute;ng, Trưa, Tối)</h3>\r\n    <div>\r\n        <p>Trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa đo&agrave;n ra ga Sun Plaza trải nghiệm đến khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> Fansipan Legend bằng T&agrave;u hỏa leo n&uacute;i Mường Hoa hiện đại nhất Việt Nam với tổng chiều d&agrave;i gần 2000m, thưởng ngoạn bức tranh phong cảnh đầy m&agrave;u sắc của c&aacute;nh rừng nguy&ecirc;n sinh, thung lũng Mường Hoa.<br><strong>- Chinh phục đỉnh n&uacute;i Fansipan</strong> với độ cao 3.143m h&ugrave;ng vĩ bằng c&aacute;p treo (chi ph&iacute; tự t&uacute;c).&nbsp;<br>- Lễ Phật tại<strong>&nbsp;ch&ugrave;a Tr&igrave;nh</strong> hay cầu ph&uacute;c lộc, b&igrave;nh an cho gia đ&igrave;nh tại&nbsp;<strong>B&iacute;ch V&acirc;n Thiền Tự</strong> trong hệ thống cảnh quan t&acirc;m linh tr&ecirc;n đỉnh Fansipan.&nbsp;<br>Sau đ&oacute; xe khởi h&agrave;nh đi Lai Ch&acirc;u, tr&ecirc;n đường gh&eacute; tham quan:<br>- Chinh phục đ&egrave;o&nbsp;<strong>&Ocirc; Quy Hồ</strong> - con đ&egrave;o đẹp, h&ugrave;ng vĩ v&agrave; d&agrave;i nhất trong Tứ Đại Đỉnh Đ&egrave;o miền Bắc.<br>- Khu du lịch&nbsp;<strong>Cổng Trời &Ocirc; Quy Hồ</strong> - một trong những điểm săn m&acirc;y, ngắm ho&agrave;ng h&ocirc;n cực đẹp tại&nbsp;<a href="https://travel.com.vn/lao-cai/tour-sapa.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Sapa</a>.<br>Đến nơi, Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Lai Ch&acirc;u.</strong></p>\r\n        <p><strong>Lưu &yacute;: Trong trường hợp, Fansipan kh&ocirc;ng hoạt động chương tr&igrave;nh sẽ được thay thế bằng điểm tham quan Cầu K&iacute;nh Rồng M&acirc;y (kh&ocirc;ng bao gồm v&eacute; tham quan).&nbsp;</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 5 - LAI CH&Acirc;U - ĐIỆN BI&Ecirc;N &nbsp; &nbsp; &nbsp; 03 bữa ăn: (S&aacute;ng, Trưa, Tối)</h3>\r\n    <div>\r\n        <p>Đo&agrave;n khởi h&agrave;nh đi<strong>&nbsp;Điện Bi&ecirc;n:</strong><br>- Tr&ecirc;n đường ngắm cảnh rừng n&uacute;i&nbsp;<strong>T&acirc;y Bắc&nbsp;</strong>dọc theo d&ograve;ng s&ocirc;ng Nậm Na v&agrave; v&ugrave;ng ngập nước do đập nh&agrave; m&aacute;y Thủy điện Sơn La d&acirc;ng l&ecirc;n tại ng&atilde; ba s&ocirc;ng: s&ocirc;ng Đ&agrave;, s&ocirc;ng Nậm Na v&agrave; s&ocirc;ng Nậm Rốm.&nbsp;<br><br>-&nbsp;<strong>Di t&iacute;ch Đồi A1, hầm Đờ C&aacute;t, cầu Mường Thanh</strong>,&nbsp;<strong>s&ocirc;ng Nậm Rốm,</strong> Tượng đ&agrave;i Chiến thắng Điện Bi&ecirc;n Phủ<br><br>-&nbsp;<strong>Đền thờ liệt sĩ tại Chiến trường Điện Bi&ecirc;n Phủ&nbsp;</strong>c&ocirc;ng tr&igrave;nh kiến tr&uacute;c độc đ&aacute;o với kh&ocirc;ng gian tưởng niệm tại s&acirc;n tĩnh t&acirc;m v&agrave; hồ tưởng niệm được tạo h&igrave;nh bởi c&aacute;c v&ograve;ng tr&ograve;n đồng t&acirc;m, tạo sự tập trung, tĩnh t&acirc;m trước khi đến với kh&ocirc;ng gian t&acirc;m linh.</p>\r\n        <p>- Tham quan mua sắm đặc sản Qu&agrave; T&acirc;y Bắc Điện Bi&ecirc;n về l&agrave;m qu&agrave; cho người th&acirc;n v&agrave; gia đ&igrave;nh như gạo Điện Bi&ecirc;n, mật ong, s&acirc;u ch&iacute;t, thịt tr&acirc;u hun kh&oacute;i, chẳm ch&eacute;o, măng, c&aacute;c sản phẩm m&acirc;y tre đan, c&aacute;c sản phẩm th&ecirc;u, dệt thổ cẩm,..</p>\r\n        <p>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm chiều v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi. Buổi tối, Qu&yacute; kh&aacute;ch tự do tham quan</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Điện Bi&ecirc;n</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 6 - ĐIỆN BI&Ecirc;N - SƠN LA - MỘC CH&Acirc;U &ndash; RỪNG TH&Ocirc;NG BẢN &Aacute;NG 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <p>Đo&agrave;n khởi h&agrave;nh đi&nbsp;<a href="https://travel.com.vn/son-la/tour-moc-chau.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Mộc Ch&acirc;u</a> - Sơn La, tham quan:&nbsp;<br><strong>- Đ&egrave;o Pha Đin</strong>: đ&acirc;y cũng l&agrave; một trong &quot;tứ đại đ&egrave;o&quot; v&ugrave;ng Đ&ocirc;ng T&acirc;y Bắc v&agrave; được xếp c&ugrave;ng nh&oacute;m 6 con đ&egrave;o g&acirc;y ấn tượng nhất Việt Nam.&nbsp;<br><strong>- Rừng th&ocirc;ng Bản &Aacute;ng:&nbsp;</strong>Được bao phủ bởi rừng th&ocirc;ng cổ thụ h&agrave;ng trăm năm tuổi với kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t mẻ. Với diện t&iacute;ch gần 50ha với đầy đủ c&aacute;c dịch vụ: nghỉ dưỡng, nh&agrave; h&agrave;ng, s&acirc;n tập golf, khu vui chơi cảm gi&aacute;c mạnh, ... l&agrave; một trong những điểm tham quan đặc sắc thu h&uacute;t nhiều sự quan t&acirc;m của du kh&aacute;ch.&nbsp;<br>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm chiều tại nh&agrave; h&agrave;ng địa phương v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi hoặc tự do tham quan.&nbsp;</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Mộc Ch&acirc;u.&nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 7 - MỘC CH&Acirc;U ISLAND - S&Acirc;N BAY NỘI B&Agrave;I (H&Agrave; NỘI) - TPHCM 02 bữa ăn: (S&aacute;ng, Trưa)</h3>\r\n    <div>\r\n        <p>Ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch tham quan:&nbsp;</p>\r\n        <p>- Quần thể phức hợp vui chơi giải tr&iacute; v&agrave; nghỉ dưỡng&nbsp;<strong><a href="https://travel.com.vn/son-la/tour-moc-chau.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Mộc Ch&acirc;u</a> Island</strong> chi&ecirc;m ngưỡng vẻ đẹp Cầu k&iacute;nh Bạch Long c&oacute; chiều d&agrave;i 632m, ở độ cao c&aacute;ch mặt đất 150m, l&agrave; &ldquo;C&acirc;y cầu k&iacute;nh đi bộ d&agrave;i nhất thế giới&rdquo; vừa được x&aacute;c lập kỷ lục Guinness v&agrave;o th&aacute;ng 28/5/2022. Trải nghiệm c&aacute;c tr&ograve; chơi (chi ph&iacute; tự t&uacute;c): Chinh phục cầu k&iacute;nh Bạch Long, trượt Airslide, zipline, &hellip;</p>\r\n        <p><strong>- Mua sắm đặc sản</strong> nổi tiếng được chế biến từ sữa b&ograve; tươi nổi tiếng của Mộc Ch&acirc;u về l&agrave;m qu&agrave;.&nbsp;<br>- Dừng chụp h&igrave;nh đ&egrave;o Thung Khe hay còn gọi là&nbsp;<strong>đèo Đá Trắng</strong>, nơi đ&acirc;y quanh năm trắng như tuy&ecirc;́t và m&acirc;y mù bao phủ tạo cảnh quan tuy&ecirc;̣t đep thu hút nhi&ecirc;̀u du khách đ&ecirc;́n chụp ảnh.<br>Tiếp tục h&agrave;nh tr&igrave;nh đến s&acirc;n bay Nội B&agrave;i, Hướng dẫn vi&ecirc;n hỗ trợ Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay về Tp.HCM. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất. &nbsp;<br><br><strong>*Lưu &yacute; :</strong><br>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;<br>- Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm,...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.<br>- Kh&aacute;ch Sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao Điểm.</p>\r\n        <p><br><br><strong>&nbsp;KẾT TH&Uacute;C CHƯƠNG TR&Igrave;NH, TẠM BIỆT QU&Yacute; KH&Aacute;CH!</strong></p>\r\n    </div>\r\n</div>', 132, '2023-08-11 00:00:00.000000', 'TP. Hồ Chí Minh', 'Máy bay, Xe du lịch', 6, 'Tây Bắc: Hà Nội - Nghĩa Lộ - Tú Lệ - Mù Cang Chải - Sapa - Fansipan - Lai Châu - Điện Biên - Sơn La - Mộc Châu Island - Trải nghiệm cầu kính đi bộ dài nhất thế giới', '7 ngày 6 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN303-031-100823VN-F-7', 'Theo thực đơn', 'todo', 'Ngũ Hành Sơn, Phố cổ Hội An, Bà Nà, Chùa Linh Ứng Sơn Trà', 'Gia đình nhiều thế hệ', NULL, 5990000, NULL, NULL, 'NDSGN303-031-100823VN-F-7.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'NDSGN548-084-120823XE-V-F.jpg', 'NDSGN846-108-100823XE-D.jpg', 'Khách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP.HỒ CH&Iacute; MINH - Đ&Agrave; NẴNG - HỘI AN Số bữa ăn: 02 (trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <p>Qu&yacute; kh&aacute;ch tập trung tại điểm hẹn, Ga đi trong nước, s&acirc;n bay T&acirc;n Sơn Nhất. Hướng dẫn vi&ecirc;n&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> hỗ trợ l&agrave;m thủ tục cho đo&agrave;n đ&aacute;p chuyến bay đi Đ&agrave; Nẵng. Tại s&acirc;n bay Đ&agrave; Nẵng xe v&agrave; HDV Vietravel đ&oacute;n đo&agrave;n đi tham quan:&nbsp;<br><br><strong>-&nbsp;<a href="https://travel.com.vn/da-nang/tour-ban-dao-son-tra.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">B&aacute;n đảo Sơn Tr&agrave;&nbsp;</a>v&agrave; Viếng Ch&ugrave;a Linh Ứng:</strong> Nơi đ&acirc;y c&oacute; tượng Phật Quan Thế &Acirc;m cao nhất Việt Nam, đứng nơi đ&acirc;y, Qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng to&agrave;n cảnh th&agrave;nh phố, n&uacute;i rừng v&agrave; biển đảo Sơn Tr&agrave; một c&aacute;ch ho&agrave;n hảo nhất.&nbsp;<br><strong>-&nbsp;<a href="https://travel.com.vn/da-nang/tour-ngu-hanh-son.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Ngũ H&agrave;nh Sơn</a>:</strong> Động T&agrave;ng Chơn, Động Hoa Nghi&ecirc;m, Ch&ugrave;a Non Nước, L&agrave;ng Đ&aacute; Mỹ Nghệ,..<br>- Tham quan<strong>&nbsp;L&agrave;ng Đ&aacute; Non Nước Nguyễn H&ugrave;ng:</strong> nơi chế t&aacute;c sản phẩm đ&aacute; mỹ nghệ tạo phong thủy.<br><strong>- Phố Cổ Hội An</strong>: Ch&ugrave;a Cầu, Nh&agrave; Cổ Ph&ugrave;ng Hưng, Hội Qu&aacute;n Phước Kiến, Cơ sở Thủ C&ocirc;ng Mỹ Nghệ,&hellip;Qu&yacute; kh&aacute;ch tự do dạo phố đ&egrave;n lồng đầy m&agrave;u sắc, cảm nhận sự y&ecirc;n b&igrave;nh cổ k&iacute;nh v&agrave; l&atilde;ng mạn Phố Cổ về đ&ecirc;m,...&nbsp;<br><br>Buổi tối qu&yacute; kh&aacute;ch tự do dạo phố ngắm nh&igrave;n sự lung linh v&agrave; ph&aacute;t triển của th&agrave;nh phố &hellip;</p>\r\n                <p><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.&nbsp;</strong><br>&nbsp;</p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - Đ&Agrave; NẴNG &ndash; KDL B&Agrave; N&Agrave; - CẦU V&Agrave;NG Số bữa ăn: 02 (Ăn s&aacute;ng,chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:<br><br><strong>- Khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> B&agrave; N&agrave; - Suối Mơ (Chi ph&iacute; c&aacute;p treo &amp; Ăn trưa tự t&uacute;c)</strong>: Tận hưởng kh&ocirc;ng kh&iacute; se lạnh của Đ&agrave; Lạt tại miền Trung, đo&agrave;n tự do tham quan Ch&ugrave;a Linh Ứng, Hầm Rượu Debay, vườn hoa Le Jardin D&rsquo;Amour, Khu T&acirc;m linh mới của B&agrave; N&agrave; viếng Đền Lĩnh Ch&uacute;a Linh Từ, khu vui chơi Fantasy Park, tự do chụp h&igrave;nh tại&nbsp;<strong>Cầu V&agrave;ng</strong> điểm tham quan mới si&ecirc;u hot tại B&agrave; N&agrave;&hellip;<strong>Ăn trưa tại B&agrave; N&agrave; tự t&uacute;c.&nbsp;</strong>Sau đ&oacute; đo&agrave;n tiếp tục tham quan vui chơi đến giờ xuống c&aacute;p.&nbsp;<br><br><strong>- B&atilde;i biển Mỹ Kh&ecirc;:&nbsp;</strong>Một trong những b&atilde;i biển quyến rũ nhất h&agrave;nh tinh. Qu&yacute; kh&aacute;ch tự do dạo biển, chụp h&igrave;nh....<br>Buổi tối Qu&yacute; kh&aacute;ch tự t&uacute;c đi dạo phố, thưởng thức đặc sản địa phương; thưởng ngoạn cảnh đẹp của Đ&agrave; Nẵng về đ&ecirc;m, ngắm nh&igrave;n Cầu Rồng, Cầu T&igrave;nh Y&ecirc;u, Cầu Trần Thị L&yacute;, Trung T&acirc;m Thương Mại, Khu phố ẩm thực, Caf&eacute; - Bar - Disco&hellip;</p>\r\n                <p>-&nbsp;<strong>Mua sắm đặc sản tại qu&agrave; Đ&agrave; Nẵng:</strong> Với hơn 2000 sản phẩm đặc sản Đ&agrave; Nẵng, Miền Trung v&agrave; c&aacute;c v&ugrave;ng miền l&acirc;n cận, nổi bật nhất l&agrave; chả b&ograve;, b&ograve; kh&ocirc;, mực rim me, mực kh&ocirc;, ghẹ sữa sốt v&agrave; đầy đủ c&aacute;c loại kh&ocirc; c&aacute;,...</p>\r\n                <p><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</strong></p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - Đ&Agrave; NẴNG - TP. HỒ CH&Iacute; MINH Số bữa ăn: 01 (Ăn s&aacute;ng)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn.&nbsp;<br><br>- Xe tiễn Qu&yacute; kh&aacute;ch ra s&acirc;n bay Đ&agrave; Nẵng đ&oacute;n chuyến bay trở về Tp.Hồ Ch&iacute; Minh. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất.</p>\r\n            </div>\r\n            <div>\r\n                <p><strong>Lưu &yacute;:</strong><br>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;<br>- Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm,...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.<br>- Kh&aacute;ch Sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao Điểm</p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>', 167, '2023-10-10 00:00:00.000000', 'TP.Đà Nẵng', 'Máy bay, Xe du lịch', 2, 'Đà Nẵng - Bà Nà - Cầu Vàng - Sơn Trà - Biển Mỹ Khê - Hội An - Đà Nẵng (Khách sạn tương đương 4*)', '3 ngày 2 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN3398-115-100823VU-D', 'Buffet sáng, Đặc sản địa phương, Theo thực đơn', 'todo', 'Đà Nẵng, Huế, Đại Nội, Bà Nà, Sơn Trà, Hội An, Động Phong Nha, Chùa Thiên Mụ, Ngũ Hành Sơn', 'Gia đình nhiều thế hệ, Cặp đôi', NULL, 7790000, NULL, NULL, 'NDSGN3398-115-100823VU-D.jpg', 'DSSGN820-014-120823FWT.jpg', 'NDSGN846-108-100823XE-D.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'Khách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP HỒ CH&Iacute; MINH - Đ&Agrave; NẴNG - HUẾ (Ăn trưa, chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch tập trung tại Ga đi Trong Nước, s&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> l&agrave;m thủ tục cho Qu&yacute; kh&aacute;ch đ&aacute;p chuyến bay đi&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a>. Xe đ&oacute;n qu&yacute; kh&aacute;ch tại s&acirc;n bay Đ&agrave; Nẵng, l&ecirc;n đường đi Huế. Tr&ecirc;n đường dừng tham quan:<br><br><strong>- Đầm Lập An:&nbsp;</strong>Ngắm cảnh m&acirc;y bồng bềnh tr&ecirc;n những ch&oacute;p n&uacute;i bao bọc quanh đầm,..<br><strong>- Tinh dầu tr&agrave;m Th&aacute;i H&agrave;:&nbsp;</strong>Huế được coi l&agrave; xứ sở của loại dầu tr&agrave;m nổi danh khắp nước với c&ocirc;ng dụng tuyệt vời, l&agrave; phương thuốc l&agrave;nh t&iacute;nh, an to&agrave;n với mọi lứa tuổi, gi&uacute;p ti&ecirc;u tan c&aacute;i mệt mỏi, c&aacute;c cơn đau kinh ni&ecirc;n, cho giấc ngủ được s&acirc;u hơn,&hellip;<br><strong>-&nbsp;<a href="https://travel.com.vn/hue/tour-chua-thien-mu.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Ch&ugrave;a Thi&ecirc;n Mụ</a>:&nbsp;</strong>ng&ocirc;i ch&ugrave;a được xem l&agrave; biểu tượng xứ Huế v&agrave; l&agrave; nơi lưu giữ nhiều cổ vật qu&yacute; gi&aacute; kh&ocirc;ng chỉ về mặt lịch sử m&agrave; c&ograve;n cả về nghệ thuật.<br><strong>- Đại Nội</strong>: ho&agrave;ng cung xưa của 13 vị Vua triều Nguyễn, tham quan Ngọ M&ocirc;n, Điện Th&aacute;i H&ograve;a, Tử Cấm Th&agrave;nh, Thế Miếu, Hiển L&acirc;m C&aacute;c, Cửu Đ&igrave;nh.<br><strong>- Dạo Phố Đ&ecirc;m:</strong> trải nghiệm một Huế về đ&ecirc;m kh&ocirc;ng hề trầm mặc với c&aacute;c c&ocirc;ng tr&igrave;nh trải theo chiều d&agrave;i lịch sử m&agrave; như một thiếu nữ trẻ trung kho&aacute;c l&ecirc;n m&igrave;nh bộ c&aacute;nh sắc m&agrave;u căng tr&agrave;n nhựa sống với tại Phố đi bộ ven s&ocirc;ng Hương h&agrave;i h&ograve;a với vẻ lung linh cầu Trường Tiền, tranh th&ecirc;u XQ, tự do thưởng thức c&aacute;c m&oacute;n đường phố xứ Huế như b&aacute;nh m&igrave; lọc, ch&egrave; Huế, ngắm nh&igrave;n thuyền rồng ngược xu&ocirc;i b&ecirc;n bến T&ograve;a Kh&acirc;m văng vẳng &acirc;m vang điệu h&ograve; Huế. Kh&aacute;m ph&aacute; khu phố T&acirc;y s&ocirc;i động về đ&ecirc;m với nhiều qu&aacute;n x&aacute; đ&ocirc;ng vui, những h&agrave;ng qu&agrave; lưu niệm.<br><br>Đo&agrave;n quay về kh&aacute;ch sạn, nhận ph&ograve;ng tự do nghỉ ngơi.</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Huế.</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - HUẾ - ĐỘNG THI&Ecirc;N ĐƯỜNG - ĐỘNG PHONG NHA (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch khởi h&agrave;nh sớm đi Quảng B&igrave;nh, đo&agrave;n gh&eacute; tham quan:<br><strong>- &nbsp;Động Phong Nha:</strong> Nằm trong quần thể Di sản thi&ecirc;n nhi&ecirc;n thế giới Phong Nha - Kẻ B&agrave;ng, được xem như chốn thần ti&ecirc;n bởi hệ thống n&uacute;i đ&aacute; v&ocirc;i v&agrave; s&ocirc;ng ngầm d&agrave;i nhất thế giới.<br><br><strong>-&nbsp;<a href="https://travel.com.vn/quang-binh/tour-dong-thien-duong.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Động Thi&ecirc;n Đường</a>:</strong> Một trong những hang động kỳ vĩ nhất thế giới, được xem như ho&agrave;ng cung dưới l&ograve;ng đất, một nh&aacute;nh thuộc Quần thể di sản thi&ecirc;n nhi&ecirc;n thế giới Vườn Quốc Gia Phong Nha Kẻ B&agrave;ng.&nbsp;<br><br>Sau khi tham quan, đo&agrave;n khởi h&agrave;nh về kh&aacute;ch sạn, nhận ph&ograve;ng tự do nghỉ ngơi v&agrave; kh&aacute;m ph&aacute; Quảng B&igrave;nh về đ&ecirc;m.<br><br><strong>Nghỉ đ&ecirc;m tại Quảng B&igrave;nh.&nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - QUẢNG B&Igrave;NH - LA VANG - LẬP AN - Đ&Agrave; NẴNG (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch khởi h&agrave;nh về&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a>, tr&ecirc;n đường đi dừng tham quan:<br><strong>- Th&agrave;nh cổ Quảng Trị:</strong> nghe hướng dẫn vi&ecirc;n thuyết minh Khu phi qu&acirc;n sự DMZ v&agrave; Cầu Hiền Lương S&ocirc;ng Bến Hải (Vĩ tuyến 17).<br><strong>-&nbsp;<a href="https://travel.com.vn/quang-tri/tour-thanh-dia-la-vang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Th&aacute;nh địa La Vang</a>:</strong> một trong bốn tiểu vương cung th&aacute;nh đường La M&atilde; tại Việt Nam<br>- Nghỉ ch&acirc;n, tham quan Lucky Pearl: l&agrave; một trong những điểm đến th&acirc;n thuộc của người d&acirc;n xứ Huế chuy&ecirc;n t&igrave;m kiếm những nguồn ngọc trai chất lượng v&agrave; đưa đến kh&aacute;ch h&agrave;ng những sản phẩm tinh tế nhất, cho Qu&yacute; kh&aacute;ch trải nghiệm đẳng cấp.<br><strong>- B&aacute;n đảo Sơn Tr&agrave; v&agrave; viếng Ch&ugrave;a Linh Ứng:&nbsp;</strong>Nơi đ&acirc;y c&oacute; tượng Phật Quan Thế &Acirc;m cao nhất Việt Nam. Đứng nơi đ&acirc;y, qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng to&agrave;n cảnh th&agrave;nh phố, n&uacute;i rừng v&agrave; biển đảo Sơn Tr&agrave; một c&aacute;ch ho&agrave;n hảo nhất.<br><br>Buổi tối Qu&yacute; kh&aacute;ch tự t&uacute;c đi dạo phố thưởng ngoạn cảnh đẹp của Đ&agrave; Nẵng về đ&ecirc;m, ngắm nh&igrave;n Cầu Rồng, Cầu T&igrave;nh Y&ecirc;u, Cầu Trần Thị L&yacute;, Trung T&acirc;m Thương Mại, Khu phố ẩm thực, Caf&eacute; - Bar - Disco&hellip;. Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng. &nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - Đ&Agrave; NẴNG - KHU DU LỊCH B&Agrave; N&Agrave; - CẦU V&Agrave;NG &ndash; HỘI AN (Ăn s&aacute;ng, chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:&nbsp;<br><strong>- B&atilde;i biển Mỹ Kh&ecirc;:</strong> Một trong những b&atilde;i biển quyến rũ nhất h&agrave;nh tinh, Qu&yacute; kh&aacute;ch tự do dạo biển, chụp h&igrave;nh.&nbsp;<br><strong>- Khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> B&agrave; N&agrave;</strong> <em><strong>(chi ph&iacute; c&aacute;p treo &amp; ăn trưa tự t&uacute;c):</strong>&nbsp;</em>tận hưởng kh&ocirc;ng kh&iacute; se lạnh của Đ&agrave; Lạt tại miền Trung, đo&agrave;n tự do tham quan Ch&ugrave;a Linh Ứng, Hầm Rượu Debay, vườn hoa Le Jardin D&rsquo;Amour, Khu T&acirc;m linh mới của B&agrave; N&agrave; viếng Đền Lĩnh Ch&uacute;a Linh Từ, khu vui chơi Fantasy Park, tự do chụp h&igrave;nh tại Cầu V&agrave;ng điểm tham quan si&ecirc;u hot tại B&agrave; N&agrave;&hellip;&nbsp;<strong>Ăn trưa tại B&agrave; N&agrave; tự t&uacute;c.</strong> Sau đ&oacute; đo&agrave;n tiếp tục tham quan vui chơi đến giờ xuống c&aacute;p.<br>Buổi chiều tiếp tục h&agrave;nh tr&igrave;nh đến với Hội An:&nbsp;<br><strong>- Tham quan L&agrave;ng Đ&aacute; Non Nước Nguyễn H&ugrave;ng:</strong> mua sắm sản phẩm đ&aacute; mỹ nghệ tạo phong thủy.&nbsp;<br><br>Buổi chiều tiếp tục h&agrave;nh tr&igrave;nh đến với Hội An:&nbsp;<br><strong>- Phố Cổ Hội An:</strong> Ch&ugrave;a Cầu, Nh&agrave; Cổ Ph&ugrave;ng Hưng, Hội Qu&aacute;n Phước Kiến, Cơ sở Thủ C&ocirc;ng Mỹ Nghệ,&hellip; Tự do thả đ&egrave;n hoa đăng cầu sức khỏe b&igrave;nh an &amp; ph&uacute;c lộc năm mới tr&ecirc;n d&ograve;ng s&ocirc;ng Ho&agrave;i&hellip;. (chi ph&iacute; tự t&uacute;c).&nbsp;<br><br>Đo&agrave;n quay về&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a>, nhận ph&ograve;ng kh&aacute;ch sạn, tự do nghỉ ngơi.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng. &nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 5 - Đ&Agrave; NẴNG - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn.<br><br>Mua sắm đặc sản phố biển tại Hương Đ&agrave; hoặc Qu&agrave; Miền Trung hoặc Qu&agrave;&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a> Thi&ecirc;n Ph&uacute;: với hơn 2000 sản phẩm đặc sản Đ&agrave; Nẵng, Miền Trung v&agrave; c&aacute;c v&ugrave;ng miền l&acirc;n cận, nổi bật nhất l&agrave; chả b&ograve;, b&ograve; kh&ocirc;, mực rim me, mực kh&ocirc;, ghẹ sữa sốt v&agrave; đầy đủ c&aacute;c loại kh&ocirc; c&aacute;,...</p>\r\n        <p>Xe tiễn Qu&yacute; kh&aacute;ch ra s&acirc;n bay Đ&agrave; Nẵng đ&oacute;n chuyến bay trở về Tp.Hồ Ch&iacute; Minh. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất. &nbsp;&nbsp;</p>\r\n        <p><br><strong>Lưu &yacute;:</strong><br>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;<br>- Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm,...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.<br>- Kh&aacute;ch Sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao Điểm<br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n', 126, '2023-08-10 00:00:00.000000', 'TP. Hồ Chí Minh', 'Máy bay', 6, 'Đà Nẵng - Huế - Đầm Lập An - La Vang - Động Phong Nha & Thiên Đường - KDL Bà Nà - Cầu Vàng -Sơn Trà - Hội An - Đà Nẵng (Khách sạn 4* trọn tour)', '5 ngày 4 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN3399-035-160823VN-D', 'Theo thực đơn', NULL, '10 điểm tham quan ngoài trời, 4 điểm tham quan trong nhà', 'Gia đình nhiều thế hệ', NULL, 9390000, NULL, NULL, 'NDSGN3399-035-160823VN-D.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'NDSGN876-267-100823VJ-F.jpg', 'Khách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP HỒ CH&Iacute; MINH - Đ&Agrave; NẴNG - HUẾ (Ăn trưa, chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch tập trung tại Ga đi Trong Nước, s&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> l&agrave;m thủ tục cho Qu&yacute; kh&aacute;ch đ&aacute;p chuyến bay đi&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a>. Xe đ&oacute;n qu&yacute; kh&aacute;ch tại s&acirc;n bay Đ&agrave; Nẵng, l&ecirc;n đường đi Huế. Tr&ecirc;n đường dừng tham quan:<br><br><strong>- Đầm Lập An:&nbsp;</strong>Ngắm cảnh m&acirc;y bồng bềnh tr&ecirc;n những ch&oacute;p n&uacute;i bao bọc quanh đầm,..<br><strong>- Tinh dầu tr&agrave;m Th&aacute;i H&agrave;:&nbsp;</strong>Huế được coi l&agrave; xứ sở của loại dầu tr&agrave;m nổi danh khắp nước với c&ocirc;ng dụng tuyệt vời, l&agrave; phương thuốc l&agrave;nh t&iacute;nh, an to&agrave;n với mọi lứa tuổi, gi&uacute;p ti&ecirc;u tan c&aacute;i mệt mỏi, c&aacute;c cơn đau kinh ni&ecirc;n, cho giấc ngủ được s&acirc;u hơn,&hellip;<br><strong>-&nbsp;<a href="https://travel.com.vn/hue/tour-chua-thien-mu.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Ch&ugrave;a Thi&ecirc;n Mụ</a>:&nbsp;</strong>ng&ocirc;i ch&ugrave;a được xem l&agrave; biểu tượng xứ Huế v&agrave; l&agrave; nơi lưu giữ nhiều cổ vật qu&yacute; gi&aacute; kh&ocirc;ng chỉ về mặt lịch sử m&agrave; c&ograve;n cả về nghệ thuật.<br><strong>- Đại Nội</strong>: ho&agrave;ng cung xưa của 13 vị Vua triều Nguyễn, tham quan Ngọ M&ocirc;n, Điện Th&aacute;i H&ograve;a, Tử Cấm Th&agrave;nh, Thế Miếu, Hiển L&acirc;m C&aacute;c, Cửu Đ&igrave;nh.<br><strong>- Dạo Phố Đ&ecirc;m:</strong> trải nghiệm một Huế về đ&ecirc;m kh&ocirc;ng hề trầm mặc với c&aacute;c c&ocirc;ng tr&igrave;nh trải theo chiều d&agrave;i lịch sử m&agrave; như một thiếu nữ trẻ trung kho&aacute;c l&ecirc;n m&igrave;nh bộ c&aacute;nh sắc m&agrave;u căng tr&agrave;n nhựa sống với tại Phố đi bộ ven s&ocirc;ng Hương h&agrave;i h&ograve;a với vẻ lung linh cầu Trường Tiền, tranh th&ecirc;u XQ, tự do thưởng thức c&aacute;c m&oacute;n đường phố xứ Huế như b&aacute;nh m&igrave; lọc, ch&egrave; Huế, ngắm nh&igrave;n thuyền rồng ngược xu&ocirc;i b&ecirc;n bến T&ograve;a Kh&acirc;m văng vẳng &acirc;m vang điệu h&ograve; Huế. Kh&aacute;m ph&aacute; khu phố T&acirc;y s&ocirc;i động về đ&ecirc;m với nhiều qu&aacute;n x&aacute; đ&ocirc;ng vui, những h&agrave;ng qu&agrave; lưu niệm.<br><br>Đo&agrave;n quay về kh&aacute;ch sạn, nhận ph&ograve;ng tự do nghỉ ngơi.</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Huế.</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - HUẾ - ĐỘNG THI&Ecirc;N ĐƯỜNG - ĐỘNG PHONG NHA (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch khởi h&agrave;nh sớm đi Quảng B&igrave;nh, đo&agrave;n gh&eacute; tham quan:<br><strong>- &nbsp;Động Phong Nha:</strong> Nằm trong quần thể Di sản thi&ecirc;n nhi&ecirc;n thế giới Phong Nha - Kẻ B&agrave;ng, được xem như chốn thần ti&ecirc;n bởi hệ thống n&uacute;i đ&aacute; v&ocirc;i v&agrave; s&ocirc;ng ngầm d&agrave;i nhất thế giới.<br><br><strong>-&nbsp;<a href="https://travel.com.vn/quang-binh/tour-dong-thien-duong.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Động Thi&ecirc;n Đường</a>:</strong> Một trong những hang động kỳ vĩ nhất thế giới, được xem như ho&agrave;ng cung dưới l&ograve;ng đất, một nh&aacute;nh thuộc Quần thể di sản thi&ecirc;n nhi&ecirc;n thế giới Vườn Quốc Gia Phong Nha Kẻ B&agrave;ng.&nbsp;<br><br>Sau khi tham quan, đo&agrave;n khởi h&agrave;nh về kh&aacute;ch sạn, nhận ph&ograve;ng tự do nghỉ ngơi v&agrave; kh&aacute;m ph&aacute; Quảng B&igrave;nh về đ&ecirc;m.<br><br><strong>Nghỉ đ&ecirc;m tại Quảng B&igrave;nh.&nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - QUẢNG B&Igrave;NH - LA VANG - LẬP AN - Đ&Agrave; NẴNG (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch khởi h&agrave;nh về&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a>, tr&ecirc;n đường đi dừng tham quan:<br><strong>- Th&agrave;nh cổ Quảng Trị:</strong> nghe hướng dẫn vi&ecirc;n thuyết minh Khu phi qu&acirc;n sự DMZ v&agrave; Cầu Hiền Lương S&ocirc;ng Bến Hải (Vĩ tuyến 17).<br><strong>-&nbsp;<a href="https://travel.com.vn/quang-tri/tour-thanh-dia-la-vang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Th&aacute;nh địa La Vang</a>:</strong> một trong bốn tiểu vương cung th&aacute;nh đường La M&atilde; tại Việt Nam<br>- Nghỉ ch&acirc;n, tham quan Lucky Pearl: l&agrave; một trong những điểm đến th&acirc;n thuộc của người d&acirc;n xứ Huế chuy&ecirc;n t&igrave;m kiếm những nguồn ngọc trai chất lượng v&agrave; đưa đến kh&aacute;ch h&agrave;ng những sản phẩm tinh tế nhất, cho Qu&yacute; kh&aacute;ch trải nghiệm đẳng cấp.<br><strong>- B&aacute;n đảo Sơn Tr&agrave; v&agrave; viếng Ch&ugrave;a Linh Ứng:&nbsp;</strong>Nơi đ&acirc;y c&oacute; tượng Phật Quan Thế &Acirc;m cao nhất Việt Nam. Đứng nơi đ&acirc;y, qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng to&agrave;n cảnh th&agrave;nh phố, n&uacute;i rừng v&agrave; biển đảo Sơn Tr&agrave; một c&aacute;ch ho&agrave;n hảo nhất.<br><br>Buổi tối Qu&yacute; kh&aacute;ch tự t&uacute;c đi dạo phố thưởng ngoạn cảnh đẹp của Đ&agrave; Nẵng về đ&ecirc;m, ngắm nh&igrave;n Cầu Rồng, Cầu T&igrave;nh Y&ecirc;u, Cầu Trần Thị L&yacute;, Trung T&acirc;m Thương Mại, Khu phố ẩm thực, Caf&eacute; - Bar - Disco&hellip;. Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</p>\r\n        <p><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng. &nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - Đ&Agrave; NẴNG - KHU DU LỊCH B&Agrave; N&Agrave; - CẦU V&Agrave;NG &ndash; HỘI AN (Ăn s&aacute;ng, chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:&nbsp;<br><strong>- B&atilde;i biển Mỹ Kh&ecirc;:</strong> Một trong những b&atilde;i biển quyến rũ nhất h&agrave;nh tinh, Qu&yacute; kh&aacute;ch tự do dạo biển, chụp h&igrave;nh.&nbsp;<br><strong>- Khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> B&agrave; N&agrave;</strong> <em><strong>(chi ph&iacute; c&aacute;p treo &amp; ăn trưa tự t&uacute;c):</strong>&nbsp;</em>tận hưởng kh&ocirc;ng kh&iacute; se lạnh của Đ&agrave; Lạt tại miền Trung, đo&agrave;n tự do tham quan Ch&ugrave;a Linh Ứng, Hầm Rượu Debay, vườn hoa Le Jardin D&rsquo;Amour, Khu T&acirc;m linh mới của B&agrave; N&agrave; viếng Đền Lĩnh Ch&uacute;a Linh Từ, khu vui chơi Fantasy Park, tự do chụp h&igrave;nh tại Cầu V&agrave;ng điểm tham quan si&ecirc;u hot tại B&agrave; N&agrave;&hellip;&nbsp;<strong>Ăn trưa tại B&agrave; N&agrave; tự t&uacute;c.</strong> Sau đ&oacute; đo&agrave;n tiếp tục tham quan vui chơi đến giờ xuống c&aacute;p.<br>Buổi chiều tiếp tục h&agrave;nh tr&igrave;nh đến với Hội An:&nbsp;<br><strong>- Tham quan L&agrave;ng Đ&aacute; Non Nước Nguyễn H&ugrave;ng:</strong> mua sắm sản phẩm đ&aacute; mỹ nghệ tạo phong thủy.&nbsp;<br><br>Buổi chiều tiếp tục h&agrave;nh tr&igrave;nh đến với Hội An:&nbsp;<br><strong>- Phố Cổ Hội An:</strong> Ch&ugrave;a Cầu, Nh&agrave; Cổ Ph&ugrave;ng Hưng, Hội Qu&aacute;n Phước Kiến, Cơ sở Thủ C&ocirc;ng Mỹ Nghệ,&hellip; Tự do thả đ&egrave;n hoa đăng cầu sức khỏe b&igrave;nh an &amp; ph&uacute;c lộc năm mới tr&ecirc;n d&ograve;ng s&ocirc;ng Ho&agrave;i&hellip;. (chi ph&iacute; tự t&uacute;c).&nbsp;<br><br>Đo&agrave;n quay về&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a>, nhận ph&ograve;ng kh&aacute;ch sạn, tự do nghỉ ngơi.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng. &nbsp;</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 5 - Đ&Agrave; NẴNG - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn.<br><br>Mua sắm đặc sản phố biển tại Hương Đ&agrave; hoặc Qu&agrave; Miền Trung hoặc Qu&agrave;&nbsp;<a href="https://travel.com.vn/da-nang/tour-da-nang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Nẵng</a> Thi&ecirc;n Ph&uacute;: với hơn 2000 sản phẩm đặc sản Đ&agrave; Nẵng, Miền Trung v&agrave; c&aacute;c v&ugrave;ng miền l&acirc;n cận, nổi bật nhất l&agrave; chả b&ograve;, b&ograve; kh&ocirc;, mực rim me, mực kh&ocirc;, ghẹ sữa sốt v&agrave; đầy đủ c&aacute;c loại kh&ocirc; c&aacute;,...</p>\r\n        <p>Xe tiễn Qu&yacute; kh&aacute;ch ra s&acirc;n bay Đ&agrave; Nẵng đ&oacute;n chuyến bay trở về Tp.Hồ Ch&iacute; Minh. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất. &nbsp;&nbsp;</p>\r\n        <p><br><strong>Lưu &yacute;:</strong><br>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;<br>- Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm,...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.<br>- Kh&aacute;ch Sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao Điểm</p>\r\n    </div>\r\n</div>', 134, '2023-11-16 00:00:00.000000', 'TP.Hà Nội', 'Máy bay, Xe du lịch', 9, 'Huế - La Vang - Động Phong Nha & Thiên Đường - KDL Bà Nà - Cầu Vàng - Sơn Trà - Hội An - Đà Nẵng (Khách sạn 4* trọn tour)', '5 ngày 4 đêm', 'Quanh năm', 'Ưu đãi trực tiếp vào giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN397-015-150823-V-7', 'Buffet sáng, Theo thực đơn, Đặc sản địa phương', NULL, 'Liên Tuyến, Đà Nẵng, Hội An, Quảng Bình, La Vang, Hà Nội, Lào Cai, Sapa, Ninh Bình', 'Cặp đôi, Gia đình nhiều thế hệ, Thanh niên', NULL, 19790000, NULL, NULL, 'NDSGN397-015-150823-V-7.jpg', 'DSSGN820-014-120823FWT.jpg', 'NDSGN1731-095-110823VU-V-7.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'Khách sạn 4 sao\r\n\r\nKhách sạn 3 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TPHCM &ndash; Đ&Agrave; NẴNG &ndash; HUẾ 02 bữa ăn: (Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>Qu&yacute; kh&aacute;ch tập trung tại Ga đi Trong Nước, s&acirc;n bay T&acirc;n Sơn Nhất, hướng dẫn vi&ecirc;n&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> l&agrave;m thủ tục cho Qu&yacute; kh&aacute;ch đ&aacute;p chuyến bay đi Đ&agrave; Nẵng. Xe đ&oacute;n qu&yacute; kh&aacute;ch tại s&acirc;n bay Đ&agrave; Nẵng, l&ecirc;n đường đi&nbsp;<a href="https://travel.com.vn/hue/tour-hue.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Huế</a>. Tr&ecirc;n đường dừng tham quan:<br><strong>- Đầm Lập An:</strong> Ngắm cảnh m&acirc;y bồng bềnh tr&ecirc;n những ch&oacute;p n&uacute;i bao bọc quanh đầm<br><strong>- Ch&ugrave;a Thi&ecirc;n Mụ:</strong> Ng&ocirc;i ch&ugrave;a được xem l&agrave; biểu tượng xứ Huế v&agrave; l&agrave; nơi lưu giữ nhiều cổ vật qu&yacute; gi&aacute; kh&ocirc;ng chỉ về mặt lịch sử m&agrave; c&ograve;n cả về nghệ thuật.<br><strong>-&nbsp;<a href="https://travel.com.vn/hue/tour-dai-noi.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đại Nội</a></strong>: Ho&agrave;ng cung xưa của 13 vị Vua triều Nguyễn, tham quan Ngọ M&ocirc;n, Điện Th&aacute;i H&ograve;a, Tử Cấm Th&agrave;nh, Thế Miếu, Hiển L&acirc;m C&aacute;c, Cửu Đ&igrave;nh.<br><strong>- Dạo Phố Đ&ecirc;m:&nbsp;</strong>Trải nghiệm một Huế về đ&ecirc;m căng tr&agrave;n nhựa sống với tại Phố đi bộ ven s&ocirc;ng Hương h&agrave;i h&ograve;a với vẻ lung linh cầu Trường Tiền, tranh th&ecirc;u XQ, tự do thưởng thức c&aacute;c m&oacute;n đường phố xứ Huế như b&aacute;nh m&igrave; lọc, ch&egrave; Huế, ngắm nh&igrave;n thuyền rồng ngược xu&ocirc;i b&ecirc;n bến T&ograve;a Kh&acirc;m văng vẳng &acirc;m vang điệu h&ograve; Huế. Kh&aacute;m ph&aacute; khu phố T&acirc;y s&ocirc;i động về đ&ecirc;m với nhiều qu&aacute;n x&aacute; đ&ocirc;ng vui, những h&agrave;ng qu&agrave; lưu niệm.<br><br>Đo&agrave;n quay về kh&aacute;ch sạn, nhận ph&ograve;ng tự do nghỉ ngơi.<br><br><strong>Nghỉ đ&ecirc;m tại Huế.</strong><br>&nbsp;</div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - HUẾ - QUẢNG B&Igrave;NH - ĐỘNG PHONG NHA &amp; THI&Ecirc;N ĐƯỜNG 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>D&ugrave;ng bữa s&aacute;ng kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch khởi h&agrave;nh sớm đi Quảng B&igrave;nh, đo&agrave;n gh&eacute; tham quan:<br><strong>-&nbsp;<a href="https://travel.com.vn/quang-binh/tour-dong-phong-nha.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Động Phong Nha</a>:</strong> Nằm trong quần thể Di sản thi&ecirc;n nhi&ecirc;n thế giới Phong Nha - Kẻ B&agrave;ng, được xem như chốn thần ti&ecirc;n bởi hệ thống n&uacute;i đ&aacute; v&ocirc;i v&agrave; s&ocirc;ng ngầm d&agrave;i nhất thế giới.<br><strong>-&nbsp;<a href="https://travel.com.vn/quang-binh/tour-dong-thien-duong.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Động Thi&ecirc;n Đường</a>:&nbsp;</strong>Một trong những hang động kỳ vĩ nhất thế giới, được xem như ho&agrave;ng cung dưới l&ograve;ng đất, một nh&aacute;nh thuộc Quần thể di sản thi&ecirc;n nhi&ecirc;n thế giới Vườn Quốc Gia Phong Nha Kẻ B&agrave;ng.&nbsp;<br><br>Sau khi tham quan, đo&agrave;n khởi h&agrave;nh về kh&aacute;ch sạn, nhận ph&ograve;ng tự do nghỉ ngơi v&agrave; kh&aacute;m ph&aacute; Quảng B&igrave;nh về đ&ecirc;m.<br><br><strong>Nghỉ đ&ecirc;m tại Quảng B&igrave;nh. &nbsp;</strong><br>&nbsp;</div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - QUẢNG B&Igrave;NH &ndash; LA VANG &ndash; Đ&Agrave; NẴNG &ndash; B&Aacute;N ĐẢO SƠN TR&Agrave; 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <div>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch khởi h&agrave;nh về Đ&agrave; Nẵng, tr&ecirc;n đường đi dừng tham quan:<br><strong>- Th&agrave;nh cổ Quảng Trị:&nbsp;</strong>Nghe hướng dẫn vi&ecirc;n thuyết minh Khu phi qu&acirc;n sự DMZ v&agrave; Cầu Hiền Lương S&ocirc;ng Bến Hải (Vĩ tuyến 17).<br><strong>-&nbsp;<a href="https://travel.com.vn/quang-tri/tour-thanh-dia-la-vang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Th&aacute;nh địa La Vang</a>:</strong> Một trong bốn tiểu vương cung th&aacute;nh đường La M&atilde; tại Việt Nam<br><em>(Ghi ch&uacute;: Giai đoạn từ 08/08 &ndash; 16/08 h&agrave;ng năm, Th&aacute;nh Địa La Vang diễn ra &quot;Đại Hội H&agrave;nh Hương Đức Mẹ La Vang&quot; h&agrave;ng năm, n&ecirc;n c&aacute;c tour khởi h&agrave;nh trong giai đoạn n&agrave;y sẽ kh&ocirc;ng v&agrave;o tham quan La Vang)</em><br><strong>-&nbsp;<a href="https://travel.com.vn/da-nang/tour-ban-dao-son-tra.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">B&aacute;n đảo Sơn Tr&agrave;&nbsp;</a>v&agrave; viếng Ch&ugrave;a Linh Ứng:</strong> Nơi đ&acirc;y c&oacute; tượng Phật Quan Thế &Acirc;m cao nhất Việt Nam. Đứng nơi đ&acirc;y, qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng to&agrave;n cảnh th&agrave;nh phố, n&uacute;i rừng v&agrave; biển đảo Sơn Tr&agrave; một c&aacute;ch ho&agrave;n hảo nhất.<br><strong>- Cửa h&agrave;ng Đặc sản Hương Đ&agrave;</strong>: Qu&yacute; kh&aacute;ch tự do mua sắm c&aacute;c mặt h&agrave;ng đặc sản Miền Trung với hơn 50 loại đặc sản ngon, lạ v&agrave; đậm n&eacute;t truyền thống.<br><br>Buổi tối Qu&yacute; kh&aacute;ch tự t&uacute;c đi dạo phố thưởng ngoạn cảnh đẹp của Đ&agrave; Nẵng về đ&ecirc;m, ngắm nh&igrave;n Cầu Rồng, Cầu T&igrave;nh Y&ecirc;u, Cầu Trần Thị L&yacute;, Trung T&acirc;m Thương Mại, Khu phố ẩm thực, Caf&eacute; - Bar - Disco&hellip;.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng. &nbsp;</strong><br>&nbsp;</div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - Đ&Agrave; NẴNG &ndash; B&Agrave; N&Agrave; &ndash; CẦU V&Agrave;NG - PHỐ CỔ HỘI AN 02 bữa ăn: (S&aacute;ng, Chiều)</h3>\r\n    <div>\r\n        <div>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch bắt đầu h&agrave;nh tr&igrave;nh đến với Khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> B&agrave; N&agrave;:&nbsp;<br><strong>- Khu du lịch B&agrave; N&agrave; - Suối Mơ:</strong> (chi ph&iacute; c&aacute;p treo &amp; ăn trưa tự t&uacute;c) Tận hưởng kh&ocirc;ng kh&iacute; se lạnh của Đ&agrave; Lạt tại miền Trung, đo&agrave;n tự do tham quan Ch&ugrave;a Linh Ứng, Hầm Rượu Debay, vườn hoa Le Jardin D&rsquo;Amour, Khu T&acirc;m linh mới của B&agrave; N&agrave; viếng Đền Lĩnh Ch&uacute;a Linh Từ, khu vui chơi Fantasy Park.&nbsp;<br><strong>- Cầu V&agrave;ng:</strong> Tự do chụp h&igrave;nh tại điểm tham quan si&ecirc;u hot của Đ&agrave; Nẵng.&nbsp;<br>- Trải nghiệm T&agrave;u hỏa leo n&uacute;i đến với L&acirc;u đ&agrave;i Mặt Trăng: C&ocirc;ng tr&igrave;nh mới tại KDL B&agrave; N&agrave; vừa được đưa v&agrave;o hoạt động trong th&aacute;ng 4.2022.&nbsp;<br><br>Ăn trưa tại B&agrave; N&agrave; tự t&uacute;c. Sau đ&oacute; đo&agrave;n tiếp tục tham quan vui chơi đến giờ xuống c&aacute;p.<br><br>Buổi chiều tiếp tục h&agrave;nh tr&igrave;nh đến với Hội An:&nbsp;<br><strong>- L&agrave;ng đ&aacute; Non nước Nguyễn H&ugrave;ng:</strong> T&igrave;m hiểu về nghề đ&aacute; truyền thống, thỏa sức mua sắm c&aacute;c mặt h&agrave;ng đ&aacute; thủ c&ocirc;ng mỹ nghệ nổi tiếng.&nbsp;<br><strong>- Phố Cổ Hội An:</strong> Ch&ugrave;a Cầu, Nh&agrave; Cổ Ph&ugrave;ng Hưng, Hội Qu&aacute;n Phước Kiến, Cơ sở Thủ C&ocirc;ng Mỹ Nghệ,&hellip; Tự do thả đ&egrave;n hoa đăng cầu sức khỏe b&igrave;nh an &amp; ph&uacute;c lộc năm mới tr&ecirc;n d&ograve;ng s&ocirc;ng Ho&agrave;i&hellip;.&nbsp;<strong>(chi ph&iacute; tự t&uacute;c).&nbsp;</strong><br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Nẵng.</strong><br>&nbsp;</div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 5 - Đ&Agrave; NẴNG - H&Agrave; NỘI &ndash; VĂN MIẾU - QUỐC TỬ GI&Aacute;M 02 bữa ăn: (S&aacute;ng, Trưa &ndash; Tự t&uacute;c bữa chiều)</h3>\r\n    <div>\r\n        <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn. Qu&yacute; kh&aacute;ch tự do dạo phố cảm nhận kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; khung cảnh y&ecirc;n b&igrave;nh v&agrave;o s&aacute;ng sớm. Xe tiễn Qu&yacute; kh&aacute;ch ra s&acirc;n bay Đ&agrave; Nẵng đ&oacute;n chuyến bay tiếp tục h&agrave;nh tr&igrave;nh đến với H&agrave; Nội.&nbsp;<br><br>Đến s&acirc;n bay Nội B&agrave;i, xe v&agrave; HDV&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> đ&oacute;n Qu&yacute; kh&aacute;ch đi H&agrave; Nội. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:<br><br><strong>-&nbsp;<a href="https://travel.com.vn/ha-noi/tour-van-mieu.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Văn Miếu</a>:</strong> Nơi thờ Khổng Tử v&agrave; c&aacute;c bậc hiền triết của Nho Gi&aacute;o, Quốc Tử Gi&aacute;m - trường đại học đầu ti&ecirc;n của Việt Nam, t&igrave;m về cội nguồn lịch sử của c&aacute;c vị Nho học.<br><br>Qu&yacute; kh&aacute;ch nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi hoặc tự do đi tham quan phố cổ H&agrave; Nội.<br><br><strong>Nghỉ đ&ecirc;m tại H&agrave; Nội</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 6 - H&Agrave; NỘI &ndash; SAPA &ndash; BẢN C&Aacute;T C&Aacute;T 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng. Xe khởi h&agrave;nh đưa Qu&yacute; kh&aacute;ch đi tham quan:<br><strong>-&nbsp;<a href="https://travel.com.vn/ha-noi/tour-lang-ho-chu-tich.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Lăng Hồ Chủ Tịch</a></strong> (kh&ocirc;ng viếng v&agrave;o thứ 2, thứ 6 h&agrave;ng tuần v&agrave; giai đoạn bảo tr&igrave; định k&igrave; h&agrave;ng năm 15/6 &ndash; 15/8) tham quan v&agrave; t&igrave;m hiểu cuộc đời v&agrave; sự nghiệp của vị cha gi&agrave; d&acirc;n tộc tại&nbsp;<em>Nh&agrave; S&agrave;n B&aacute;c Hồ, Bảo T&agrave;ng Hồ Ch&iacute; Minh, Ch&ugrave;a Một Cột.</em><br><br>Tiếp tục h&agrave;nh tr&igrave;nh, Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi&nbsp;<a href="https://travel.com.vn/lao-cai/tour-sapa.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Sapa</a> theo cao tốc H&agrave; Nội &ndash; L&agrave;o Cai. Tr&ecirc;n đường, Qu&yacute; kh&aacute;ch d&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng địa phương. Đến nơi, Qu&yacute; kh&aacute;ch tham quan:<br><br><strong>- Bản C&aacute;t C&aacute;t -</strong> đẹp như một bức tranh giữa v&ugrave;ng phố cổ Sapa, nơi đ&acirc;y thu h&uacute;t du kh&aacute;ch bởi cầu treo, th&aacute;c nước, guồng nước v&agrave; những mảng m&agrave;u hoa m&ecirc; hoặc du kh&aacute;ch khi lạc bước đến đ&acirc;y. Thăm những nếp nh&agrave; của người M&ocirc;ng, Dao, Gi&aacute;y trong bản, du kh&aacute;ch sẽ kh&ocirc;ng khỏi ngỡ ng&agrave;ng trước vẻ đẹp mộng mị của một trong những ng&ocirc;i l&agrave;ng cổ đẹp nhất Sapa.<br><br>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối v&agrave; nhận ph&ograve;ng nghỉ ngơi hoặc tự do dạo phố ngắm nh&agrave; thờ Đ&aacute; Sapa, tự do thưởng thức đặc sản v&ugrave;ng cao như:&nbsp;<strong>thịt lợn cắp n&aacute;ch nướng, trứng nướng, rượu t&aacute;o m&egrave;o, giao lưu với người d&acirc;n tộc v&ugrave;ng cao.</strong><br><br><strong>Nghỉ đ&ecirc;m tại Sapa</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 7 - SAPA &ndash; FANSIPAN LEGEND &ndash; Đ&Egrave;O &Ocirc; QUY HỒ 03 bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m s&aacute;ng tại kh&aacute;ch sạn. xe đưa đo&agrave;n ra ga&nbsp;<a href="https://travel.com.vn/lao-cai/tour-sapa.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Sapa</a>, Qu&yacute; kh&aacute;ch trải nghiệm đến khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> <strong>Fansipan Legend</strong> bằng<strong>&nbsp;T&agrave;u hỏa leo n&uacute;i Mường Hoa&nbsp;</strong>hiện đại nhất Việt Nam với tổng chiều d&agrave;i gần 2000m, thưởng ngoạn bức tranh phong cảnh đầy m&agrave;u sắc của c&aacute;nh rừng nguy&ecirc;n sinh, thung lũng Mường Hoa.<br>- Chinh phục đỉnh&nbsp;<strong>n&uacute;i Fansipan</strong> với độ cao 3.143m h&ugrave;ng vĩ bằng c&aacute;p treo (chi ph&iacute; tự t&uacute;c).&nbsp;<br>- Lễ Phật tại&nbsp;<strong>Ch&ugrave;a Tr&igrave;nh</strong> hay cầu ph&uacute;c lộc, b&igrave;nh an cho gia đ&igrave;nh tại<strong>&nbsp;B&iacute;ch V&acirc;n Thiền Tự&nbsp;</strong>trong hệ thống cảnh quan t&acirc;m linh tr&ecirc;n đỉnh Fansipan. &nbsp; &nbsp;<br><br>Tiếp tục h&agrave;nh tr&igrave;nh, Qu&yacute; kh&aacute;ch d&ugrave;ng cơm trưa v&agrave; tham quan:&nbsp;<br>- Chinh phục<strong>&nbsp;đ&egrave;o &Ocirc; Quy Hồ:</strong> Con đ&egrave;o đẹp, h&ugrave;ng vĩ v&agrave; d&agrave;i nhất trong Tứ Đại Đỉnh Đ&egrave;o miền Bắc.<br>- Khu du lịch&nbsp;<strong>Cổng Trời &Ocirc; Quy Hồ</strong>: Một trong những điểm săn m&acirc;y, ngắm ho&agrave;ng h&ocirc;n cực đẹp tại Sapa.<br><br>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm tối tại Sapa v&agrave; tự do nghỉ ngơi.<br><br><strong>Nghỉ đ&ecirc;m tại Sapa.</strong><br><br><em><strong>Lưu &yacute;: Trong trường hợp, Fansipan kh&ocirc;ng hoạt động chương tr&igrave;nh sẽ được thay thế bằng điểm tham quan Cầu K&iacute;nh Rồng M&acirc;y (kh&ocirc;ng bao gồm v&eacute; tham quan).</strong></em><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 8 - SAPA &ndash; L&Agrave;O CAI &ndash; H&Agrave; NỘI 03 Bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe khởi h&agrave;nh đưa Qu&yacute; kh&aacute;ch về H&agrave; Nội, tr&ecirc;n đường dừng tham quan mua sắm tại Si&ecirc;u thị Du lịch n&ocirc;ng nghiệp Ocop Ph&uacute; Thọ - chợ Tp Việt Tr&igrave; v&agrave; d&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng địa phương. &nbsp;Đến H&agrave; Nội nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi hoặc tự do đi tham quan&nbsp;<strong>Hồ Ho&agrave;n Kiếm</strong> ngắm b&ecirc;n ngo&agrave;i&nbsp;<strong>Th&aacute;p R&ugrave;a,&nbsp;<a href="https://travel.com.vn/ha-noi/tour-den-ngoc-son.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đền Ngọc Sơn</a>, Cầu Th&ecirc; H&uacute;c.</strong></p>\r\n        <p><br><strong>Nghỉ đ&ecirc;m tại H&agrave; Nội.</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 9 - H&Agrave; NỘI &ndash; HẠ LONG 03 Bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng. Xe khởi h&agrave;nh đưa Qu&yacute; kh&aacute;ch đến th&agrave;nh phố biển Hạ Long theo quốc lộ 18, tr&ecirc;n đường dừng ăn trưa v&agrave; tham quan v&ugrave;ng đất thi&ecirc;ng Y&ecirc;n Tử:&nbsp;<br><br>- Tham quan chụp h&igrave;nh L&agrave;ng Nương v&agrave; d&ugrave;ng bữa trưa tại Cơm Qu&ecirc;&nbsp;<br>- Qu&yacute; kh&aacute;ch&nbsp;<strong>l&ecirc;n c&aacute;p treo du ngoạn thắng cảnh thi&ecirc;n nhi&ecirc;n Đ&ocirc;ng Y&ecirc;n Tử&nbsp;</strong>(chi ph&iacute; c&aacute;p treo tự t&uacute;c), nơi c&ograve;n lưu giữ nhiều di t&iacute;ch lịch sử mệnh danh &ldquo;Đất tổ Phật gi&aacute;o Việt Nam&rdquo;, chi&ecirc;m b&aacute;i&nbsp;<strong>ch&ugrave;a Một M&aacute;i, ch&ugrave;a Hoa Y&ecirc;n&nbsp;</strong>&ndash; nơi tu h&agrave;nh của phật ho&agrave;ng Trần Nh&acirc;n T&ocirc;ng khai sinh ra d&ograve;ng mới Thiền Ph&aacute;i Tr&uacute;c L&acirc;m, nằm tr&ecirc;n lưng chừng n&uacute;i ở độ cao 516m. Theo dấu ch&acirc;n Phật Ho&agrave;ng viếng<strong>&nbsp;Ch&ugrave;a Đồng c&oacute; t&ecirc;n Thi&ecirc;n Tr&uacute;c Tự&nbsp;</strong>(ch&ugrave;a C&otilde;i Phật), tọa lạc ở đỉnh cao nhất d&atilde;y Y&ecirc;n Tử (1.068m) &ndash; ng&ocirc;i ch&ugrave;a bằng đồng lớn nhất Việt Nam.<br><br>Đến Hạ Long, Qu&yacute; kh&aacute;ch tham quan mua sắm tại&nbsp;<strong>Trung t&acirc;m Mỹ Ngọc</strong>- Trung t&acirc;m Ngọc trai lớn nhất miền bắc Việt Nam.Tới đ&acirc;y Qu&yacute; kh&aacute;ch chi&ecirc;m ngưỡng vẻ đẹp sang trọng, đủ c&aacute;c m&agrave;u sắc hấp dẫn của những vi&ecirc;n ngọc trai được nu&ocirc;i trong l&ograve;ng Di sản &ndash; Kỳ quan thi&ecirc;n nhi&ecirc;n thế giới vịnh Hạ Long tại ph&ograve;ng trưng b&agrave;y sản phẩm. Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn cho m&igrave;nh v&agrave; người th&acirc;n những đồ trang sức ngọc trai Hạ Long như &yacute; v&agrave; những sản phẩm lưu niệm được chế t&aacute;c từ vỏ trai, sản phẩm đạt chất lượng Occop 5 sao cấp Quốc gia đầu ti&ecirc;n của Việt Nam.&nbsp;<br><br>D&ugrave;ng cơm chiều v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn nghỉ nghơi. Buổi tối, Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; nhiều hoạt động dịch vụ giải tr&iacute; s&ocirc;i nổi tại &ldquo;phố cổ&rdquo; B&atilde;i Ch&aacute;y - nằm cạnh c&ocirc;ng vi&ecirc;n Sun World Hạ Long từ những ẩm thực đường phố đến c&aacute;c qu&aacute;n c&agrave; ph&ecirc; si&ecirc;u dễ thương như H&ograve;n Gai Coffee &amp; Lounge hay thoải m&aacute;i bung x&otilde;a tại The Mini Bar, Brothers Pub,&hellip;<br><br><strong>Nghỉ đ&ecirc;m tại Hạ Long.</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 10 - HẠ LONG &ndash; ĐỘNG THI&Ecirc;N CUNG &ndash; NINH B&Igrave;NH 03 Bữa ăn: (S&aacute;ng, Trưa, Chiều)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa qu&yacute; kh&aacute;ch ra bến t&agrave;u, xuống thuyền du ngoạn&nbsp;<a href="https://travel.com.vn/ha-long/tour-vinh-ha-long.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vịnh Hạ Long</a> - Thắng cảnh thi&ecirc;n nhi&ecirc;n tuyệt đẹp v&agrave; v&ocirc; c&ugrave;ng sống động, được UNESCO c&ocirc;ng nhận l&agrave; di sản thi&ecirc;n nhi&ecirc;n Thế giới năm 1994.&nbsp;<br><br><strong>- Động Thi&ecirc;n Cung:</strong> L&agrave; một trong những động đẹp nhất ở Hạ Long. Vẻ đẹp nguy nga v&agrave; lộng lẫy bởi những lớp thạch nhũ v&agrave; những luồng &aacute;nh s&aacute;ng lung linh.<br>- Từ tr&ecirc;n t&agrave;u ngắm nh&igrave;n c&aacute;c h&ograve;n đảo lớn nhỏ trong Vịnh Hạ Long<strong>: H&ograve;n G&agrave; Chọi, H&ograve;n Lư Hương.</strong><br><br>Xe đ&oacute;n Qu&yacute; kh&aacute;ch tại bến thuyền khởi h&agrave;nh đi Ninh B&igrave;nh - v&ugrave;ng đất mệnh danh l&agrave; &ldquo;Nơi mơ đến, chốn mong về&rdquo; với nhiều di t&iacute;ch văn h&oacute;a, thi&ecirc;n nhi&ecirc;n v&ocirc; c&ugrave;ng đặc sắc.<br><br>Buổi tối qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; Phố cổ Hoa Lư, một kh&ocirc;ng gian check-in cổ, đẹp, trầm mặc đẹp trở n&ecirc;n lung linh, huyền ảo hơn với sắc m&agrave;u của những chiếc đ&egrave;n lồng v&agrave; Bảo Th&aacute;p tr&ecirc;n Hồ Kỳ L&acirc;n; trải nghiệm nhiều hoạt động tr&ograve; chơi d&acirc;n gian v&agrave; nhiều loại h&igrave;nh văn h&oacute;a nghệ thuật từ d&acirc;n tộc như m&uacute;a rối nước, nhảy m&uacute;a Tắc X&igrave;nh, h&aacute;t xẩm, ... đến những buổi tr&igrave;nh diễn acoustic sẽ được diễn ra v&agrave;o c&aacute;c buổi tối ng&agrave;y cuối tuần.<br><br><strong>Nghỉ đ&ecirc;m tại Ninh B&igrave;nh.</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 11 - NINH B&Igrave;NH &ndash; S&Acirc;N BAY NỘI B&Agrave;I 02 Bữa ăn: (S&aacute;ng, Trưa)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch ăn s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa qu&yacute; kh&aacute;ch tham quan<br><strong>- Khu Du Lịch Tr&agrave;ng An:</strong> Qu&yacute; kh&aacute;ch l&ecirc;n thuyền truyền thống đi tham quan thắng cảnh hệ thống n&uacute;i đ&aacute; v&ocirc;i h&ugrave;ng vĩ v&agrave; c&aacute;c thung lũng ngập nước, th&ocirc;ng với nhau bởi c&aacute;c d&ograve;ng suối tạo n&ecirc;n c&aacute;c hang động ngập nước quanh năm. Điểm xuyến trong kh&ocirc;ng gian hoang sơ, tĩnh lặng l&agrave; h&igrave;nh ảnh r&ecirc;u phong, cổ k&iacute;nh của c&aacute;c m&aacute;i đ&igrave;nh, đền, phủ nằm n&eacute;p m&igrave;nh dưới ch&acirc;n c&aacute;c d&atilde;y n&uacute;i cao.<br><strong>-&nbsp;</strong>Viếng&nbsp;<strong>Ch&ugrave;a B&aacute;i Đ&iacute;nh:&nbsp;</strong>Một quần thể ch&ugrave;a với nhiều kỷ lục Việt Nam như pho tượng phật Di Lặc bằng đồng nặng 80 tấn, h&agrave;nh lang với 500 tượng vị La H&aacute;n, t&ograve;a Bảo Th&aacute;p cao 99m&hellip;&nbsp;<br><br>Xe khởi h&agrave;nh đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay Nội B&agrave;i l&agrave;m thủ tục đ&oacute;n chuyến bay về Tp.HCM. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất.</p>\r\n        <p><strong>----------*** CH&Acirc;N TH&Agrave;NH CẢM ƠN V&Agrave; HẸN GẶP LẠI ***----------</strong></p>\r\n        <p><strong>Lưu &yacute;:</strong><br>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế.&nbsp;<br>- Lịch tr&igrave;nh tham quan (tắm biển, ngắm hoa, trải nghiệm,...) rất dễ bị ảnh hưởng bởi thời tiết. Đ&acirc;y l&agrave; trường hợp bất khả kh&aacute;ng mong Qu&yacute; kh&aacute;ch hiểu v&agrave; th&ocirc;ng cảm.<br>- Kh&aacute;ch Sạn c&oacute; thể ở xa trung t&acirc;m th&agrave;nh phố v&agrave;o c&aacute;c m&ugrave;a Cao Điểm<br>&nbsp;</p>\r\n    </div>\r\n</div>', 136, '2023-08-15 00:00:00.000000', 'TP. Hồ Chí Minh', 'Máy bay, Xe du lịch', 5, 'Liên Tuyến Trung Bắc: Đà Nẵng - Huế - Động Phong Nha & Thiên Đường - Bà Nà - Cầu Vàng - Hội An - Hà Nội - Sapa - Fansipan - Vịnh Hạ Long - Động Thiên Cung - Bái Đính - Tràng An - Hà Nội', '11 ngày 10 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN5251-056-090823XE-V', 'Buffet sáng, Theo thực đơn, Đặc sản địa phương, Buffet sáng, Theo thực đơn, Đặc sản địa phương', 'todo', 'Nha Trang, Đà Lạt, Vinwonder, Ga Đà Lạt, Thác Bobla, Đường Hầm Đất Sét, Làng Hoa Vạn Thành, Làng Chài Xưa, Hòn Lao, Đảo Khỉ', 'Gia đình nhiều thế hệ', NULL, 4790000, NULL, NULL, 'NDSGN5251-056-090823XE-V.jpg', 'NDSGN303-031-100823VN-F-7.jpg', 'NDSGN303-031-100823VN-F-7.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'Khách sạn 3 sao\r\n\r\nKhách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP. HỒ CH&Iacute; MINH &ndash; Đ&Agrave; LẠT Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks&_ga=2.6687602.1560718033.1692242789-1928680096.1688736308&_gac=1.27781838.1688736577.Cj0KCQjw756lBhDMARIsAEI0Agkn2st-0ia2MNPATrB3wpO11pVSskcTEHhi87sQcCePmGzNQ-uL2PwaAjWaEALw_wcB" target="_blank">Vietravel</a> (190 Pasteur, phường V&otilde; Thị S&aacute;u, quận 3, TP.HCM), xe đưa đo&agrave;n khởi h&agrave;nh đi&nbsp;<a href="https://travel.com.vn/da-lat/tour-da-lat.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Lạt</a>. Tr&ecirc;n đường đi Qu&yacute; kh&aacute;ch gh&eacute; tham quan:<br><br><strong>- Th&aacute;c Bobla:</strong> đẹp như một bức tranh thi&ecirc;n nhi&ecirc;n với d&ograve;ng th&aacute;c nguy&ecirc;n sơ, h&ugrave;ng vĩ cao hơn 40m, rộng hơn 20m, c&ugrave;ng cảnh quan được t&ocirc;n tạo tuyệt đẹp, lưu giữ nhiều c&acirc;y cổ thụ h&agrave;ng trăm năm tuổi, ph&iacute;a xa h&uacute;t tầm mắt l&agrave; những đồi ch&egrave;, c&agrave; ph&ecirc; xanh m&aacute;t.<br><strong>- Ga Đ&agrave; Lạt:&nbsp;</strong>nh&agrave; ga cổ k&iacute;nh nhất Việt Nam v&agrave; Đ&ocirc;ng Dương, c&oacute; phong c&aacute;ch kiến tr&uacute;c độc đ&aacute;o với ba m&aacute;i h&igrave;nh ch&oacute;p c&aacute;ch điệu như ba đỉnh n&uacute;i Langbiang v&agrave; nh&agrave; r&ocirc;ng T&acirc;y Nguy&ecirc;n.<br><br>Đến Đ&agrave; Lạt sau khi ăn chiều, Qu&yacute; kh&aacute;ch nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi<br><br>Buổi tối, Qu&yacute; kh&aacute;ch tự do dạo phố thưởng thức ẩm thực nổi tiếng phố n&uacute;i: Lẩu g&agrave; l&aacute; &eacute; Tao Ngộ, Lẩu b&ograve; qu&aacute;n Gỗ, B&aacute;nh ướt l&ograve;ng g&agrave;, Ốc nhồi thịt, Xe lẩu buffet, Ng&oacute;i BBQ &hellip; hoặc đến Nhạc Qu&aacute;n Diễm Xưa lắng nghe những t&igrave;nh kh&uacute;c bất hủ của nhạc sỹ Trịnh C&ocirc;ng Sơn &hellip;<strong>&nbsp;(chi ph&iacute; tự t&uacute;c).&nbsp;<br><br>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt</strong><br>&nbsp;</div>\r\n            <div>&nbsp;</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - Đ&Agrave; LẠT - TH&Agrave;NH PHỐ NG&Agrave;N HOA Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch khởi h&agrave;nh đi tham quan:<br><br><strong>-&nbsp;<a href="https://travel.com.vn/da-lat/tour-duong-ham-dat-set.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đường Hầm Đất S&eacute;t</a>:</strong> c&aacute;c c&ocirc;ng tr&igrave;nh nghệ thuật với những t&aacute;c phẩm đi&ecirc;u khắc bằng đất nung ho&agrave;nh tr&aacute;ng, ấn tượng được s&aacute;ch Kỷ Lục Việt Nam c&ocirc;ng nhận như: Ga xe lửa, dinh Bảo Đại, Đại học&nbsp;<a href="https://travel.com.vn/da-lat/tour-da-lat.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Lạt</a>, nh&agrave; thờ Con G&agrave;, s&acirc;n bay Li&ecirc;n Khương, đường cao tốc, hồ Tuyền L&acirc;m, thung lũng T&igrave;nh y&ecirc;u&hellip;). Nổi tiếng l&agrave; &ldquo;hồ v&ocirc; cực&rdquo; thu h&uacute;t nhiều du kh&aacute;ch đến tham quan hiện nay.<br><br>Ăn trưa, đo&agrave;n về kh&aacute;ch sạn nghỉ ngơi. Buổi chiều đo&agrave;n tiếp tục tham quan:<br><strong>-&nbsp;<a href="https://travel.com.vn/da-lat/tour-cao-nguyen-hoa.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">KDL Cao Nguy&ecirc;n Hoa</a>:</strong> với diện t&iacute;ch hơn 122ha, l&agrave; nơi bảo tồn đa dạng sinh học đặc biệt l&agrave; c&aacute;c l&ograve;ai hoa th&acirc;n gỗ kh&ocirc;ng chỉ c&oacute; ở Đ&agrave; Lạt m&agrave; c&ograve;n tr&ecirc;n to&agrave;n Thế giới với mảng xanh của rừng - thảm cỏ tự nhi&ecirc;n rộng khắp, điểm xuyến những kh&oacute;m hoa Thanh Anh nhẹ nh&agrave;ng dọc lối đi. Đến đ&acirc;y Qu&yacute; kh&aacute;ch sẽ được trải nghiệm:&nbsp;<br><strong>- Qu&aacute;n</strong> <strong>Seven-T Coffee</strong> nằm giữa rừng th&ocirc;ng với view 360&deg; n&uacute;i đồi xanh m&aacute;t, bể bơi v&ocirc; cực giữa rừng nhiệt đới&nbsp;<br>- Xả stress v&agrave; ghi lại những khoảnh khắc đẹp với<strong>&nbsp;Đ&agrave; Lạt Swing</strong> &ndash; tr&ograve; chơi &ldquo;x&iacute;ch đu ti&ecirc;n&rdquo; lớn nhất Việt Nam.&nbsp;<br><strong>- Rose Garden</strong> &ndash; Vườn hoa hồng với h&agrave;ng trăm gốc hồng ngoại, hồng nội, hồng cổ thụ qu&yacute; hiếm tỏa hương khoe sắc bốn m&ugrave;a.&nbsp;<br>- Con đường rừng nhiệt đới dẫn lối đến<strong>&nbsp;gốc si cổ thụ ngh&igrave;n năm</strong> huyền b&iacute; v&agrave; cổ k&iacute;nh.&nbsp;<br>- Thoả sức chụp h&igrave;nh&nbsp;<strong>check in với d&agrave;n si&ecirc;u xe</strong> cực chill, cực chất hay ng&ocirc;i nh&agrave; ph&ugrave; thủy, đồi mai anh đ&agrave;o, đồi huệ s&ocirc;ng nile, đồi hoa sim t&iacute;m, đồi hoa ho&agrave;ng yến,&hellip;<br><strong>- Trang trại rau v&agrave; hoa Vạn Th&agrave;nh</strong>:&nbsp;du kh&aacute;ch sẽ cho&aacute;ng ngợp bởi vẻ quyến rũ của bức tranh tuyệt mỹ được tạo n&ecirc;n bởi v&ocirc; v&agrave;n c&aacute;c lo&agrave;i hoa đua nhau khoe sắc như đ&oacute;a Hồng, Cẩm Chướng, Lily, Đồng Tiền, Cẩm T&uacute; Cầu&hellip; Ngo&agrave;i ra, Qu&yacute; kh&aacute;ch c&ograve;n được kh&aacute;m ph&aacute; quy tr&igrave;nh trồng những loại n&ocirc;ng sản v&agrave; thỏa sức chụp h&igrave;nh tại vườn c&agrave; chua, b&iacute; khổng lồ, dưa Pepino, d&acirc;u v&agrave; c&aacute;c loại rau trồng thủy canh (t&ugrave;y theo m&ugrave;a).</div>\r\n            <div><strong>- Mua sắm đặc sản Đ&agrave; Lạt tại cơ sở mứt Thanh Nhu:&nbsp;</strong>c&aacute;c loại tr&aacute;i c&acirc;y sấy gi&ograve;n, sấy dẻo, nước cốt d&acirc;u tằm &ndash; d&acirc;u tươi, tr&agrave; atiso, tr&agrave; hoa, ...<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.&nbsp;</strong></div>\r\n            <div>&nbsp;</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - Đ&Agrave; LẠT - NHA TRANG Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Sau khi d&ugrave;ng bữa s&aacute;ng, đo&agrave;n khởi h&agrave;nh tham quan:<br>-&nbsp;<strong>Fairytale Land &amp; hầm rượu vang Vĩnh Tiến:&nbsp;</strong>đến đ&acirc;y du kh&aacute;ch như lạc v&agrave;o khu vườn cổ t&iacute;ch của những ch&uacute; l&ugrave;n Hobbiton, điểm xuyến trong khu vườn l&agrave; những ng&ocirc;i nh&agrave; độc đ&aacute;o v&agrave; đầy sắc m&agrave;u, những bức vẽ tr&ecirc;n tường đầy l&ocirc;i cuốn v&agrave; những thảm hoa nhỏ kh&ocirc;ng k&eacute;m phần quyến rũ. Dạo một v&ograve;ng quanh khu vườn đừng qu&ecirc;n rảo bước v&agrave;o hầm rượu vang với hơn 10.000 chai đang được lưu trữ dưới hầm.<br><br>Qu&yacute; kh&aacute;ch ăn trưa, đo&agrave;n tiếp tục khởi h&agrave;nh đi Nha Trang.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Nha Trang</strong></div>\r\n            <div>&nbsp;</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - NHA TRANG - H&Ograve;N LAO - VINWONDERS NHA TRANG Số bữa ăn: 2 bữa (Ăn s&aacute;ng, trưa, tự t&uacute;c ăn chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Sau khi d&ugrave;ng buffet s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n đến cảng xuống t&agrave;u du ngoạn v&agrave; tham quan:<br><br><strong>- H&ograve;n Lao - Đảo Khỉ:&nbsp;</strong>nơi c&oacute; hơn 1000 ch&uacute; khỉ đang được bảo tồn v&agrave; sinh sống tự nhi&ecirc;n. Qu&yacute; kh&aacute;ch tự do tắm biển, thư gi&atilde;n, tham quan xem chương tr&igrave;nh biểu diễn xiếc Khỉ, đua ch&oacute;, khỉ bơi lội hoặc trải nghiệm c&aacute;c m&ocirc;n thể thao tr&ecirc;n b&atilde;i biển v&agrave; tr&ograve; chơi: can&ocirc; k&eacute;o d&ugrave;, m&ocirc; t&ocirc; nước, đua xe c&ocirc;ng thức 1, bắn s&uacute;ng sơn, cưỡi đ&agrave; điểu &hellip; (chi ph&iacute; tự t&uacute;c).<br><br>Sau khi d&ugrave;ng bữa trưa, Qu&yacute; kh&aacute;ch quay về trung t&acirc;m th&agrave;nh phố. Buổi chiều Qu&yacute; kh&aacute;ch ăn chiều tự t&uacute;c v&agrave; tự do nghỉ ngơi tại kh&aacute;ch sạn hoặc tự t&uacute;c chi ph&iacute; lựa chọn 1 trong 2 điểm tham quan như sau:<br><br><strong>- Lựa chọn 1: Vinwonders Nha Trang</strong> (chi ph&iacute; tự t&uacute;c): Qu&yacute; kh&aacute;ch tự do tham quan vui chơi trong khu&ocirc;n vi&ecirc;n với những l&acirc;u đ&agrave;i ch&acirc;u &Acirc;u tr&aacute;ng lệ, c&aacute;p treo h&igrave;nh th&aacute;p Eiffel, Đồi Vạn Hoa,.. Dạo bước trong kh&ocirc;ng gian n&agrave;y v&agrave;o ban ng&agrave;y, du kh&aacute;ch tưởng như m&igrave;nh đang lạc v&agrave;o một phố cổ n&agrave;o đ&oacute; của phương t&acirc;y. Đ&ecirc;m xuống, cảnh vật trở n&ecirc;n lung linh hơn bao giờ hết bởi đ&egrave;n trang tr&iacute; trong c&aacute;c l&acirc;u đ&agrave;i, khu vui chơi, tạo n&ecirc;n vẻ đẹp huyền ảo, thần ti&ecirc;n.&nbsp;Tự do Tham gia những tr&ograve; chơi như th&aacute;p rơi tự do Sky Drop, trượt v&aacute;n Alpine Coaster, t&agrave;u lượn cao tốc Roller Coaster, đu d&acirc;y văng sẽ th&iacute;ch hợp với c&aacute;c bạn y&ecirc;u th&iacute;ch cảm gi&aacute;c mạnh tạo ra từ c&aacute;c c&uacute; xoay, lộn nh&agrave;o tr&ecirc;n kh&ocirc;ng trung, lật nghi&ecirc;ng. Qu&yacute; kh&aacute;ch c&oacute; thể dạo bước trong thế giới Thuỷ Cung, tại đ&acirc;y c&oacute; hơn 300 loại sinh vật biển qu&yacute; hiếm từ nhiều quốc gia kh&aacute;c nhau hội tụ về. Đặc biệt,&nbsp;<strong>Thủy Cung Vinpearl Land Nha Trang&nbsp;</strong>c&ograve;n mang tới cho du kh&aacute;ch một thế giới lung linh sắc m&agrave;u với c&aacute;c chương tr&igrave;nh biểu diễn nghệ thuật độc đ&aacute;o như biểu diễn N&agrave;ng ti&ecirc;n c&aacute; &ndash; nơi du kh&aacute;ch được chi&ecirc;m ngưỡng c&aacute;c n&agrave;ng ti&ecirc;n c&aacute; xinh đẹp bước ra từ cổ t&iacute;ch, hay chương tr&igrave;nh cho c&aacute; ăn &ndash; với những ch&agrave;ng thợ lặn khỏe khoắn, chơi đ&ugrave;a v&agrave; chăm s&oacute;c c&aacute;c cư d&acirc;n Thủy Cung. Hoặc tại L&acirc;u đ&agrave;i phim 4D, Qu&yacute; kh&aacute;ch sẽ được thưởng thức c&ocirc;ng nghệ chiếu phim cực sắc n&eacute;t c&ugrave;ng hiệu ứng 4D ch&acirc;n thực như đang sống trong thế giới đại dương. Ngo&agrave;i ra, c&ograve;n c&oacute; c&aacute;c chương tr&igrave;nh biểu diễn nhạc nước, biểu diễn c&aacute; heo, biểu diễn Carnival v&ocirc; c&ugrave;ng hấp dẫn....&nbsp;<br><br><strong>- Lựa chọn 2: du ngoạn tr&ecirc;n du thuyền Emperor (chi ph&iacute; tự t&uacute;c),</strong> t&agrave;u sẽ di chuyển dọc th&agrave;nh phố biển Nha Trang, du kh&aacute;ch sẽ được ngắm cảnh ho&agrave;ng h&ocirc;n đẹp nhất trong khi thưởng thức tiệc cocktail, bữa tối tinh tế v&agrave; nghệ sỹ ghi ta, violon chơi những bản nhạc du dương trong một khung cảnh l&atilde;ng mạn dưới trời sao, &aacute;nh trăng v&agrave; &aacute;nh đ&egrave;n rực rỡ của th&agrave;nh phố.<br><br><strong>Nghỉ đ&ecirc;m tại Nha Trang.</strong><br>&nbsp;</div>\r\n            <div>&nbsp;</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 5 - NHA TRANG &ndash; TP.HCM Số bữa ăn: 2 bữa (Ăn s&aacute;ng, trưa)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Sau khi d&ugrave;ng buffet s&aacute;ng v&agrave; trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa Qu&yacute; kh&aacute;ch đi tham quan:<br><br><strong>- Trung t&acirc;m trang sức Princess Jewelry (đường L&ecirc; Hồng Phong):&nbsp;</strong>tham quan, t&igrave;m hi&ecirc;u quy tr&igrave;nh sản xuất ngọc trai, c&aacute;ch ph&acirc;n loại. Tại đ&acirc;y, Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn cho m&igrave;nh những sản phẩm trang sức cao cấp nổi tiếng được gắn đ&aacute; qu&yacute;, ngọc trai cũng như những sản phẩm chất lượng cao được l&agrave;m từ ngọc trai nu&ocirc;i cấy tự nhi&ecirc;n.</div>\r\n            <div>- &nbsp;Mua sắm đặc sản phố biển tại&nbsp;<strong>Hương Đ&agrave; Nha Trang</strong><br><br>Ăn trưa. Đo&agrave;n khởi h&agrave;nh về lại Tp.HCM. Tr&ecirc;n đường về, đo&agrave;n dừng ch&acirc;n tại tham quan:&nbsp;<br><br><strong>- L&agrave;ng Ch&agrave;i Xưa:&nbsp;</strong>với lịch sử 300 năm c&aacute;i n&ocirc;i của nghề l&agrave;m nước mắm, trải nghiệm cảm gi&aacute;c lao động tr&ecirc;n đồng muối, đi tr&ecirc;n con đường rạng xưa, thăm phố cổ Phan Thiết, , thăm nh&agrave; lều của h&agrave;m hộ nước mắm xưa, đắm ch&igrave;m cảm x&uacute;c trong biển 3D v&agrave; th&iacute;ch th&uacute; khi đi chợ l&agrave;ng ch&agrave;i xưa với b&agrave;n t&iacute;nh tay, b&agrave;n c&acirc;n xưa th&uacute; vị,&hellip;<br><br>Xe đưa đo&agrave;n về lại điểm đ&oacute;n ban đầu v&agrave; kết th&uacute;c h&agrave;nh tr&igrave;nh nhiều trải nghiệm.</div>\r\n            <div>&nbsp;</div>\r\n        </div>\r\n        <p><strong>*** KẾT TH&Uacute;C CHƯƠNG TR&Igrave;NH &ndash; TẠM BIỆT QU&Yacute; KH&Aacute;CH***&nbsp;</strong></p>\r\n    </div>\r\n</div>', 115, '2023-09-19 00:00:00.000000', 'TP.Hà Nội', 'Xe du lịch', 2, 'Hành trình Hoa và Biển: Đà Lạt - Nha Trang', '5 ngày 4 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN547-083-080823XE-V-F', 'Theo thực đơn, Đặc sản địa phương', 'todo', 'Đà Lạt, Thác Bobla, Ga Đà Lạt, Cao Nguyên Hoa, Chợ Đà Lạt, Thiền Viện Trúc Lâm, Fresh Garden, Mongo Land', 'Gia đình nhiều thế hệ, Cặp đôi, Thanh niên, Người lớn tuổi, Trẻ em', NULL, 3390000, NULL, NULL, 'NDSGN547-083-080823XE-V-F.jpg', 'NDSGN303-031-100823VN-F-7.jpg', 'NDSGN1731-095-110823VU-V-7.jpg', 'NDSGN876-267-100823VJ-F.jpg', 'Khách sạn 3 sao\r\n\r\nKhách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP. HỒ CH&Iacute; MINH - TH&Aacute;C BOBLA - Đ&Agrave; LẠT Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, tối)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <div>\r\n                    <p>Qu&yacute; kh&aacute;ch tập trung tại c&ocirc;ng ty&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> (190 Pasteur, P. V&otilde; Thị S&aacute;u, Q3, TP.HCM). Đo&agrave;n khởi h&agrave;nh đi Đ&agrave; Lạt v&agrave; d&ugrave;ng bữa s&aacute;ng. Tr&ecirc;n đường, đo&agrave;n dừng ch&acirc;n tham quan:<br><br><strong>- Th&aacute;c Bobla:</strong> Đẹp như một bức tranh thi&ecirc;n nhi&ecirc;n với d&ograve;ng th&aacute;c nguy&ecirc;n sơ, h&ugrave;ng vĩ cao hơn 40m, rộng hơn 20m, c&ugrave;ng cảnh quan được t&ocirc;n tạo tuyệt đẹp, lưu giữ nhiều c&acirc;y cổ thụ h&agrave;ng trăm năm tuổi, ph&iacute;a xa h&uacute;t tầm mắt l&agrave; những đồi ch&egrave;, c&agrave; ph&ecirc; xanh m&aacute;t.<br><br>Sau đ&oacute; xe v&agrave; HDV đưa kh&aacute;ch đi tr&ecirc;n cung đường&nbsp;<strong>đ&egrave;o Mimosa&nbsp;</strong>&ndash; một trong những trục đường ch&iacute;nh dẫn v&agrave;o th&agrave;nh phố Đ&agrave; Lạt. So với đ&egrave;o Ngoạn Mục đầy th&aacute;ch thức, đ&egrave;o Prenn hiểm trở. Th&igrave; đ&egrave;o Mimosa lại mang vẻ đẹp nữ t&iacute;nh, hiền h&ograve;a v&agrave; quyến rũ du kh&aacute;ch gần xa. Đo&agrave;n dừng ch&acirc;n tham quan:<br><br><strong>- F C&aacute;nh Đồng Hoa:&nbsp;</strong>nằm ẩn m&igrave;nh trong một thung lũng b&ecirc;n sườn đồi thoai thoải, với một khoảng xanh m&aacute;t của hoa v&agrave; những b&oacute;ng c&acirc;y. Ph&iacute;a b&ecirc;n dưới l&agrave; những c&aacute;nh đồng hoa với mu&ocirc;n v&agrave;n m&agrave;u sắc đua nhau nở rộ dưới một v&ugrave;ng trời tuyệt đẹp mang đậm chất Đ&agrave; Lạt.&nbsp;<br><br>Sau bữa tối, xe đưa đo&agrave;n chi&ecirc;m ngưỡng khung cảnh&nbsp;<strong>&lsquo;&lsquo;Thung Lũng Đ&egrave;n&rsquo;&rsquo;</strong> về đ&ecirc;m tại qu&aacute;n&nbsp;<strong>c&agrave; ph&ecirc; L&agrave;ng Vũ Thị</strong> v&agrave; thưởng thức cốc c&agrave; ph&ecirc;/ ca cao n&oacute;ng - một trong những nơi sở hữu view ngắm thung lũng đ&egrave;n &lsquo;&lsquo;hot&rsquo;&rsquo; nhất Đ&agrave; Lạt. Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; dịp được tự tay nướng v&agrave; thưởng thức những củ khoai lang thơm ngon giữa tiết trời se lạnh. (Chi ph&iacute; tự t&uacute;c)<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</strong></p>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - GA XE LỬA &ndash; THIỀN VIỆN TR&Uacute;C L&Acirc;M &ndash; FRESH GARDEN Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, tối)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Đ&agrave; Lạt ch&agrave;o đ&oacute;n du kh&aacute;ch với kh&ocirc;ng kh&iacute; lạnh tr&agrave;n về, sau khi d&ugrave;ng bữa s&aacute;ng, xe đưa đo&agrave;n tham quan:<br><strong>- Ga xe lửa:&nbsp;</strong>đ&acirc;y được xem l&agrave; nh&agrave; ga cổ k&iacute;nh nhất Việt Nam v&agrave; Đ&ocirc;ng Dương, c&oacute; phong c&aacute;ch kiến tr&uacute;c độc đ&aacute;o, c&oacute; ba m&aacute;i h&igrave;nh ch&oacute;p, l&agrave; c&aacute;ch điệu ba đỉnh n&uacute;i Langbiang v&agrave; nh&agrave; r&ocirc;ng T&acirc;y Nguy&ecirc;n.<br><strong>- Thiền viện Tr&uacute;c L&acirc;m:&nbsp;</strong>với quần thể c&ocirc;ng tr&igrave;nh kiến tr&uacute;c độc đ&aacute;o, du kh&aacute;ch c&ograve;n được ngắm nh&igrave;n c&aacute;c vườn hoa xinh đẹp khoe sắc v&agrave; to&agrave;n cảnh Hồ Tuyền L&acirc;m với thi&ecirc;n nhi&ecirc;n, n&uacute;i đồi, rừng th&ocirc;ng Đ&agrave; Lạt.<br><br>Buổi chiều, đo&agrave;n tiếp tục tham quan:<br><strong>- Nh&agrave; Thờ Domain de Marie:</strong> theo d&ograve;ng nữ tu B&aacute;c &Aacute;i, tọa lạc tr&ecirc;n đồi Mai Anh, nổi bật với m&agrave;u hồng s&aacute;ng rực. Qu&yacute; kh&aacute;ch c&oacute; thể chọn cho m&igrave;nh những m&oacute;n đồ từ len trong khu&ocirc;n vi&ecirc;n ph&iacute;a sau nh&agrave; thờ.<br><strong>- Fresh Garden:</strong> l&agrave; một trong những khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> c&oacute; đồi hoa đẹp tại Đ&agrave; Lạt với view rừng nguy&ecirc;n sinh. Đến đ&acirc;y, qu&yacute; kh&aacute;ch sẽ đắm ch&igrave;m trong khung cảnh thi&ecirc;n nhi&ecirc;n rộng lớn đầy thơ mộng với ng&agrave;n hoa đua nở. Nổi bật giữa Fresh Garden l&agrave; c&aacute;nh đồng hoa b&aacute;t ng&aacute;t ngập sắc m&agrave;u. B&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; c&aacute;c tiểu cảnh v&ocirc; c&ugrave;ng độc đ&aacute;o, mới lạ &amp; duy nhất như: đường hầm nhiệt đới, đường hầm thời gian, hồ view rừng, cổng trời,...<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt</strong><br>&nbsp;</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - KDL CAO NGUY&Ecirc;N HOA &ndash; TỰ DO CHỢ Đ&Agrave; LẠT Số bữa ăn: 2 bữa (Ăn s&aacute;ng, trưa, tự t&uacute;c ăn tối)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <div>\r\n                    <div>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n tham quan:<br><strong>-&nbsp;<a href="https://travel.com.vn/da-lat/tour-cao-nguyen-hoa.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">KDL Cao Nguy&ecirc;n Hoa</a>:&nbsp;</strong>với diện t&iacute;ch hơn 122ha, l&agrave; nơi bảo tồn đa dạng sinh học đặc biệt l&agrave; c&aacute;c l&ograve;ai hoa th&acirc;n gỗ kh&ocirc;ng chỉ c&oacute; ở Đ&agrave; Lạt m&agrave; c&ograve;n tr&ecirc;n to&agrave;n Thế giới với mảng xanh của rừng - thảm cỏ tự nhi&ecirc;n rộng khắp, điểm xuyến những kh&oacute;m hoa Thanh Anh nhẹ nh&agrave;ng dọc lối đi. Đến đ&acirc;y Qu&yacute; kh&aacute;ch sẽ được trải nghiệm:&nbsp;<br><strong>- Qu&aacute;n Seven-T Coffee</strong> nằm giữa rừng th&ocirc;ng với view 360&deg; n&uacute;i đồi xanh m&aacute;t, bể bơi v&ocirc; cực giữa rừng nhiệt đới&nbsp;<br>- Xả stress v&agrave; ghi lại những khoảnh khắc đẹp với&nbsp;<strong>Đ&agrave; Lạt Swing&nbsp;</strong>&ndash; tr&ograve; chơi &ldquo;x&iacute;ch đu ti&ecirc;n&rdquo; lớn nhất Việt Nam.&nbsp;<br><strong>- Rose Garden&nbsp;</strong>&ndash; Vườn hoa hồng với h&agrave;ng trăm gốc hồng ngoại, hồng nội, hồng cổ thụ qu&yacute; hiếm tỏa hương khoe sắc bốn m&ugrave;a.&nbsp;<br>- Con đường rừng nhiệt đới dẫn lối đến&nbsp;<strong>gốc si cổ thụ ngh&igrave;n năm</strong> huyền b&iacute; v&agrave; cổ k&iacute;nh.&nbsp;<br>- Thoả sức chụp h&igrave;nh<strong>&nbsp;check in với d&agrave;n si&ecirc;u xe cực chill,</strong> cực chất hay ng&ocirc;i nh&agrave; ph&ugrave; thủy, đồi mai anh đ&agrave;o, đồi huệ s&ocirc;ng nile, đồi hoa sim t&iacute;m, đồi hoa ho&agrave;ng yến,&hellip;<br><strong>- Mua sắm đặc sản Đ&agrave; Lạt tại cơ sở mứt Thanh Nhu:&nbsp;</strong>c&aacute;c loại tr&aacute;i c&acirc;y sấy gi&ograve;n, sấy dẻo, nước cốt d&acirc;u tằm &ndash; d&acirc;u tươi, tr&agrave; atiso, tr&agrave; hoa, ...<br><br>Đo&agrave;n d&ugrave;ng bữa trưa v&agrave; về lại kh&aacute;ch sạn tự do nghỉ ngơi. Buổi chiều Qu&yacute; kh&aacute;ch c&oacute; thể tự t&uacute;c lựa chọn tham gia c&aacute;c trải nghiệm độc đ&aacute;o như:<br>- V&agrave;o 16h30&nbsp;<strong>đ&oacute;n ho&agrave;ng h&ocirc;n v&agrave; thưởng thức bữa tiệc &acirc;m nhạc c&ugrave;ng Amazing Show</strong> với kh&ocirc;ng gian ngo&agrave;i trời v&agrave; c&oacute; tầm nh&igrave;n xuống th&agrave;nh phố Đ&agrave; Lạt thơ mộng. Hoặc v&agrave;o 19h00&nbsp;<strong>thưởng thức những bản nhạc trữ t&igrave;nh xưa, nhạc Trịnh, nhạc Bolero tại &lsquo;&lsquo;&Acirc;m sắc Đ&agrave; Lạt &ndash; Cung Đ&agrave;n Xưa&rsquo;&rsquo;,</strong> Qu&yacute; kh&aacute;ch c&oacute; dịp trải nghiệm một phong c&aacute;ch sống &ldquo;chậm&rdquo; v&agrave; tận hưởng từng ph&uacute;t gi&acirc;y, lưu giữ những khoảnh khắc tuyệt vời ở tận s&acirc;u trong t&acirc;m hồn mỗi người. (Chi ph&iacute; tự t&uacute;c)<br>- Tham gia chương tr&igrave;nh<strong>&nbsp;ch&egrave;o thuyền Sup v&agrave; ngắm ho&agrave;ng h&ocirc;n tr&ecirc;n hồ Tuyền L&acirc;m&nbsp;</strong>l&agrave; hoạt động ngo&agrave;i trời mang đến cho qu&yacute; kh&aacute;ch cảm x&uacute;c vui vẻ, thư gi&atilde;n để thưởng thức cảnh đẹp của thi&ecirc;n nhi&ecirc;n tr&ecirc;n hồ. Đặc biệt c&ugrave;ng chụp những bức h&igrave;nh &ldquo; sống ảo&rdquo; rất chill, c<strong>heck-in b&atilde;i cỏ lau, rừng ngập nước,&hellip; thưởng thức ly tr&agrave;, c&agrave; ph&ecirc; thơm lừng v&agrave; x&igrave; xụp m&igrave; ly x&uacute;c x&iacute;ch. (Chi ph&iacute; tự t&uacute;c)</strong><br><strong>- Trải nghiệm tắm kho&aacute;ng n&oacute;ng &amp; x&ocirc;ng hơi tại Dlat&rsquo;s&nbsp;</strong>gi&uacute;p bạn thư gi&atilde;n v&agrave; nghỉ ngơi, giải tỏa căng thẳng (Chi ph&iacute; tự t&uacute;c)<br><strong>***Lưu &yacute;: Chi ph&iacute; v&eacute; v&agrave; đi lại tự t&uacute;c***</strong><br><br>Buổi tối Qu&yacute; kh&aacute;ch tự do tản bộ Chợ Đ&agrave; Lạt thưởng thức sữa đậu n&agrave;nh v&agrave; b&aacute;nh tr&aacute;ng nướng tại khu phố Tăng Bạt Hổ.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt</strong></div>\r\n                    <div>&nbsp;</div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - Đ&Agrave; LẠT &ndash; MONGO LAND &ndash; TP. HỒ CH&Iacute; MINH Số bữa ăn: 2 bữa (Ăn s&aacute;ng, trưa)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n tham quan:<br><br>- <strong>Mongo Land:&nbsp;</strong>mang<strong>&nbsp;</strong>vẻ đẹp ho&agrave;n to&agrave;n mới lạ - một &quot;tiểu M&ocirc;ng Cổ thu nhỏ&quot; trong l&ograve;ng Đ&agrave; Lạt được thiết kế như một n&ocirc;ng trại với những chiếc lều đủ m&agrave;u sắc. Tất cả đều được b&agrave;i tr&iacute; độc đ&aacute;o, bắt mắt theo phong c&aacute;ch M&ocirc;ng Cổ đặc trưng. Đến đ&acirc;y, ngo&agrave;i việc tận hưởng bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; chi&ecirc;m ngưỡng vẻ đẹp của thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, Qu&yacute; kh&aacute;ch sẽ được trải nghi&ecirc;m:</div>\r\n            <div>\r\n                <p><strong>-&nbsp;</strong>Thỏa th&iacute;ch chụp ảnh tại&nbsp;<strong>lều m&ocirc;ng cổ, x&iacute;ch đu, n&ocirc;ng trại &acirc;u, thảm cỏ, sa mạc xương rồng,&hellip;</strong></p>\r\n                <p>- H&ograve;a m&igrave;nh v&agrave;o thế giới của động vật:&nbsp;<strong>hươu sao, d&ecirc; mini, cừu, thỏ Việt, thỏ Holland Lop H&agrave; Lan, thỏ Sư Tử, chim Yến, Chinchilla, s&oacute;c Chipmunk,&hellip;</strong> v&agrave; tự tay cho th&uacute; ăn (thức ăn sẽ được Mongo Land chuẩn bị sẵn cho qu&yacute; kh&aacute;ch).</p>\r\n                <p>- Tham gia c&aacute;c hoạt động vui chơi như:&nbsp;<strong>trượt cỏ, bắn cung</strong></p>\r\n                <p>-&nbsp;<strong>Trở&nbsp;th&agrave;nh những c&ocirc; n&agrave;ng, ch&agrave;ng trai du mục thực thụ</strong> khi diện tr&ecirc;n m&igrave;nh trang phục người M&ocirc;ng Cổ. (Chi ph&iacute; thu&ecirc; trang phục tự t&uacute;c).<br><br>Đo&agrave;n dừng tại Bảo Lộc d&ugrave;ng cơm trưa, sau đ&oacute; khởi h&agrave;nh về điểm đ&oacute;n ban đầu. Chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a>.</p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>', 120, '2023-08-08 00:00:00.000000', 'TP.Đà Nẵng', 'Xe du lịch', 4, 'Siêu Sale 🔥 | Đà Lạt - Thác Bobla - Mongo Land - Fresh Garden - Kích cầu du lịch', '4 ngày 3 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN548-084-120823XE-V-F', 'Theo thực đơn, Buffet sáng', NULL, 'Đà Lạt, Dambri, Ga Xe Lửa, Mongo Land, Puppy Farm, Samten Hills', 'Gia đình nhiều thế hệ, Thanh niên, Cặp đôi', NULL, 3390000, NULL, NULL, 'NDSGN548-084-120823XE-V-F.jpg', 'NDSGN846-108-100823XE-D.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'Khách sạn 3 sao\r\n\r\nKhách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP. HỒ CH&Iacute; MINH - TH&Aacute;C DAMBRI - Đ&Agrave; LẠT Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, tối)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> (190 Pasteur, phường V&otilde; Thị S&aacute;u, Quận 3, TP.HCM), xe đưa đo&agrave;n khởi h&agrave;nh đi Đ&agrave; Lạt. Tr&ecirc;n đường đo&agrave;n dừng ch&acirc;n tham quan v&agrave; ăn trưa tại Bảo Lộc:<br><br><strong>- Th&aacute;c DamBri:&nbsp;</strong>được coi l&agrave; ngọn th&aacute;c lớn nhất v&ugrave;ng đất L&acirc;m Đồng với chiều cao 60m tạo th&agrave;nh 2 d&ograve;ng chảy cao thấp rất h&ugrave;ng vĩ. Khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> th&aacute;c Đamb&apos;ri l&agrave; một quần thể du lịch bao gồm khu vui chơi, hồ, th&aacute;c v&agrave; rừng nguy&ecirc;n sinh rộng h&agrave;ng ngh&igrave;n hecta. Nơi đ&acirc;y c&oacute; &ldquo;ống trượt d&agrave;i nhất Đ&ocirc;ng Nam &Aacute;&rdquo; với độ d&agrave;i 1.650 m<br><br>Tiếp tục h&agrave;nh tr&igrave;nh đến với th&agrave;nh phố sương m&ugrave;, Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối v&agrave; nhận ph&ograve;ng nghỉ ngơi.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</strong><br>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - Đ&Agrave; LẠT &ndash; ĐỒI CH&Egrave; CẦU ĐẤT &ndash; PUPPY FARM Số bữa ăn: 3 bữa (Ăn s&aacute;ng, trưa, tối)</h3>\r\n    <div>\r\n        <p>Đ&agrave; Lạt ch&agrave;o đ&oacute;n du kh&aacute;ch với du kh&aacute;ch với kh&ocirc;ng kh&iacute; lạnh tr&agrave;n về, sau khi d&ugrave;ng bữa s&aacute;ng, xe đưa đo&agrave;n tham quan:<br><strong>- Ga xe lửa:</strong> đ&acirc;y được xem l&agrave; nh&agrave; ga cổ k&iacute;nh nhất Việt Nam v&agrave; Đ&ocirc;ng Dương, c&oacute; phong c&aacute;ch kiến tr&uacute;c độc đ&aacute;o, c&oacute; ba m&aacute;i h&igrave;nh ch&oacute;p, l&agrave; c&aacute;ch điệu ba đỉnh n&uacute;i Langbiang v&agrave; nh&agrave; r&ocirc;ng T&acirc;y Nguy&ecirc;n.<br><br><strong>-&nbsp;<a href="https://travel.com.vn/da-lat/tour-doi-che-cau-dat.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đồi ch&egrave; Cầu Đất</a>&nbsp;</strong>&ndash;&nbsp;<strong>check-in tua bin điện gi&oacute;:</strong> tr&ecirc;n đầu l&agrave; bầu trời xanh ng&aacute;t với l&agrave;n m&acirc;y trắng bay bay, b&ecirc;n dưới l&agrave; thảm tr&agrave; xanh tươi tắn, ở giữa l&agrave; những c&aacute;nh đồng quạt gi&oacute; trắng quay đều tuyệt đẹp kh&ocirc;ng chỉ đưa lữ kh&aacute;ch h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n kho&aacute;ng đạt m&agrave; c&ograve;n ngẫu nhi&ecirc;n kết hợp những gam m&agrave;u h&agrave;i h&ograve;a, mang đến những bức ảnh v&ocirc; c&ugrave;ng n&ecirc;n thơ.&nbsp;<br><br>Buổi chiều, đo&agrave;n tiếp tục tham quan:<br><strong>- N&ocirc;ng trại c&uacute;n Puppy Farm:</strong> du kh&aacute;ch thỏa th&iacute;ch tạo d&aacute;ng c&ugrave;ng những ch&uacute; c&uacute;n si&ecirc;u dễ thương hay chọn cho m&igrave;nh những g&oacute;c chụp đẹp b&ecirc;n đồi hoa ngo&agrave;i trời rực rỡ, vườn d&acirc;u c&ocirc;ng nghệ cao, b&iacute; khổng lồ, dưa pepino, c&agrave; chua bi,&hellip; (theo m&ugrave;a).<br><br><strong>- Quảng trường L&acirc;m Vi&ecirc;n:</strong> Điểm nhấn của TP. Đ&agrave; Lạt b&ecirc;n bờ hồ Xu&acirc;n Hương với c&ocirc;ng tr&igrave;nh nghệ thuật b&ocirc;ng hoa d&atilde; quỳ v&agrave; nụ hoa Atiso khổng lồ được thiết kế bằng k&iacute;nh m&agrave;u, lấp l&aacute;nh phản chiếu đẹp mắt bởi &aacute;nh s&aacute;ng.<br><br>Buổi tối, đo&agrave;n tự do uống caf&eacute; quanh khu vực Hồ Xu&acirc;n Hương hay thưởng thức ly sữa đậu n&agrave;nh n&oacute;ng hổi, b&aacute;nh tr&aacute;ng nướng đặc sản Đ&agrave; Lạt khu vực Tăng Bạt Hổ.&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt</strong></p>\r\n        <p>&nbsp;</p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - Đ&Agrave; LẠT &ndash; MONGO LAND Số bữa ăn: 2 bữa (Ăn s&aacute;ng, trưa, tự t&uacute;c ăn tối)</h3>\r\n    <div>\r\n        <p>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n tham quan:<br><strong>- Mongo Land:&nbsp;</strong>mang vẻ đẹp ho&agrave;n to&agrave;n mới lạ - một &quot;tiểu M&ocirc;ng Cổ thu nhỏ&quot; trong l&ograve;ng Đ&agrave; Lạt được thiết kế như một n&ocirc;ng trại với những chiếc lều đủ m&agrave;u sắc. Tất cả đều được b&agrave;i tr&iacute; độc đ&aacute;o, bắt mắt theo phong c&aacute;ch M&ocirc;ng Cổ đặc trưng. Đến đ&acirc;y, ngo&agrave;i việc tận hưởng bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh v&agrave; chi&ecirc;m ngưỡng vẻ đẹp của thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, Qu&yacute; kh&aacute;ch sẽ được trải nghi&ecirc;m:<br>- Thỏa th&iacute;ch chụp ảnh tại&nbsp;<strong>lều m&ocirc;ng cổ, x&iacute;ch đu, n&ocirc;ng trại &acirc;u, thảm cỏ, sa mạc xương rồng,&hellip;</strong><br>- H&ograve;a m&igrave;nh v&agrave;o thế giới của động vật:&nbsp;<strong>hươu sao, d&ecirc; mini, cừu, thỏ Việt, thỏ Holland Lop H&agrave; Lan, thỏ Sư Tử, chim Yến, Chinchilla, s&oacute;c Chipmunk,&hellip;</strong> v&agrave; tự tay cho th&uacute; ăn (thức ăn sẽ được Mongo Land chuẩn bị sẵn cho qu&yacute; kh&aacute;ch).&nbsp;<br>- Tham gia c&aacute;c hoạt động vui chơi như: trượt cỏ, bắn cung<br>- Trở th&agrave;nh những c&ocirc; n&agrave;ng, ch&agrave;ng trai du mục thực thụ khi diện tr&ecirc;n m&igrave;nh trang phục người M&ocirc;ng Cổ. (Chi ph&iacute; thu&ecirc; trang phục tự t&uacute;c).</p>\r\n        <p><strong>- Mua sắm đặc sản Đ&agrave; Lạt tại cơ sở mứt Thanh Nhu:&nbsp;</strong>c&aacute;c loại tr&aacute;i c&acirc;y sấy gi&ograve;n, sấy dẻo, nước cốt d&acirc;u tằm &ndash; d&acirc;u tươi, tr&agrave; atiso, tr&agrave; hoa, ...<br><br>Đo&agrave;n d&ugrave;ng bữa trưa v&agrave; về lại kh&aacute;ch sạn tự do nghỉ ngơi. Buổi chiều Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn tham gia c&aacute;c trải nghiệm độc đ&aacute;o như:<br>- V&agrave;o 16h30&nbsp;<strong>đ&oacute;n ho&agrave;ng h&ocirc;n v&agrave; thưởng thức bữa tiệc &acirc;m nhạc c&ugrave;ng Amazing Show</strong> với kh&ocirc;ng gian ngo&agrave;i trời v&agrave; c&oacute; tầm nh&igrave;n xuống th&agrave;nh phố Đ&agrave; Lạt thơ mộng. Hoặc v&agrave;o 19h00<strong>&nbsp;thưởng thức những bản nhạc trữ t&igrave;nh xưa, nhạc Trịnh, nhạc Bolero tại &lsquo;&lsquo;&Acirc;m sắc Đ&agrave; Lạt &ndash; Cung Đ&agrave;n Xưa&rsquo;&rsquo;</strong>, Qu&yacute; kh&aacute;ch c&oacute; dịp trải nghiệm một phong c&aacute;ch sống &ldquo;chậm&rdquo; v&agrave; tận hưởng từng ph&uacute;t gi&acirc;y, lưu giữ những khoảnh khắc tuyệt vời ở tận s&acirc;u trong t&acirc;m hồn mỗi người. (Chi ph&iacute; tự t&uacute;c)<br>- Tham gia chương tr&igrave;nh&nbsp;<strong>ch&egrave;o thuyền Sup v&agrave; ngắm ho&agrave;ng h&ocirc;n tr&ecirc;n hồ Tuyền L&acirc;m</strong> l&agrave; hoạt động ngo&agrave;i trời mang đến cho qu&yacute; kh&aacute;ch cảm x&uacute;c vui vẻ, thư gi&atilde;n để thưởng thức cảnh đẹp của thi&ecirc;n nhi&ecirc;n tr&ecirc;n hồ. Đặc biệt c&ugrave;ng chụp những bức h&igrave;nh &ldquo; sống ảo&rdquo; rất chill,&nbsp;<strong>check-in b&atilde;i cỏ lau, rừng ngập nước,&hellip;</strong> <strong>thưởng thức ly tr&agrave;, c&agrave; ph&ecirc; thơm lừng v&agrave; x&igrave; xụp m&igrave; ly x&uacute;c x&iacute;ch.&nbsp;</strong>(Chi ph&iacute; tự t&uacute;c)<br><strong>- Trải nghiệm tắm kho&aacute;ng n&oacute;ng &amp; x&ocirc;ng hơi tại Dlat&rsquo;s&nbsp;</strong>gi&uacute;p bạn thư gi&atilde;n v&agrave; nghỉ ngơi, giải tỏa căng thẳng (Chi ph&iacute; tự t&uacute;c)<br><br>***Lưu &yacute;: Chi ph&iacute; v&eacute; v&agrave; đi lại tự t&uacute;c***<br><br>Buổi tối Qu&yacute; kh&aacute;ch tự do tản bộ Chợ Đ&agrave; Lạt v&agrave; thưởng thức ẩm thực địa phương: lẩu b&ograve;, lẩu g&agrave; l&aacute; &eacute;, b&aacute;nh ướt l&ograve;ng g&agrave;, b&aacute;nh căn,....&nbsp;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt</strong></p>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - Đ&Agrave; LẠT &ndash; SAMTEN HILLS Đ&Agrave; LẠT - TP. HỒ CH&Iacute; MINH Số bữa ăn: 2 bữa (Ăn s&aacute;ng, trưa)</h3>\r\n    <div>\r\n        <p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng, l&agrave;m thủ tục trả ph&ograve;ng. Sau đ&oacute; xe v&agrave; HDV đưa kh&aacute;ch đi tham quan:&nbsp;<br><br><strong>- Samten Hills Dalat:</strong> l&agrave; một khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> trải nghiệm về văn h&oacute;a t&acirc;m linh vừa đi v&agrave;o hoạt động từ cuối th&aacute;ng 2/2023. Lấy cảm hứng từ kiến tr&uacute;c độc đ&aacute;o của văn h&oacute;a T&acirc;y Tạng, kết hợp với phong c&aacute;ch thiết kế hiện đại, tất cả đ&atilde; tạo n&ecirc;n một kh&ocirc;ng gian Văn h&oacute;a T&acirc;m linh Phật gi&aacute;o Kim Cương Thừa &ndash; Drigung Kagyu Samten Ling Việt Nam. Với điểm nhấn nổi bật l&agrave;<strong>&nbsp;Đại bảo th&aacute;p Kinh Lu&acirc;n&nbsp;</strong>bằng đồng nguy&ecirc;n chất d&aacute;t v&agrave;ng 24K lớn nhất thế giới đ&atilde; được x&aacute;c lập kỷ lục Guinness.&nbsp;<br><br>Khởi h&agrave;nh về TP. Hồ Ch&iacute; Minh, đo&agrave;n dừng tại Bảo Lộc d&ugrave;ng cơm trưa, sau đ&oacute; khởi h&agrave;nh về điểm đ&oacute;n ban đầu. Chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh du lịch.</p>\r\n    </div>\r\n</div>', 123, '2023-10-12 00:00:00.000000', 'TP.Hà Nội', 'Máy bay, Xe du lịch', 9, 'Siêu Sale 🔥 | Đà Lạt - Thác Dambri - Đồi Chè Cầu Đất - Samten Hills Dalat - Chiêm ngưỡng Đại Bảo Tháp Kinh Luân lớn nhất thế giới - Kích cầu du lịch', '4 ngày 3 đêm', 'Quanh năm', 'Quanh năm');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN550-013-100823XE-F', 'Theo thực đơn', NULL, 'Phan Thiết, Mũi Né, Dinh Vạn Thủy Tú, Hòn Rơm', 'Cặp đôi, Gia đình nhiều thế hệ, Thanh niên, Trẻ em', NULL, 499000, NULL, NULL, 'NDSGN550-013-100823XE-F.jpg', 'DSSGN820-014-120823FWT.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'NDSGN550-013-100823XE-F.jpg', NULL, '<h3>Ng&agrave;y 1 - TP. HCM &ndash; ĐỒI C&Aacute;T BAY &ndash; CHỢ PHAN THIẾT (1 bữa ăn trưa)</h3>\r\n<div>\r\n    <div>\r\n        <div>Qu&yacute; kh&aacute;ch&nbsp;<strong>tập trung l&uacute;c 04:30 s&aacute;ng tại&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a>&nbsp;</strong>(190 Pasteur, Phường V&otilde; Thị S&aacute;u, Quận 3, TP.Hồ Ch&iacute; Minh) khởi h&agrave;nh<strong>&nbsp;đi Phan Thiết theo cung đường cao tốc mới Dầu Gi&acirc;y &ndash; Phan Thiết</strong>. Đo&agrave;n dừng ch&acirc;n ăn s&aacute;ng tr&ecirc;n cung đường đi (tự t&uacute;c chi ph&iacute;) v&agrave; tiếp tục h&agrave;nh tr&igrave;nh đến với v&ugrave;ng biển nổi tiếng của khu vực Nam Trung Bộ.<br><br>Ngồi tr&ecirc;n xe, Qu&yacute; kh&aacute;ch sẽ được chi&ecirc;m ngưỡng cảnh đẹp của v&ugrave;ng đất B&igrave;nh Thuận như:&nbsp;<strong>Lầu &Ocirc;ng Ho&agrave;ng, Th&aacute;p chăm Poshanu, B&atilde;i Đ&aacute; &Ocirc;ng Địa, Rạng Dừa H&agrave;m Tiến,&hellip;</strong> Đo&agrave;n dừng ch&acirc;n tham quan:<br><br><strong>&bull;&nbsp;<a href="https://travel.com.vn/phan-thiet/tour-doi-cat-vang.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đồi C&aacute;t V&agrave;ng</a>:&nbsp;</strong>Một trong những khu vực đẹp nhất nằm ở Mũi N&eacute; thu h&uacute;t kh&aacute; nhiều du kh&aacute;ch do h&igrave;nh d&aacute;ng đẹp của c&aacute;t v&agrave; m&agrave;u sắc của c&aacute;t, nơi đ&acirc;y được xem l&agrave; đồi c&aacute;t c&oacute; một kh&ocirc;ng hai tại Việt Nam bắt nguồn từ mỏ sắt cổ tồn tại h&agrave;ng trăm năm tạo n&ecirc;n.&nbsp;<br><br><strong>&bull; Khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> H&ograve;n Rơm - B&atilde;i biển H&ograve;n Rơm</strong>: như một phần đất liền nh&ocirc; ra khỏi biển với b&atilde;i c&aacute;t v&agrave;ng trải d&agrave;i. Đ&ocirc;i khi c&oacute; những cơn s&oacute;ng nhẹ nh&agrave;ng vỗ bờ v&agrave; h&agrave;ng dừa xanh cao v&uacute;t mang đến một bức tranh thủy mặc &ecirc;m đềm v&agrave; b&igrave;nh y&ecirc;n, qu&ecirc;n đi mọi mệt nhọc trong cuộc sống đời thường. Qu&yacute; kh&aacute;ch sẽ được vui chơi, tắm biển, ngắm cảnh, thưởng ngoạn phong cảnh hữu t&igrave;nh, thơ mộng m&agrave; mộc mạc, giản dị hoặc tự do thưởng thức c&aacute;c loại hải sản tươi ngon của v&ugrave;ng biển xinh đẹp n&agrave;y (Chi ph&iacute; tự t&uacute;c)&nbsp;<br><br>Qu&yacute; kh&aacute;ch tắm lại bằng nước ngọt, sau đ&oacute; đo&agrave;n thưởng thức bữa trưa tại nh&agrave; h&agrave;ng v&agrave; tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh, kh&iacute; hậu dễ chịu n&ecirc;n c&aacute;c bạn như được h&ograve;a m&igrave;nh, được thả hồn v&agrave;o thi&ecirc;n nhi&ecirc;n. Tiếp tục khởi h&agrave;nh về trung t&acirc;m th&agrave;nh phố Phan Thiết, dừng ch&acirc;n tham quan:&nbsp;<br><br><strong>&bull; Dinh Vạn Thủy T&uacute;:</strong> nơi thờ v&agrave; trưng b&agrave;y bộ xương c&aacute; &Ocirc;ng lớn nhất Việt Nam. Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; cơ hội t&igrave;m hiểu về tục thờ c&aacute; &Ocirc;ng của ngư d&acirc;n miền Trung.<br><br><strong>&bull; Chợ Phan Thiết:</strong> được x&acirc;y dựng v&agrave;o năm 1697, ở hai b&ecirc;n bờ s&ocirc;ng Mường M&aacute;n xưa( s&ocirc;ng C&agrave; Ty ng&agrave;y nay). Nơi đ&acirc;y tập trung nhiều đặc sản nổi tiếng của th&agrave;nh phố biển như c&aacute;c loại hải sản kh&ocirc; &ndash; tươi như t&ocirc;m, mực, c&aacute; kh&ocirc;,&hellip; Nhưng điều th&uacute; vị khi đến với khu chợ nổi tiếng n&agrave;y m&agrave; chắc hẳn kh&ocirc;ng ai muốn bỏ lỡ đ&oacute; l&agrave; cơ hội&nbsp;<strong>kh&aacute;m ph&aacute; những m&oacute;n ăn hấp dẫn, mang đậm n&eacute;t ẩm thực đặc trưng của miền Trung như b&aacute;nh căn, b&aacute;nh ướng, chả c&aacute;, b&aacute;nh bột lọc, trứng mực hấp&hellip; (chi ph&iacute; tự t&uacute;c)&nbsp;</strong><br><br>Đo&agrave;n khởi h&agrave;nh về TP. Hồ Ch&iacute; Minh. Xe v&agrave; HDV đưa về điểm đ&oacute;n ban đầu. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh du lịch.</div>\r\n    </div>\r\n</div>', 99, '2023-08-10 00:00:00.000000', 'TP.Hà Nội', 'Máy bay, Xe du lịch', 4, 'Phan Thiết - Mũi Né - Hòn Rơm - Đồi Cát Bay - Trải nghiệm cung đường cao tốc mới Dầu Giây Phan Thiết - Kích Cầu Du Lịch', '1 ngày', 'Quanh năm', 'Đã bao gồm trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN567-005-110823XE-V', 'Buffet sáng, Theo thực đơn, Đặc sản địa phương, Buffet sáng, Theo thực đơn, Đặc sản địa phương', NULL, 'Ninh Chữ, vịnh vĩnh Hy, Ninh Thuận, Poklong Giaraoi, Đà Lạt, Linh Ẩn tự, Samten Hills', 'Gia đình nhiều thế hệ, Thanh niên, Cặp đôi, Người lớn tuổi', NULL, 3790000, NULL, NULL, 'NDSGN567-005-110823XE-V.jpg', 'NDSGN547-083-080823XE-V-F.jpg', 'NDSGN548-084-120823XE-V-F.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'Khách sạn 3 sao', '<div>\r\n    <div>\r\n        <h3>Ng&agrave;y 1 - TP. HỒ CH&Iacute; MINH &ndash; C&Agrave; N&Aacute; &ndash; NINH CHỮ Số bữa ăn: 3 bữa ăn (S&aacute;ng, trưa, chiều)</h3>\r\n        <div>\r\n            <p>Qu&yacute; kh&aacute;ch tập trung tại C&ocirc;ng ty&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> (190 Pasteur, Phường V&otilde; Thị S&aacute;u, Quận 3, TP.Hồ Ch&iacute; Minh) khởi h&agrave;nh đi Phan Thiết theo&nbsp;<strong>cung đường cao tốc mới Dầu Gi&acirc;y &ndash; Phan Thiết &ndash; Vĩnh Hảo</strong>. Đo&agrave;n ăn s&aacute;ng tr&ecirc;n cung đường đi v&agrave; dừng ch&acirc;n tham quan:<br><br>-&nbsp;<strong>Dinh Vạn Thủy T&uacute;</strong>: nơi thờ v&agrave; trưng b&agrave;y bộ xương c&aacute; &Ocirc;ng lớn nhất Việt Nam. Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; cơ hội t&igrave;m hiểu về tục thờ c&aacute; &Ocirc;ng của ngư d&acirc;n miền Trung.<br><br>Sau bữa trưa, đo&agrave;n tiếp tục h&agrave;nh tr&igrave;nh v&agrave; dừng ch&acirc;n tham quan:<br><br>-<strong>&nbsp;B&atilde;i biển C&agrave; N&aacute;:</strong> một trong những b&atilde;i biển đẹp nhất miền Trung, vốn được mệnh danh l&agrave; &ldquo;N&agrave;ng c&ocirc;ng ch&uacute;a ngủ qu&ecirc;n&rdquo; bởi vẻ đẹp hoang sơ của n&oacute;.&nbsp;<br><br>-&nbsp;<strong>Cung đường ven biển Ninh Thuận &ndash; DT701:</strong> dừng ch&acirc;n chụp ảnh v&agrave; chi&ecirc;m ngưỡng cảnh đẹp hoang sơ m&agrave; tạo h&oacute;a đ&atilde; ban tặng cho v&ugrave;ng đất đầy nắng n&agrave;y, một b&ecirc;n l&agrave; n&uacute;i v&agrave; một b&ecirc;n l&agrave; biển cả m&ecirc;nh m&ocirc;ng.<br><br>Đến TP. Phan Rang &ndash; Th&aacute;p Ch&agrave;m, Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng địa phương v&agrave; nghỉ ngơi, tự do dạo phố đ&ecirc;m.<br><br><strong>Nghỉ đ&ecirc;m tại Ninh Chữ.</strong><br>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n    <div>\r\n        <h3>Ng&agrave;y 2 - NINH CHỮ &ndash; Đ&Egrave;O SONG PHA &ndash; Đ&Agrave; LẠT Số bữa ăn: 3 bữa ăn (S&aacute;ng, trưa, chiều)</h3>\r\n        <div>\r\n            <p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn v&agrave; l&agrave;m thủ tục trả ph&ograve;ng. Sau đ&oacute; khởi h&agrave;nh theo cung đường ven biển, đo&agrave;n dừng ch&acirc;n tham quan:<br><br><strong>-&nbsp;<a href="https://travel.com.vn/ninh-thuan/tour-vinh-vinh-hy.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vịnh Vĩnh Hy</a>:&nbsp;</strong>tại đ&acirc;y du kh&aacute;ch xuống t&agrave;u đ&aacute;y k&iacute;nh thưởng ngoạn to&agrave;n cảnh vịnh v&agrave; những rặng san h&ocirc; rực rỡ c&ugrave;ng với những sinh vật dưới đ&aacute;y biển.&nbsp;<br><br><strong>-&nbsp;Trở về B&atilde;i C&oacute;c:</strong> Qu&yacute; kh&aacute;ch tự do tắm biển v&agrave; thưởng thức hải sản biển tươi sống (chi ph&iacute; tự t&uacute;c).<br>&nbsp;<br><strong>- Vườn nho Ninh Thuận:&nbsp;</strong>L&agrave; loại nho đặc sản v&ugrave;ng đất nắng gi&oacute; Ninh Thuận để mua về l&agrave;m qu&agrave; cho gia đ&igrave;nh người th&acirc;n&nbsp;<br><br>Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng địa phương. Khởi h&agrave;nh đi&nbsp;<a href="https://travel.com.vn/da-lat/tour-da-lat.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Lạt</a> theo cung đường QL27 qua ngọn&nbsp;<strong>đ&egrave;o Song Pha hay c&ograve;n gọi l&agrave; đ&egrave;o Ngoạn Mục&nbsp;</strong>để đến với cao nguy&ecirc;n Di Linh. Tr&ecirc;n đường đi, đo&agrave;n dừng ch&acirc;n tham quan:<br><br><strong>- Th&aacute;p Ch&agrave;m Poklong Giarai:</strong> một c&ocirc;ng tr&igrave;nh độc đ&aacute;o, được c&ocirc;ng nhận l&agrave; di t&iacute;ch kiến tr&uacute;c nghệ thuật v&agrave; đi&ecirc;u khắc Chăm. Nơi đ&acirc;y được xem l&agrave; cụm th&aacute;p h&ugrave;ng vĩ v&agrave; đẹp nhất trong những đền th&aacute;p của người Chăm c&ograve;n tồn tại ở Việt Nam. Qu&yacute; kh&aacute;ch c&oacute; dịp nghe thuyết minh về văn h&oacute;a, phong tục, lễ hội, c&ugrave;ng tham gia m&uacute;a h&aacute;t với người Chăm.&nbsp;<br><br>Đến Đ&agrave; Lạt, xe đưa đo&agrave;n về kh&aacute;ch sạn nhận ph&ograve;ng v&agrave; nghỉ ngơi. Buổi tối Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương v&agrave; tự do tản bộ tham quan chợ đ&ecirc;m Đ&agrave; Lạt về đ&ecirc;m v&agrave; kh&aacute;m ph&aacute; ẩm thực đường phố độc đ&aacute;o của Đ&agrave; Lạt như b&aacute;nh tr&aacute;ng nướng phố Xu&acirc;n Diệu, sữa đậu n&agrave;nh Tăng Bạt Hổ, Kem Bơ, xi&ecirc;n nướng,&hellip;<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</strong><br>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n    <div>\r\n        <h3>Ng&agrave;y 3 - Ng&agrave;y 3 : Đ&Agrave; LẠT &ndash; LINH ẨN TỰ - C&Agrave; PH&Ecirc; M&Ecirc; LINH Số bữa ăn: 2 bữa ăn (S&aacute;ng, trưa, tự t&uacute;c ăn chiều)</h3>\r\n        <div>\r\n            <p><a href="https://travel.com.vn/da-lat/tour-da-lat.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Đ&agrave; Lạt</a> ch&agrave;o đ&oacute;n du kh&aacute;ch với kh&ocirc;ng kh&iacute; lạnh tr&agrave;n về, sau khi d&ugrave;ng bữa s&aacute;ng, xe đưa đo&agrave;n tham quan:<br><br><strong>- Linh Ẩn Tự:</strong> nơi c&oacute; tượng quan &acirc;m lớn nhất L&acirc;m Đồng, l&agrave; một ng&ocirc;i ch&ugrave;a ấn tượng với một cảnh quan cực kỳ đẹp v&agrave; h&ugrave;ng vĩ.&nbsp;<br><br><strong>- C&agrave; Ph&ecirc; M&ecirc; Linh:&nbsp;</strong>được thiết kế với kh&ocirc;ng gian mở c&oacute; một tầm nh&igrave;n trọn vẹn 360 độ hướng rẫy c&agrave; ph&ecirc; bạt ng&agrave;n... Đặc biệt, thưởng thức một ly c&agrave; ph&ecirc; chồn nguy&ecirc;n chất với hương vị đậm đ&agrave; đặc trưng của chất xạ hương (chi ph&iacute; tự t&uacute;c). Ngo&agrave;i ra, Qu&yacute; kh&aacute;ch c&oacute; thể check in những phi&ecirc;n bản thu nhỏ nhưng kh&ocirc;ng k&eacute;m phần sống động của những c&ocirc;ng tr&igrave;nh nổi tiếng như &quot;cổng trời Bali, cổng trời Linh Quy Ph&aacute;p Ấn, nấc thang l&ecirc;n thi&ecirc;n đường, tổ chim...<br><br><strong>- Mua sắm đặc sản Đ&agrave; Lạt tại cơ sở mứt Thanh Nhu (chi ph&iacute; tự t&uacute;c)</strong>: c&aacute;c loại tr&aacute;i c&acirc;y sấy gi&ograve;n, sấy dẻo, nước cốt d&acirc;u tằm &ndash; d&acirc;u tươi, tr&agrave; atiso, tr&agrave; hoa, ...<br><br><br>Đo&agrave;n d&ugrave;ng bữa trưa v&agrave; về lại kh&aacute;ch sạn tự do nghỉ ngơi. Buổi chiều Qu&yacute; kh&aacute;ch c&oacute; thể tự t&uacute;c lựa chọn tham gia c&aacute;c trải nghiệm độc đ&aacute;o như:<br><br><em>- V&agrave;o 16h30&nbsp;<strong>đ&oacute;n ho&agrave;ng h&ocirc;n v&agrave; thưởng thức bữa tiệc &acirc;m nhạc c&ugrave;ng Amazing Show</strong> với kh&ocirc;ng gian ngo&agrave;i trời v&agrave; c&oacute; tầm nh&igrave;n xuống th&agrave;nh phố Đ&agrave; Lạt thơ mộng. Hoặc v&agrave;o 19h00&nbsp;<strong>thưởng thức những bản nhạc trữ t&igrave;nh xưa, nhạc Trịnh, nhạc Bolero tại &lsquo;&lsquo;&Acirc;m sắc Đ&agrave; Lạt &ndash; Cung Đ&agrave;n Xưa&rsquo;&rsquo;,&nbsp;</strong>Qu&yacute; kh&aacute;ch c&oacute; dịp trải nghiệm một phong c&aacute;ch sống &ldquo;chậm&rdquo; v&agrave; tận hưởng từng ph&uacute;t gi&acirc;y, lưu giữ những khoảnh khắc tuyệt vời ở tận s&acirc;u trong t&acirc;m hồn mỗi người. (Chi ph&iacute; tự t&uacute;c)<br><br><strong>- Trải nghiệm tắm kho&aacute;ng n&oacute;ng &amp; x&ocirc;ng hơi tại Dlat&rsquo;s</strong> gi&uacute;p bạn thư gi&atilde;n v&agrave; nghỉ ngơi, giải tỏa căng thẳng (Chi ph&iacute; tự t&uacute;c)</em><br><br><strong>***Lưu &yacute;: Chi ph&iacute; v&eacute; v&agrave; đi lại tự t&uacute;c***</strong><br><br>Qu&yacute; kh&aacute;ch tự t&uacute;c ăn tối v&agrave; đi dạo Đ&agrave; Lạt về đ&ecirc;m, thưởng thức c&aacute;c m&oacute;n ngon ở Chợ đ&ecirc;m &Acirc;m Phủ - chợ đ&ecirc;m Đ&agrave; Lạt, thưởng thức hương vị c&agrave; ph&ecirc; phố n&uacute;i ở qu&aacute;n Thủy Tạ, ngắm cảnh Hồ Xu&acirc;n Hương.<br><br><strong>Nghỉ đ&ecirc;m tại Đ&agrave; Lạt.</strong></p>\r\n        </div>\r\n    </div>\r\n    <div>\r\n        <h3>Ng&agrave;y 4 - Đ&Agrave; LẠT &ndash; SAMTEN HILLS &ndash; TP.HCM Số bữa ăn: 2 bữa ăn (S&aacute;ng, trưa)</h3>\r\n        <div>\r\n            <p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng, l&agrave;m thủ tục trả ph&ograve;ng. Sau đ&oacute; xe v&agrave; HDV đưa kh&aacute;ch tham quan:<br><br><strong>- Samten Hills Dalat:&nbsp;</strong>l&agrave; một khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> trải nghiệm về văn h&oacute;a t&acirc;m linh vừa đi v&agrave;o hoạt động từ cuối th&aacute;ng 2/2023. Lấy cảm hứng từ kiến tr&uacute;c độc đ&aacute;o của văn h&oacute;a T&acirc;y Tạng, kết hợp với phong c&aacute;ch thiết kế hiện đại, tất cả đ&atilde; tạo n&ecirc;n một kh&ocirc;ng gian&nbsp;<strong>Văn h&oacute;a T&acirc;m linh Phật gi&aacute;o Kim Cương Thừa &ndash; Drigung Kagyu Samten Ling Việt Nam.</strong> Với điểm nhấn nổi bật l&agrave;<strong>&nbsp;Đại bảo th&aacute;p Kinh Lu&acirc;n</strong> bằng đồng nguy&ecirc;n chất d&aacute;t v&agrave;ng 24K lớn nhất thế giới đ&atilde; được x&aacute;c lập kỷ lục Guinness.&nbsp;<br><br>Đo&agrave;n d&ugrave;ng cơm trưa, sau đ&oacute; khởi h&agrave;nh về điểm đ&oacute;n ban đầu. Chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh du lịch.<br>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <div>\r\n        <div><br></div>\r\n    </div>\r\n</div>', 152, '2023-08-11 00:00:00.000000', 'TP. Hồ Chí Minh', 'Xe du lịch', 6, 'Ninh Chữ - Đà Lạt: Vịnh Vĩnh Hy - Linh Ẩn Tự - Thưởng Thức Buffet Lẩu Rau - Samten Hills Dalat - Chiêm ngưỡng Đại Bảo Tháp Kinh Luân lớn nhất thế giới', '4 ngày 3 đêm', 'Quanh năm', 'Quanh năm');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN612-036-100823XE-F', 'Theo thực đơn', NULL, '4 địa điểm ngoài trời, 2 địa điểm trong nhà', 'Gia đình nhiều thế hệ', NULL, 3690000, NULL, NULL, 'NDSGN612-036-100823XE-F.jpg', 'NDSGN846-108-100823XE-D.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'NDSGN550-013-100823XE-F.jpg', 'Khách sạn 3 sao\r\n\r\nKhách sạn 4 sao\r\n\r\nKhách sạn 5 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP.HCM &ndash; PHAN THIẾT &ndash; NHA TRANG Số bữa ăn: 03 (Ăn s&aacute;ng, trưa, tối)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <p>Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> (190 Pasteur, Phường V&otilde; Thị S&aacute;u, Quận 3, TPHCM). Xe đưa đo&agrave;n khởi h&agrave;nh đi Nha Trang. Tr&ecirc;n đường đi đo&agrave;n gh&eacute; tham quan:</p>\r\n                <ul>\r\n                    <li><strong>Bảo t&agrave;ng L&agrave;ng Ch&agrave;i Xưa</strong>: mở ra kh&ocirc;ng gian tương t&aacute;c t&aacute;i hiện L&agrave;ng Ch&agrave;i Xưa Mũi N&eacute; với lịch sử 300 năm c&aacute;i n&ocirc;i của nghề l&agrave;m nước mắm, trải nghiệm cảm gi&aacute;c lao động tr&ecirc;n đồng muối, đi tr&ecirc;n con đường rạng xưa, thăm phố cổ Phan Thiết, v&agrave;o x&oacute;m l&ograve; tĩn, thăm nh&agrave; lều của h&agrave;m hộ nước mắm xưa, đắm ch&igrave;m cảm x&uacute;c trong biển 3D v&agrave; th&iacute;ch th&uacute; khi đi chợ l&agrave;ng ch&agrave;i xưa với b&agrave;n t&iacute;nh tay, b&agrave;n c&acirc;n xưa th&uacute; vị.</li>\r\n                </ul>\r\n                <p>Đến Nha Trang, Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối, tự do kh&aacute;m ph&aacute; th&agrave;nh phố biển về đ&ecirc;m. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - NHA TRANG - BIỂN NHŨ TI&Ecirc;N - VINWONDERS (Ăn s&aacute;ng, trưa, ăn chiều tự t&uacute;c)</h3>\r\n    <div>\r\n        <div>\r\n            <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n tham quan:</p>\r\n            <ul>\r\n                <li><strong>Biển Nhũ Ti&ecirc;n</strong>: đắm m&igrave;nh trong l&agrave;n nước biển trong xanh, m&aacute;t mẻ, được bao bọc bởi b&atilde;i c&aacute;t d&agrave;i trắng x&oacute;a v&agrave; những h&agrave;ng dừa lao xao trong gi&oacute;. Với vẻ đẹp hoang sơ, thơ mộng, biển Nhũ Ti&ecirc;n tựa như n&agrave;ng ti&ecirc;n đang ngủ v&ugrave;i giữa kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng của s&oacute;ng, của gi&oacute;, của n&uacute;i v&agrave; của m&acirc;y.</li>\r\n            </ul>\r\n            <p>Buổi chiều, Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; th&agrave;nh phố Nha Trang hoặc đăng k&yacute; tham quan<strong>&nbsp;Vinwonders Nha Trang&nbsp;</strong>đầy sắc m&agrave;u (chi ph&iacute; tự t&uacute;c): ngắm to&agrave;n cảnh Vịnh Nha Trang từ đỉnh v&ograve;ng xoay mặt trời lớn nhất Việt Nam, tham quan bộ sưu tập &ldquo;kỳ hoa, dị thảo&rdquo; tại đồi Vạn Hoa, khu vườn th&uacute; mở - vườn Qu&yacute; Vương, tham gia c&aacute;c tr&ograve; chơi tương t&aacute;c v&agrave; li&ecirc;n ho&agrave;n tại Quảng trường Thần Thoại hay V&ugrave;ng đất y&ecirc;u thương, xem phim 4D tại l&acirc;u đ&agrave;i Đại Dương, kh&aacute;m ph&aacute; khu tr&ograve; chơi cảm gi&aacute;c mạnh, khu tr&ograve; chơi trong nh&agrave; hay tắm biển, tắm hồ bơi lớn nhất Đ&ocirc;ng Nam &Aacute;&hellip; v&agrave; thưởng thức chương tr&igrave;nh biểu diễn Nhạc Nước hiện đại. Đo&agrave;n tự do d&ugrave;ng bữa tối tại đ&acirc;y v&agrave; tiếp tục tham gia c&aacute;c hoạt động buổi tối tại Vinpearl đến giờ về lại đất liền. Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - NHA TRANG - I-RESORT - TH&Aacute;P B&Agrave; PONARGAR (Ăn s&aacute;ng, ăn trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <p>D&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe đưa đo&agrave;n tham quan v&agrave; thư gi&atilde;n tại:</p>\r\n            <ul>\r\n                <li><strong>Suối kho&aacute;ng n&oacute;ng I-Resort:</strong> với kh&ocirc;ng gian y&ecirc;n tĩnh, c&acirc;y cối xanh tươi, I-Resort sẽ hiện ra như một bức tranh thi&ecirc;n nhi&ecirc;n đậm chất Việt, Qu&yacute; kh&aacute;ch sẽ được trải nghiệm dịch vụ tắm kho&aacute;ng thư gi&atilde;n. Ngo&agrave;i ra, Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; thể tắm b&ugrave;n kho&aacute;ng hoặc massage cho l&agrave;n da th&ecirc;m tươi trẻ.</li>\r\n            </ul>\r\n            <p>Buổi chiều, đo&agrave;n tiếp tục tham quan c&aacute;c thắng cảnh tại th&agrave;nh phố biển:</p>\r\n            <ul>\r\n                <li><strong>Th&aacute;p B&agrave; Ponagar:</strong> c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu cho nghệ thuật kiến tr&uacute;c v&agrave; đi&ecirc;u khắc t&iacute;n ngưỡng t&ocirc;n gi&aacute;o bậc nhất của d&acirc;n tộc Chăm tại Nha Trang.</li>\r\n                <li><strong>H&ograve;n Chồng:</strong> một thắng cảnh tự nhi&ecirc;n nằm ở bờ biển ph&iacute;a Bắc th&agrave;nh phố Nha Trang. Nơi đ&acirc;y du kh&aacute;ch c&oacute; thể di chuyển v&agrave;i bước đ&atilde; chạm đến s&oacute;ng biển hoặc ch&acirc;n đồi. Nhiều người bảo, H&ograve;n Chồng l&agrave; nơi giao nhau giữa biển v&agrave; n&uacute;i. Quần thể đ&aacute; H&ograve;n Chồng từ l&acirc;u đ&atilde; trở th&agrave;nh điểm&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> gi&agrave;u t&iacute;nh nh&acirc;n văn. Điều kỳ th&uacute; l&agrave; những tảng đ&aacute; lớn nằm chồng chất l&ecirc;n nhau bao đời nay nhưng s&oacute;ng biển v&agrave; mưa gi&oacute; kh&ocirc;ng thể x&ocirc; ng&atilde;.</li>\r\n                <li><strong>Trung t&acirc;m trang sức Princess Jewelry (đường L&ecirc; Hồng Phong):</strong> tham quan, t&igrave;m hi&ecirc;u quy tr&igrave;nh sản xuất ngọc trai, c&aacute;ch ph&acirc;n loại. Tại đ&acirc;y, Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn cho m&igrave;nh những sản phẩm trang sức cao cấp nổi tiếng được gắn đ&aacute; qu&yacute;, ngọc trai cũng như những sản phẩm chất lượng cao được l&agrave;m từ ngọc trai nu&ocirc;i cấy tự nhi&ecirc;n.</li>\r\n                <li>Mua sắm đặc sản phố biển tại&nbsp;<strong>Hương Đ&agrave; Nha Trang</strong>.</li>\r\n            </ul>\r\n            <p>Nghỉ đ&ecirc;m tại Nha Trang.</p>\r\n            <p>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - NHA TRANG - TP. HỒ CH&Iacute; MINH (Ăn s&aacute;ng, trưa)</h3>\r\n    <div>\r\n        <div>\r\n            <p>Sau khi d&ugrave;ng bữa s&aacute;ng, xe đưa đo&agrave;n về lại th&agrave;nh phố Hồ Ch&iacute; Minh, tr&ecirc;n đường Qu&yacute; kh&aacute;ch gh&eacute; Ninh Thuận mua c&aacute;c sản phẩm đặc sản (nho, tỏi) của v&ugrave;ng đất nắng v&agrave; gi&oacute; nơi đ&acirc;y về l&agrave;m qu&agrave; cho người th&acirc;n. Đến điểm đ&oacute;n ban đầu, chia tay đo&agrave;n v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a>.</p>\r\n        </div>\r\n    </div>\r\n</div>', 115, '2023-09-10 00:00:00.000000', 'TP.Hà Nội', 'Xe du lịch', 8, 'Nha Trang - Làng Chài Xưa Mũi Né - Biển Nhũ Tiên - Vinwonders (Xe. Khách Sạn 4*. Tour Giá Sốc) - Kích cầu du lịch', '4 ngày 3 đêm', 'Quanh năm', 'Đã ưu đãi trực tiếp vào giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN617-009-090823XE-F', 'Buffet sáng, Theo thực đơn, Đặc sản địa phương', 'todo', 'Nha Trang, Vinwonder, Nhũ Tiên, Động Yến, Chùa Ốc Từ Vân', 'Người lớn tuổi, Cặp đôi, Gia đình nhiều thế hệ, Thanh niên', NULL, 1590000, NULL, NULL, 'NDSGN617-009-090823XE-F.jpg', 'NDSGN550-013-100823XE-F.jpg', 'DSSGN820-014-120823FWT.jpg', 'DSSGN819-013-130823XE-V-F.jpg', 'Khách sạn 4 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP.HCM &ndash; NHA TRANG &ndash; TRƯỜNG DỤC THANH &ndash; CH&Ugrave;A TỪ V&Acirc;N Số bữa ăn 3 bữa: S&aacute;ng, Trưa, Chiều</h3>\r\n    <div>\r\n        <div>Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> (190 Pasteur, Phường V&otilde; Thị S&aacute;u, Quận 3, TPHCM). Xe đưa đo&agrave;n khởi h&agrave;nh đến điểm ăn s&aacute;ng. Sau đ&oacute;, theo đường dẫn v&agrave;o cao tốc,<strong>&nbsp;kh&aacute;m ph&aacute; những cung đường cao tốc mới&nbsp;</strong>của khu vực ph&iacute;a Nam:&nbsp;<strong>TP.HCM &ndash; Long Th&agrave;nh - Dầu Gi&acirc;y &ndash; Phan Thiết &ndash; Vĩnh Hảo</strong>. Đo&agrave;n ăn s&aacute;ng tr&ecirc;n cung đường đi, ngắm vườn thanh long bạt ng&agrave;n. Đến Phan Thiết, tham quan:<br><br><strong>&bull; Di t&iacute;ch trường Dục Thanh:&nbsp;</strong>Kh&aacute;m ph&aacute; kiến tr&uacute;c ng&ocirc;i trường trăm tuổi, nằm cạnh d&ograve;ng s&ocirc;ng C&agrave; Ty hiền h&ograve;a với những m&aacute;i nh&agrave; r&ecirc;u phong cổ k&iacute;nh, nơi nu&ocirc;i dưỡng bao t&acirc;m hồn của những ch&iacute; sĩ y&ecirc;u nước, trong đ&oacute; c&oacute; thầy gi&aacute;o Nguyễn Tất Th&agrave;nh. Qu&yacute; kh&aacute;ch c&ugrave;ng kh&aacute;m ph&aacute; c&acirc;u chuyện lịch sử b&ecirc;n cạnh c&aacute;c di t&iacute;ch ph&ograve;ng học, nh&agrave; ngư, giếng nước hay c&acirc;y khế trăm tuổi do ch&iacute;nh tay cụ Nguyễn Th&ocirc;ng trồng,...</div>\r\n        <div><br>Ăn trưa tại th&agrave;nh phố biển Phan Thiết v&agrave; l&ecirc;n cao tốc tiếp tục h&agrave;nh tr&igrave;nh. Tr&ecirc;n đường đi, đo&agrave;n tham quan:<br><strong>&bull; C&aacute;nh đồng điện gi&oacute; Đầm Nại:</strong> gần nh&agrave; thờ G&ograve; Sạn, đến đ&acirc;y khi &aacute;nh ho&agrave;ng h&ocirc;n dần bu&ocirc;ng xuống du kh&aacute;ch sẽ tr&ocirc;ng thấy những c&aacute;nh quạt tuabin như những cối xay gi&oacute; khổng lồ tạo n&ecirc;n khung cảnh h&ugrave;ng vĩ, đẹp đến nao l&ograve;ng.<br><br><strong>&bull; Ch&ugrave;a Ốc Từ V&acirc;n:</strong> l&agrave;m từ h&agrave;ng triệu vi&ecirc;n đ&aacute; san h&ocirc; v&agrave; vỏ ốc ở Cam Ranh (Kh&aacute;nh H&ograve;a) được chuy&ecirc;n trang&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> Culture Trip ca ngợi l&agrave; &ldquo;kho b&aacute;u cho những du kh&aacute;ch th&iacute;ch phi&ecirc;u lưu&rdquo;. Sở hữu khung cảnh rất hấp dẫn v&agrave; ấn tượng, thiết kế độc lạ kh&ocirc;ng chỉ thu h&uacute;t Phật tử đến h&agrave;nh hương, m&agrave; c&ograve;n l&agrave; điểm tham quan hấp dẫn với kh&aacute;ch du lịch. Qu&yacute; kh&aacute;ch sẽ c&oacute; cảm gi&aacute;c thư gi&atilde;n, cảm nhận n&eacute;t thanh tịnh, y&ecirc;n b&igrave;nh từ những điều b&igrave;nh dị nhất như ch&aacute;nh điện rợp b&oacute;ng phi lao, th&aacute;p Bảo T&iacute;ch cao 39m nổi bật mang cấu tr&uacute;c cầu kỳ với 49 tiểu th&aacute;p b&ecirc;n ngo&agrave;i, ph&iacute;a b&ecirc;n trong c&oacute; h&igrave;nh xoắn ốc tựa hồ như bước v&agrave;o một vỏ s&ograve; khổng lồ hay con đường dẫn đến địa ngục d&agrave;i 500m độc đ&aacute;o,...</div>\r\n        <div><br>Tiếp tục h&agrave;nh tr&igrave;nh với đường cao tốc mới Cam L&acirc;m &ndash; Nha Trang. Đến nơi, đo&agrave;n d&ugrave;ng bữa ăn đặc sản với Nem nướng trứ danh của xứ Trầm xinh đẹp. Sau khi ăn tối, đo&agrave;n về nhận ph&ograve;ng nghỉ ngơi.&nbsp;</div>\r\n        <div><br>Buổi tối, Qu&yacute; kh&aacute;ch tự do dạo chợ đ&ecirc;m Nha Trang, sắm cho m&igrave;nh những m&oacute;n đồ thủ c&ocirc;ng mỹ nghệ hay tản bộ dọc c&ocirc;ng vi&ecirc;n phố biển về đ&ecirc;m.</div>\r\n        <div><br><strong>Nghỉ đ&ecirc;m tại Nha Trang.</strong><br>&nbsp;</div>\r\n        <div>&nbsp;</div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - BIỂN NHŨ TI&Ecirc;N &ndash; VINWONDERS NHA TRANG Số bữa ăn 2 bữa: S&aacute;ng, Trưa, tự t&uacute;c ăn chiều</h3>\r\n    <div>\r\n        <div>\r\n            <p>Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, Qu&yacute; kh&aacute;ch khởi h&agrave;nh tham quan:<br><strong>&bull; Biển Nhũ Ti&ecirc;n:</strong> đắm m&igrave;nh trong l&agrave;n nước biển trong xanh, m&aacute;t mẻ, được bao bọc bởi b&atilde;i c&aacute;t d&agrave;i trắng x&oacute;a v&agrave; những h&agrave;ng dừa lao xao trong gi&oacute;. Với vẻ đẹp hoang sơ, thơ mộng, biển Nhũ Ti&ecirc;n tựa như n&agrave;ng ti&ecirc;n đang ngủ v&ugrave;i giữa kh&ocirc;ng gian m&ecirc;nh m&ocirc;ng của s&oacute;ng, của gi&oacute;, của n&uacute;i v&agrave; của m&acirc;y.&nbsp;</p>\r\n            <p><br>D&ugrave;ng bữa trưa tại khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a>. Sau đ&oacute;, đo&agrave;n tự t&uacute;c lựa chọn một trong c&aacute;c chương tr&igrave;nh sau để tham quan:&nbsp;<br><strong>&bull; Thế Giới Giải Tr&iacute; Vinwonders:</strong> ngắm to&agrave;n cảnh Vịnh Nha Trang từ đỉnh v&ograve;ng xoay mặt trời lớn nhất Việt Nam, tham quan bộ sưu tập &ldquo;kỳ hoa, dị thảo&rdquo; tại đồi Vạn Hoa, khu vườn th&uacute; mở - vườn Qu&yacute; Vương, tham gia c&aacute;c tr&ograve; chơi tương t&aacute;c v&agrave; li&ecirc;n ho&agrave;n tại Quảng trường Thần Thoại hay V&ugrave;ng đất y&ecirc;u thương, xem phim 4D tại l&acirc;u đ&agrave;i Đại Dương, kh&aacute;m ph&aacute; khu tr&ograve; chơi cảm gi&aacute;c mạnh, khu tr&ograve; chơi trong nh&agrave; hay tắm biển, tắm tại 1 trong những hồ bơi lớn nhất Đ&ocirc;ng Nam &Aacute;&hellip; v&agrave; thưởng thức chương tr&igrave;nh biểu diễn show &aacute;nh s&aacute;ng nghệ thuật kỳ vĩ tại quảng trường thần thoại mỗi tối.</p>\r\n            <p><br><strong>&bull; Suối kho&aacute;ng n&oacute;ng I-Resort:&nbsp;</strong>với kh&ocirc;ng gian y&ecirc;n tĩnh, c&acirc;y cối xanh tươi, I-Resort sẽ hiện ra như một bức tranh thi&ecirc;n nhi&ecirc;n đậm chất Việt, Qu&yacute; kh&aacute;ch sẽ được trải nghiệm dịch vụ tắm kho&aacute;ng thư gi&atilde;n. Ngo&agrave;i ra, Qu&yacute; kh&aacute;ch c&ograve;n c&oacute; thể tắm b&ugrave;n kho&aacute;ng hoặc massage cho l&agrave;n da th&ecirc;m tươi trẻ.</p>\r\n            <p><br>Đo&agrave;n tự do d&ugrave;ng bữa tối tại nh&agrave; h&agrave;ng địa phương v&agrave; trở về kh&aacute;ch sạn nghỉ ngơi. Buổi tối, Qu&yacute; kh&aacute;ch c&oacute; thể tự do trải nghiệm phố biển về đ&ecirc;m.</p>\r\n            <p><br><strong>***Lưu &yacute;: Chi ph&iacute; v&eacute; v&agrave; đi lại tự t&uacute;c***</strong></p>\r\n            <p><strong>Nghỉ đ&ecirc;m tại Nha Trang.</strong><br>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - NHA TRANG &ndash; CH&Ugrave;A LONG SƠN &ndash; L&Agrave;NG YẾN MAI SINH - TP.HCM Số bữa ăn 2 bữa: S&aacute;ng, Trưa</h3>\r\n    <div>\r\n        <div>Sau khi d&ugrave;ng bữa s&aacute;ng, Qu&yacute; kh&aacute;ch trả ph&ograve;ng kh&aacute;ch sạn. Đo&agrave;n tham quan:<br><strong>&bull; Ch&ugrave;a Long Sơn: l</strong>&agrave; ng&ocirc;i ch&ugrave;a cổ k&iacute;nh c&oacute; quy m&ocirc; lớn nhất trong số hơn 20 ng&ocirc;i ch&ugrave;a ở Nha Trang. Ch&ugrave;a nằm ngay trong nội th&agrave;nh Nha Trang, dưới ch&acirc;n Đồi Trại Thủy. Đ&acirc;y cũng l&agrave; một trong những thắng cảnh nổi tiếng của Nha Trang.&nbsp;</div>\r\n        <div><br><strong>&bull; L&agrave;ng Yến Mai Sinh:&nbsp;</strong>hang động Yến nh&acirc;n tạo rộng v&agrave; quy m&ocirc;. Du kh&aacute;ch được dịp tận mắt ngắm m&ocirc; h&igrave;nh nu&ocirc;i chim yến trong nh&agrave; từ quy tr&igrave;nh dẫn dụ chim Yến cho đến quy tr&igrave;nh chế biến tổ yến v&agrave; thưởng thức tr&agrave; yến miễn ph&iacute;.</div>\r\n        <div><br><strong>&bull; Trung t&acirc;m trang sức Princess Jewelry&nbsp;</strong>(đường L&ecirc; Hồng Phong): tham quan, t&igrave;m hi&ecirc;u quy tr&igrave;nh sản xuất ngọc trai, c&aacute;ch ph&acirc;n loại. Tại đ&acirc;y, Qu&yacute; kh&aacute;ch c&oacute; thể lựa chọn cho m&igrave;nh những sản phẩm trang sức cao cấp nổi tiếng được gắn đ&aacute; qu&yacute;, ngọc trai cũng như những sản phẩm chất lượng cao được l&agrave;m từ ngọc trai nu&ocirc;i cấy tự nhi&ecirc;n.</div>\r\n        <div><br>Khởi h&agrave;nh về Tp. Hồ Ch&iacute; Minh. Tr&ecirc;n đường về dừng tham quan mua đặc sản Phan Rang như: nho, tỏi...<br><br>Đo&agrave;n d&ugrave;ng cơm trưa tr&ecirc;n đường về. Chiều về đến TP.Hồ Ch&iacute; Minh, xe đưa Qu&yacute; kh&aacute;ch về điểm đ&oacute;n ban đầu. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a>.</div>\r\n    </div>\r\n</div>', 99, '2023-10-09 00:00:00.000000', 'TP. Hồ Chí Minh', 'Xe du lịch', 5, 'Nha Trang - Chùa Ốc Từ Vân - Biển Nhũ Tiên - Vinwonder - Khám Phá Cao Tốc Mới - Kích Cầu Du Lịch', '3 ngày 2 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN841-024-180823XE-V-F', 'Buffet sáng, Theo thực đơn, Đặc sản địa phương, Buffet sáng, Theo thực đơn, Đặc sản địa phương', NULL, 'Miếu Bà Chúa Xứ, Chùa Tà Pạ, Lăng Thoại Ngọc Hầu, Tây An Cổ Tự, Rừng Tràm Trà Sư, Chợ Nổi Cái Răng, Làng Du Lịch Mỹ Khánh', 'Người lớn tuổi, Gia đình nhiều thế hệ, Thanh niên', NULL, 2990000, NULL, NULL, 'NDSGN841-024-180823XE-V-F.jpg', 'NDSGN1731-095-110823VU-V-7.jpg', 'NDSGN846-108-100823XE-D.jpg', 'DSSGN820-014-120823FWT.jpg', 'Khách sạn 3 sao\r\n\r\nKhách sạn 2 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP.HCM - TRI T&Ocirc;N - THẤT SƠN HUYỀN B&Iacute; - N&Uacute;I CẤM - CH&Ugrave;A T&Agrave; PẠ - CH&Acirc;U ĐỐC - MIẾU B&Agrave; CH&Uacute;A XỨ (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <p>Qu&yacute; kh&aacute;ch tập trung tại&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> (190 Pasteur, Quận 3, Tp.HCM), khởi h&agrave;nh đi An Giang theo&nbsp;<strong>tuyến cao tốc Trung Lương,</strong> chinh phục<strong>&nbsp;cầu V&agrave;m Cống</strong>: cầu d&acirc;y văng lớn thứ hai nối liền đ&ocirc;i bờ S&ocirc;ng Hậu, được kh&aacute;nh th&agrave;nh trong sự h&aacute;o hức của h&agrave;ng vạn người d&acirc;n sau bao năm chờ mong.<br><br>Sau khi ăn trưa, Qu&yacute; kh&aacute;ch di chuyển theo&nbsp;<strong>cung đường Tri T&ocirc;n,</strong> <strong>thưởng ngoạn những c&aacute;nh đồng trải d&agrave;i bất tận&nbsp;</strong>dọc đường đi đang v&agrave;o m&ugrave;a xanh mơn mởn thẳng c&aacute;nh c&ograve; bay xen lẫn h&agrave;ng thốt nốt đặc trưng v&ugrave;ng bi&ecirc;n giới An Giang (t&ugrave;y theo m&ugrave;a v&agrave; điều kiện thời tiết)</p>\r\n            <p>Qu&yacute; kh&aacute;ch chinh phục&nbsp;<strong>ngọn n&uacute;i cao nhất&nbsp;</strong>trong d&atilde;y&nbsp;<strong>Thất Sơn huyền b&iacute;&nbsp;</strong>v&agrave; cũng l&agrave;&nbsp;<strong>ngọn n&uacute;i thi&ecirc;ng nhất</strong> trong v&ugrave;ng Bảy N&uacute;i - An Giang<strong>&nbsp;bằng c&aacute;p treo d&agrave;i 3,5 km</strong> vượt qua những ngọn c&acirc;y, mặt hồ phẳng lặng như t&igrave;m về chốn an y&ecirc;n cho t&acirc;m hồn (chi ph&iacute; c&aacute;p treo tự t&uacute;c)<br><br>- Tham quan&nbsp;<strong>L&acirc;m Vi&ecirc;n N&uacute;i Cấm</strong> (hay Thi&ecirc;n Cấm Sơn - n&uacute;i &Ocirc;ng Cấm): được v&iacute; như &ldquo;N&oacute;c nh&agrave; của khu vực Đồng bằng s&ocirc;ng Cửu Long&rdquo; nằm ở độ cao tr&ecirc;n 705 m&eacute;t quanh năm m&aacute;t mẻ, phong cảnh sơn thủy hữu t&igrave;nh với c&aacute;c cảnh đẹp ngoạn mục như:&nbsp;<strong>Hồ Thủy Li&ecirc;m; Tượng phật Di Lặc lớn nhất tr&ecirc;n đỉnh n&uacute;i ở ch&acirc;u &Aacute; cao 33,60 m&eacute;t; Ch&ugrave;a Vạn Linh&nbsp;</strong>uy nghi với bảo th&aacute;p 9 tầng cao 35 m&eacute;t, nơi linh thi&ecirc;ng để Qu&yacute; kh&aacute;ch cầu mong những điều an nhi&ecirc;n v&agrave; b&igrave;nh an trong cuộc sống.</p>\r\n            <p>Sau khi ăn tối, Qu&yacute; kh&aacute;ch kh&aacute;m ph&aacute; Ch&acirc;u Đốc với c&aacute;c điểm t&acirc;m linh quan trọng dưới ch&acirc;n n&uacute;i Sam.<br><strong>-&nbsp;Miếu B&agrave; Ch&uacute;a Xứ n&uacute;i Sam:&nbsp;</strong>nơi linh thi&ecirc;ng để du kh&aacute;ch gửi gắm những lời nguyện cầu đến B&agrave;.<br><strong>- Lăng &ocirc;ng Thoại Ngọc Hầu:&nbsp;</strong>với c&ocirc;ng đức vời vợi trong c&ocirc;ng cuộc x&acirc;y dựng con k&ecirc;nh Vĩnh Tế vĩ đại.&nbsp;<br><strong>- T&acirc;y An Cổ Tự:&nbsp;</strong>kết hợp mẫu mực của phong c&aacute;ch nghệ thuật Ấn Độ v&agrave; kiến tr&uacute;c cổ d&acirc;n tộc đầu ti&ecirc;n tại Việt Nam<br><br><strong>Nghỉ đ&ecirc;m tại TP.Ch&acirc;u Đốc.</strong></p>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - CH&Acirc;U ĐỐC - RỪNG TR&Agrave;M TR&Agrave; SƯ - CẦN THƠ (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>Buổi s&aacute;ng, Qu&yacute; kh&aacute;ch trả ph&ograve;ng v&agrave; khởi h&agrave;nh tham quan:<br>- Tự do mua sắm đặc sản ngon nổi tiếng tại&nbsp;<strong>chợ Ch&acirc;u Đốc&nbsp;</strong>- nơi được mệnh danh &ldquo;Vương quốc mắm&rdquo; của miền T&acirc;y.<br><strong>- Rừng tr&agrave;m Tr&agrave; Sư:</strong> c&oacute; tổng diện t&iacute;ch gần 1.500 h&eacute;c-ta c&ugrave;ng hệ sinh th&aacute;i động-thực vật đa dạng. Du kh&aacute;ch di chuyển bằng xuồng m&aacute;y hay những chiếc ghe để len lỏi giữa c&aacute;c lối đi nhỏ xuy&ecirc;n qua khu rừng tr&agrave;m ngắm nh&igrave;n những mảng b&egrave;o xanh ng&aacute;t trải d&agrave;i (v&agrave;o m&ugrave;a nước nổi). Ngo&agrave;i ra, du kh&aacute;ch c&oacute; thể check-in<strong>&nbsp;bến t&agrave;u được thiết kế theo m&ocirc; h&igrave;nh tổ chim tr&ecirc;n cao ngộ nghĩnh độc đ&aacute;o; &ldquo;C&acirc;y Cầu Tre Vạn Bước&rdquo; d&agrave;i nhất Việt Nam</strong> - được c&aacute;ch điệu tựa &ldquo;Rồng tr&uacute;c bạch&rdquo; mang lại một cảm gi&aacute;c th&iacute;ch th&uacute; cho mọi người khi được kh&aacute;m ph&aacute; trọn vẹn cảnh quan thi&ecirc;n nhi&ecirc;n nguy&ecirc;n sinh tr&ugrave; ph&uacute; ở &ldquo;Bảo t&agrave;ng Tr&agrave;m nhiệt đới.<br><br>Sau khi ăn trưa, đo&agrave;n khởi h&agrave;nh đến Cần Thơ nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi. Buổi tối, Qu&yacute; kh&aacute;ch t<strong>ự do dạo phố đ&ecirc;m kh&aacute;m ph&aacute; &ldquo;T&acirc;y Đ&ocirc;&rdquo;</strong> lung linh sắc m&agrave;u: ngắm cảnh<strong>&nbsp;cầu Cần Thơ&nbsp;</strong>về đ&ecirc;m; check-in<strong>&nbsp;cầu T&igrave;nh Y&ecirc;u&nbsp;</strong>- thiết kế uốn lượn h&igrave;nh chữ S c&ugrave;ng hai đ&agrave;i sen v&agrave; hệ thống đ&egrave;n led rực rỡ sắc m&agrave;u; chụp ảnh tại&nbsp;<strong>ch&ugrave;a &Ocirc;ng</strong> cổ k&iacute;nh; kh&aacute;m ph&aacute;<strong>&nbsp;chợ Đ&ecirc;m Ninh Kiều&nbsp;</strong>v&agrave;&nbsp;<strong>con đường b&iacute;ch họa Cần Thơ Xưa V&agrave; Nay</strong> - t&aacute;i hiện n&eacute;t đẹp văn h&oacute;a của người d&acirc;n Cần Thơ n&oacute;i ri&ecirc;ng, v&ugrave;ng Đồng bằng s&ocirc;ng Cửu Long n&oacute;i chung.<br><br><strong>Nghỉ đ&ecirc;m tại Cần Thơ.</strong><br>&nbsp;</div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - CẦN THƠ - CHỢ NỔI C&Aacute;I RĂNG - L&Agrave;NG DU LỊCH MỸ KH&Aacute;NH - TP.HCM (Ăn s&aacute;ng, trưa)</h3>\r\n    <div>\r\n        <div>\r\n            <p>Xe đưa Qu&yacute; kh&aacute;ch đến<strong>&nbsp;bến Ninh Kiều</strong>, xuống thuyền du ngoạn tr&ecirc;n<strong>&nbsp;s&ocirc;ng Cần Thơ,</strong> ngắm&nbsp;<strong>cầu Quang Trung</strong>, đến&nbsp;<strong>chợ nổi C&aacute;i Răng -</strong> một h&igrave;nh thức họp chợ đặc trưng của v&ugrave;ng Đồng Bằng S&ocirc;ng Cửu Long, Qu&yacute; kh&aacute;ch tự do thưởng thức v&agrave; chọn mua đặc sản Miền T&acirc;y (chi ph&iacute; tự t&uacute;c).<br>Qu&yacute; kh&aacute;ch tham quan:<br><strong>-&nbsp;<a href="https://travel.com.vn/can-tho/tour-vuon-my-khanh.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">L&agrave;ng Du Lịch Mỹ Kh&aacute;nh</a></strong>:<strong>&nbsp;</strong>điểm&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> sinh th&aacute;i hấp dẫn với c&aacute;c tr&ograve; chơi d&acirc;n gian vui nhộn như c&acirc;u c&aacute;, ch&egrave;o thuyền tr&ecirc;n s&ocirc;ng, đua heo&hellip; v&agrave; tham quan nh&agrave; cổ.<br><strong>-&nbsp;<a href="https://travel.com.vn/can-tho/tour-thien-vien-truc-lam-phuong-nam.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Thiền Viện Tr&uacute;c L&acirc;m Phương Nam</a>:</strong> một trong những ng&ocirc;i thiền viện rộng nhất khu vực Đồng bằng s&ocirc;ng Cửu Long, tham quan lầu trống, lầu chu&ocirc;ng, lễ Phật tại ch&aacute;nh điện<br><br>Sau khi dừng ch&acirc;n tham quan v&agrave;<strong>&nbsp;mua sắm tại si&ecirc;u thị đặc sản v&agrave; qu&agrave; lưu niệm Hương Đảo</strong>, đo&agrave;n khởi h&agrave;nh về Tp.HCM, đến nơi đưa đo&agrave;n về điểm đ&oacute;n ban đầu, kết th&uacute;c chương tr&igrave;nh du lịch.</p>\r\n            <p><strong><em>* Điểm tham quan c&oacute; thể sắp xếp lại cho ph&ugrave; hợp m&agrave; vẫn bảo đảm đầy đủ nội dung của từng chương trinh.</em></strong><br><strong>Lưu &yacute;: Chương tr&igrave;nh của&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> l&agrave; chương tr&igrave;nh trọn g&oacute;i, kh&ocirc;ng được &aacute;p dụng ch&iacute;nh s&aacute;ch miễn giảm, ưu ti&ecirc;n v&agrave;o điểm tham quan (nếu c&oacute;) d&agrave;nh cho trẻ em, người cao tuổi, người c&oacute; c&ocirc;ng với c&aacute;ch mạng &nbsp;v&igrave; vậy Vietravel sẽ kh&ocirc;ng ho&agrave;n lại phần ch&ecirc;nh lệch cho c&aacute;c kh&aacute;ch h&agrave;ng thuộc diện ưu ti&ecirc;n n&agrave;y.</strong><br>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n</div>', 123, '2023-11-18 00:00:00.000000', 'TP.Hà Nội', 'Máy bay, Xe du lịch', 7, 'Miền Tây: Châu Đốc - Núi Cấm - Rừng Tràm Trà Sư - Cần Thơ - Chợ Nổi Cái Răng (Khách sạn 2*& 3*)', '3 ngày 2 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN846-108-100823XE-D', 'Buffet sáng, Theo thực đơn', NULL, 'Sai Gon, Can Tho, Ca Mau, Bac Lieu, Soc Trang', 'Người lớn tuổi, Cặp đôi, Gia đình nhiều thế hệ, Thanh niên', NULL, 4590000, NULL, NULL, 'NDSGN846-108-100823XE-D.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'NDSGN303-031-100823VN-F-7.jpg', 'NDSGN550-013-100823XE-F.jpg', 'Khách sạn 4 sao\r\n\r\nKhách sạn 5 sao\r\n\r\nKhách sạn 3 sao', '<div>\r\n    <h3>Ng&agrave;y 1 - TP. HCM - CẦN THƠ - CỒN SƠN (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Xe v&agrave; hướng dẫn vi&ecirc;n&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> đ&oacute;n Qu&yacute; kh&aacute;ch tại 190 Pasteur, Quận 3, Tp.HCM khởi h&agrave;nh đi miền T&acirc;y theo tuyến&nbsp;<strong>cao tốc Trung Lương</strong> ngắm nh&igrave;n những c&aacute;nh đồng l&uacute;a v&agrave; m&agrave;u xanh vườn tược hai b&ecirc;n đường.&nbsp;<br><br>Đến&nbsp;<strong>bến đ&ograve; C&ocirc; Bắc (Cần Thơ),</strong> Qu&yacute; kh&aacute;ch đi đ&ograve; qua s&ocirc;ng tham quan&nbsp;<strong>cụm&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> cộng đồng Cồn Sơn</strong> - Ng&ocirc;i l&agrave;ng du lịch cộng đồng độc đ&aacute;o giữa l&ograve;ng phố thị c&ugrave;ng những trải nghiệm đặc biệt:<br><br>-&nbsp;<strong>B&egrave; C&aacute; 7 Bon</strong>: tham quan m&ocirc; h&igrave;nh nu&ocirc;i c&aacute; lồng b&egrave;,&nbsp;<strong>&ldquo;Thư viện c&aacute; nước ngọt</strong><strong>&rdquo;</strong><strong>&nbsp;</strong>v&agrave; nơi bảo tồn những giống c&aacute; qu&yacute; hiếm tr&ecirc;n s&ocirc;ng Hậu hiền h&ograve;a.&nbsp;<strong>Thưởng thức sản phẩm l&agrave;m từ c&aacute; th&aacute;t l&aacute;t cườm,</strong> tận mắt&nbsp;<strong>chi&ecirc;m ngưỡng &ldquo;c&aacute; cung thủ&rdquo;&nbsp;</strong>v&agrave;&nbsp;<strong>trải nghiệm massage ch&acirc;n bằng c&aacute; c&oacute; vảy</strong>.<br><br>- Qu&yacute; kh&aacute;ch c&ugrave;ng nhau thưởng thức bữa cơm gia đ&igrave;nh - mỗi nh&agrave; một m&oacute;n ngon với&nbsp;<strong>&quot;thực đơn bay&quot;</strong> đặc trưng chỉ c&oacute; ở Cồn Sơn.<br><br>- Như được trở về với tuổi thơ từ trải nghiệm&nbsp;<strong>l&agrave;m b&aacute;nh d&acirc;n gian Nam bộ truyền thống</strong> (b&aacute;nh l&aacute; m&iacute;t, b&aacute;nh kẹp cuốn) qua sự hiện dẫn tận t&igrave;nh của c&aacute;c nghệ nh&acirc;n chất ph&aacute;c. Thưởng thức những chiếc b&aacute;nh tự tay l&agrave;m ra thơm m&ugrave;i gạo, m&ugrave;i l&aacute;, h&ograve;a quyện nước cốt dừa b&eacute;o ngậy.<br><br>- Đo&agrave;n dừng ch&acirc;n tham quan&nbsp;<strong>vườn tr&aacute;i c&acirc;y</strong> đặc sắc theo m&ugrave;a: ch&ocirc;m ch&ocirc;m, nh&atilde;n, v&uacute; sữa, ổi, bưởi&hellip; (mỗi m&ugrave;a mỗi tr&aacute;i).<br><br>- Thưởng thức ổi sạch trồng tại vườn v&agrave; tr&agrave; hoa đậu biếc; xem c&aacute;c &ldquo;diễn vi&ecirc;n c&aacute;&rdquo; biểu diễn&nbsp;<strong>&quot;C&aacute; L&oacute;c Bay&quot;</strong> v&agrave;&nbsp;<strong>&quot;C&aacute; Tr&ecirc; G&agrave;&quot;&nbsp;</strong>kh&ocirc;ng nơi n&agrave;o c&oacute; được.<br><br>- Mục sở thị tiết mục&nbsp;<strong>&ldquo;Xiếc Ếch&rdquo;</strong> th&uacute; vị; Thưởng thức&nbsp;<strong>b&aacute;nh phu th&ecirc; nh&acirc;n mặn&nbsp;</strong>(giải nhất b&aacute;nh ngon Nam Bộ 2015) v&agrave;&nbsp;<strong>s&acirc;m s&acirc;m giải nhiệt&nbsp;</strong>được trồng quanh vườn h&ograve;a quyện c&ugrave;ng nước cốt gừng thơm ngon.<br><br>- Check-in c&ugrave;ng l&aacute; sen<strong>&nbsp;&ldquo;si&ecirc;u to khổng lồ&rdquo;</strong>. Tham quan, t&igrave;m hiểu về&nbsp;<strong>&quot;Văn h&oacute;a Ghe xuồng Nam Bộ&quot;</strong> v&agrave; mua sắm c&aacute;c sản phẩm OCOP.<br><br>Quay trở về TP. Cần Thơ, đo&agrave;n tiếp tục tham quan:&nbsp;<br><br>-&nbsp;<strong>Đền Thờ Vua H&ugrave;ng</strong> - c&ocirc;ng tr&igrave;nh kiến tr&uacute;c được x&acirc;y dựng theo h&igrave;nh tượng trống đồng c&aacute;ch điệu, l&agrave; h&igrave;nh ảnh trống đồng Ngọc Lũ, 18 c&aacute;nh cung xung quanh tượng trưng cho 18 đời vua H&ugrave;ng, bao quanh đền thờ ch&iacute;nh c&ograve;n c&oacute; 54 khối cột h&igrave;nh trụ, tượng trưng cho 54 d&acirc;n tộc anh em. Gian ch&iacute;nh của đền thờ l&agrave; nơi để thờ Quốc tổ H&ugrave;ng Vương, Tổ phụ Lạc Long Qu&acirc;n, Tổ mẫu &Acirc;u Cơ, Lạc Hầu, Lạc Tướng.&nbsp;<em>(Lưu &yacute;: kh&aacute;ch v&agrave;o tham quan hoặc d&acirc;ng hương tại Đền thờ trang phục phải k&iacute;n đ&aacute;o, lịch sự, ph&ugrave; hợp với nơi thờ tự trang nghi&ecirc;m, THỨ HAI h&agrave;ng tuần đền thờ đ&oacute;ng cửa để bảo tr&igrave;.)</em><br><br>Sau khi d&ugrave;ng bữa tối tại TP. Cần Thơ, Qu&yacute; kh&aacute;ch tự do&nbsp;<strong>dạo phố đ&ecirc;m kh&aacute;m ph&aacute; &ldquo;T&acirc;y Đ&ocirc;&rdquo;&nbsp;</strong>lung linh sắc m&agrave;u: ngắm cảnh&nbsp;<strong>Cầu Cần Thơ&nbsp;</strong>về đ&ecirc;m; Check-in&nbsp;<strong>Cầu T&igrave;nh Y&ecirc;u</strong> -&nbsp;<em>thiết kế uốn lượn h&igrave;nh chữ S c&ugrave;ng hai đ&agrave;i sen v&agrave; hệ thống đ&egrave;n led rực rỡ sắc m&agrave;u;</em> Chụp ảnh tại&nbsp;<strong>Ch&ugrave;a &Ocirc;ng</strong> cổ k&iacute;nh; Kh&aacute;m ph&aacute;&nbsp;<strong>Chợ Đ&ecirc;m Ninh Kiều</strong> v&agrave;&nbsp;<strong>Con Đường B&iacute;ch Họa Cần Thơ Xưa V&agrave; Nay</strong> -&nbsp;<em>t&aacute;i hiện n&eacute;t đẹp văn h&oacute;a của người d&acirc;n Cần Thơ n&oacute;i ri&ecirc;ng, v&ugrave;ng Đồng bằng s&ocirc;ng Cửu Long n&oacute;i chung.</em></div>\r\n            <div><br>Nghỉ đ&ecirc;m tại Cần Thơ.</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - CẦN THƠ - CHỢ NỔI C&Aacute;I RĂNG - BẠC LI&Ecirc;U - C&Agrave; MAU (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Đo&agrave;n trả ph&ograve;ng v&agrave; khởi h&agrave;nh ra&nbsp;<strong>Bến Ninh Kiều</strong>, xuống thuyền du ngoạn tr&ecirc;n<strong>&nbsp;s&ocirc;ng Cần Thơ,</strong> nghe giới thiệu về&nbsp;<strong>Chợ thủy sản nước ngọt Cần Thơ</strong>; trải nghiệm&nbsp;<strong>Chợ nổi C&aacute;i Răng</strong> - một h&igrave;nh thức họp chợ đặc trưng tại c&aacute;c ng&atilde; s&ocirc;ng của v&ugrave;ng đồng bằng s&ocirc;ng Cửu Long, chợ nổi quy tụ h&agrave;ng trăm ghe xuồng, tạo th&agrave;nh nơi mua b&aacute;n, giao thương c&aacute;c loại tr&aacute;i c&acirc;y, đặc sản sầm uất tr&ecirc;n s&ocirc;ng nước. Chợ Nổi C&aacute;i Răng đ&atilde; được c&ocirc;ng nhận Di Sản Văn H&oacute;a Phi Vật Thể Quốc Gia.<br><br>Sau khi dừng ch&acirc;n tham quan v&agrave; mua sắm tại<strong>&nbsp;si&ecirc;u thị đặc sản v&agrave; qu&agrave; lưu niệm Hương Đảo</strong>, đo&agrave;n khởi h&agrave;nh đi Bạc Li&ecirc;u tham quan:<br>-&nbsp;<strong>Nh&agrave; c&ocirc;ng tử Bạc Li&ecirc;u</strong>: do kỹ sư người Ph&aacute;p thiết kế v&agrave;o năm 1919, đ&acirc;y được xem l&agrave; ng&ocirc;i biệt thự bề thế nhất ở Nam kỳ lục tỉnh l&uacute;c bấy giờ. Đo&agrave;n nghe kể về giai thoại v&agrave;ng son một thời của cậu Ba Huy - Người được mệnh danh l&agrave; &ldquo;C&ocirc;ng tử Bạc Li&ecirc;u&rdquo;.<br><br>Sau khi ăn trưa, đo&agrave;n khởi h&agrave;nh đi C&agrave; Mau. Tr&ecirc;n đường đi, Đo&agrave;n dừng ch&acirc;n tham quan&nbsp;<strong>Nh&agrave; thờ Tắc Sậy</strong> - Nơi an nghỉ của Linh mục Trương Bửu Diệp được nhiều người biết đến với l&ograve;ng s&ugrave;ng mộ, nơi đ&acirc;y gắn liền với những c&acirc;u chuyện cảm động về cuộc đời của cha Trương Bửu Diệp.&nbsp;<br><br>Đến C&agrave; Mau, đo&agrave;n nhận ph&ograve;ng nghỉ ngơi.&nbsp;Nghỉ đ&ecirc;m ở C&agrave; Mau.</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - C&Agrave; MAU - ĐẤT MŨI - BẠC LI&Ecirc;U - C&Aacute;NH ĐỒNG ĐIỆN GI&Oacute; (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>Đo&agrave;n trả ph&ograve;ng v&agrave; l&ecirc;n xe khởi h&agrave;nh đến&nbsp;<strong>Đất Mũi.&nbsp;</strong>Đến nơi,&nbsp;<strong>đo&agrave;n di chuyển bằng cano len lỏi trong rừng đước, kh&aacute;m ph&aacute; khu rừng ngập mặn lớn thứ hai thế giới</strong> với hệ sinh th&aacute;i đa dạng, mang vẻ đẹp hoang dại c&ugrave;ng bầu kh&ocirc;ng kh&iacute; trong l&agrave;nh.<br>Tại Đất Mũi, đo&agrave;n tham quan&nbsp;<strong>chinh phục&nbsp;<a href="https://travel.com.vn/ca-mau/tour-ca-mau.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Cột cờ Đất Mũi</a>&nbsp;</strong>- M&ocirc; phỏng theo kiến tr&uacute;c Cột cờ H&agrave; Nội, khẳng định chủ quyền l&atilde;nh thổ d&acirc;n tộc, gắn b&oacute; Bắc Nam một nh&agrave;. Từ tr&ecirc;n kỳ đ&agrave;i cao hơn 41 m, du kh&aacute;ch c&oacute; thể thưởng ngoạn b&atilde;i bồi đất mũi, vẻ đẹp h&ugrave;ng vĩ của rừng đước bạt ng&agrave;n v&agrave; biển đ&ocirc;ng m&ecirc;nh m&ocirc;ng.<br><br>Qu&yacute; kh&aacute;ch mua sắm v&agrave; thưởng thức bữa trưa tại&nbsp;<strong>khu&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> cộng đồng Ho&agrave;ng H&ocirc;n</strong> với những m&oacute;n ăn đặc sản địa phương: c&aacute; th&ograve;i l&ograve;i, vọp&hellip;.&nbsp;<br><br>Đo&agrave;n khởi h&agrave;nh đi Bạc Li&ecirc;u, tham quan:<br><strong>-&nbsp;<a href="https://travel.com.vn/bac-lieu/tour-chua-xiem-can.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Ch&ugrave;a Xi&ecirc;m C&aacute;n</a>:&nbsp;</strong>với sắc v&agrave;ng lộng lẫy c&ugrave;ng thiết kế nhiều hoa văn tỉ mỉ mang đậm dấu ấn Khmer Nam Bộ.&nbsp;<br><strong>-&nbsp;<a href="https://travel.com.vn/bac-lieu/tour-canh-dong-dien-gio.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">C&aacute;nh Đồng Điện Gi&oacute;</a>:</strong> chi&ecirc;m ngưỡng vẻ đẹp những tua-bin gi&oacute; khổng lồ cao hơn 80 m sừng sững tr&ecirc;n nền trời xanh với &yacute; nghĩa tạo một nguồn năng lượng sạch v&ocirc; tận tr&ecirc;n mặt biển, giảm thiểu t&aacute;c động g&acirc;y hiệu ứng nh&agrave; k&iacute;nh&hellip;&nbsp;<br><br>Nghỉ đ&ecirc;m ở Bạc Li&ecirc;u.</div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 4 - BẠC LI&Ecirc;U - S&Oacute;C TRĂNG - TP.HCM (Ăn s&aacute;ng, trưa)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <p>Sau bữa s&aacute;ng, Qu&yacute; kh&aacute;ch tham quan:<br>-&nbsp;<strong>Quảng Trường H&ugrave;ng Vương:&nbsp;</strong>h&igrave;nh ảnh c&acirc;y đ&agrave;n k&igrave;m c&aacute;ch điệu (cao 18,6 m) nhạc cụ kh&ocirc;ng thể thiếu trong đờn ca t&agrave;i tử cải lương, biểu tượng văn h&oacute;a của tỉnh Bạc Li&ecirc;u.&nbsp;<br>-&nbsp;<strong>Khu lưu niệm nghệ thuật Đờn Ca T&agrave;i Tử Nam Bộ v&agrave; cố nhạc sĩ Cao Văn Lầu:&nbsp;</strong>một c&ocirc;ng tr&igrave;nh kh&aacute;i qu&aacute;t về th&acirc;n thế, sự nghiệp của cố nhạc sĩ Cao Văn lầu, ghi nhận c&ocirc;ng lao v&agrave; t&ocirc;n vinh những đ&oacute;ng g&oacute;p to lớn của &ocirc;ng cho nghệ thuật Đờn ca t&agrave;i tử Nam bộ v&agrave; l&agrave; người sản sinh ra bản Dạ cổ Ho&agrave;i lang bất hủ.<br><br>Đo&agrave;n khởi h&agrave;nh đi&nbsp;<strong>S&oacute;c Trăng</strong>, tham quan hai ng&ocirc;i ch&ugrave;a nổi tiếng:&nbsp;<br><strong>- Ch&ugrave;a S&agrave; L&ocirc;n:</strong> c&ograve;n được gọi l&agrave; &quot;ch&ugrave;a Ch&eacute;n Kiểu&quot; thể hiện được n&eacute;t văn h&oacute;a độc đ&aacute;o của người Khmer c&ugrave;ng c&aacute;ch thiết kế s&aacute;ng tạo từ h&agrave;ng vạn mảnh ch&eacute;n được ốp tr&ecirc;n c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c của ch&ugrave;a.<br><strong>-&nbsp;<a href="https://travel.com.vn/soc-trang/tour-wat-patum-wongsa-som-rong.aspx?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Ch&ugrave;a Wat Pătum W&ocirc;ngsa Som R&ocirc;ng</a></strong> - Một quần thể kiến tr&uacute;c Phật gi&aacute;o rộng lớn, gồm nhiều c&ocirc;ng tr&igrave;nh như: Ch&aacute;nh điện, ng&ocirc;i bảo th&aacute;p &ldquo;vạn người m&ecirc;&rdquo; đẹp tựa ch&ugrave;a Arun tại Th&aacute;i Lan,&hellip; Đặc biệt, tượng Phật Th&iacute;ch Ca nhập niết b&agrave;n uy nghi&ecirc;m với k&iacute;ch thước chiều d&agrave;i 63 m&eacute;t&hellip;<br><br>Đo&agrave;n quay về TP. HCM, kết th&uacute;c chương tr&igrave;nh tại điểm đ&oacute;n ban đầu.<br><br><em><strong>Lưu &yacute;: Chương tr&igrave;nh của&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> l&agrave; chương tr&igrave;nh trọn g&oacute;i, kh&ocirc;ng được &aacute;p dụng ch&iacute;nh s&aacute;ch miễn giảm, ưu ti&ecirc;n v&agrave;o điểm tham quan (nếu c&oacute;) d&agrave;nh cho trẻ em, người cao tuổi, người c&oacute; c&ocirc;ng với c&aacute;ch mạng v&igrave; vậy Vietravel sẽ kh&ocirc;ng ho&agrave;n lại phần ch&ecirc;nh lệch cho c&aacute;c kh&aacute;ch h&agrave;ng thuộc diện ưu ti&ecirc;n n&agrave;y.<br>Chương tr&igrave;nh đi Đất Mũi bằng xe, nếu Qu&yacute; kh&aacute;ch c&oacute; nhu cầu đi từ Năm Căn ra Đất Mũi bằng cano, vui l&ograve;ng b&aacute;o cho nh&acirc;n vi&ecirc;n tư vấn Vietravel ngay khi đăng k&yacute; v&agrave; thanh to&aacute;n tiền ph&iacute; cano c&ugrave;ng ph&iacute; thu&ecirc; th&ecirc;m một hướng dẫn vi&ecirc;n đi theo.</strong></em></p>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>', 129, '2023-11-10 00:00:00.000000', 'TP.Đà Nẵng', 'Xe du lịch', 3, 'Miền Tây: Cần Thơ - Cà Mau - Đất Mũi - Bạc Liêu - Sóc Trăng - tham quan vườn trái cây', '4 ngày 3 đêm', 'Quanh năm', 'Ưu đãi trực tiếp vào giá tour');
INSERT INTO `Tour` (`maTour`, `amThuc`, `diemNhan`, `diemThamQuan`, `doiTuongThichHop`, `giaEmBe`, `giaTour`, `giaTreEm`, `giaTreNho`, `image`, `image2`, `image3`, `image4`, `khachSan`, `lichTrinh`, `luotQuanTam`, `ngayKhoiHanh`, `noiKhoiHanh`, `phuongTienDiChuyen`, `soCho`, `tenTour`, `thoiGian`, `thoiGianLyTuong`, `uuDai`) VALUES ('NDSGN876-267-100823VJ-F', 'Theo thực đơn, Buffet sáng, Theo thực đơn, Đặc sản địa phương', NULL, 'Phu Quoc, Grand World, VinWonders, vuon tieu, nuoc mam, ngoc trai, Vin Safari, Hon Thom', 'Gia đình nhiều thế hệ, Trẻ em, Cặp đôi, Thanh niên', NULL, 6890000, NULL, NULL, 'NDSGN876-267-100823VJ-F.jpg', 'NDSGN3398-115-100823VU-D.jpg', 'DSSGN820-014-120823FWT.jpg', 'NDSGN550-013-100823XE-F.jpg', NULL, '<div>\r\n    <h3>Ng&agrave;y 1 - TP HỒ CH&Iacute; MINH - PH&Uacute; QUỐC - H&Ograve;N THƠM - THỊ TRẤN HO&Agrave;NG H&Ocirc;N - GRANDWORLD (Ăn trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <div>\r\n                <div>\r\n                    <p>Qu&yacute; kh&aacute;ch tập trung tại S&acirc;n bay T&acirc;n Sơn Nhất, ga đi Trong Nước, hướng dẫn vi&ecirc;n hỗ trợ l&agrave;m thủ tục đ&aacute;p chuyến bay đi Ph&uacute; Quốc.&nbsp;<br><br>Xe đ&oacute;n đo&agrave;n tại s&acirc;n bay đưa đến&nbsp;<strong>nh&agrave; ga c&aacute;p treo An Thới&nbsp;</strong>tham quan&nbsp;<strong>Sun World Hon Thom Nature Park</strong> với những hoạt động th&uacute; vị:<br>- Trải nghiệm&nbsp;<strong>c&aacute;p treo 3 d&acirc;y vượt biển d&agrave;i nhất thế giới - 7.899.9 m&eacute;t.</strong><br>- Phấn kh&iacute;ch tột độ c&ugrave;ng thử th&aacute;ch<strong>&nbsp;&quot;Mộc X&agrave; thịnh nộ&quot; -</strong> tr&ograve; chơi t&agrave;u lượn bằng gỗ đầu ti&ecirc;n tại Việt Nam, c&oacute; tốc độ tối đa 80km/h với nhiều g&oacute;c lượn ngoạn mục;<strong>&nbsp;đ&agrave;i quan s&aacute;t &quot;Mắt đại b&agrave;ng&quot; cao 120m</strong> - du kh&aacute;ch như được bay l&ecirc;n kh&ocirc;ng trung, thưởng ngoạn đại dương xanh ngắt bao la, những c&aacute;nh rừng nhiệt đới xanh mướt, những đảo nhỏ nhấp nh&ocirc; giữa biển trời kho&aacute;ng đạt (tr&ograve; chơi c&oacute; thể tạm dừng v&igrave; lịch bảo tr&igrave;)<br>- Thỏa th&iacute;ch tắm biển<strong>&nbsp;H&ograve;n Thơm:&nbsp;</strong>một trong những b&atilde;i biển &ldquo;quyến rũ nhất h&agrave;nh tinh&rdquo;, với b&atilde;i c&aacute;t trắng mịn, hoang sơ, h&agrave;ng dừa nghi&ecirc;ng soi b&oacute;ng, m&agrave;u nước biển xanh m&agrave;u ngọc b&iacute;ch, phản chiếu long lanh dưới &aacute;nh nắng mặt trời.<br>- Tham gia<strong>&nbsp;c&aacute;c hoạt động giải tr&iacute; tr&ecirc;n biển:</strong> kayak, jetski, d&ugrave; lượn, phao chuối (chi ph&iacute; tự t&uacute;c).<br>- Vui chơi tại&nbsp;<strong>c&ocirc;ng vi&ecirc;n nước Aquatopia:&nbsp;</strong>một trong những c&ocirc;ng vi&ecirc;n nước chủ đề h&agrave;ng đầu Đ&ocirc;ng Nam &Aacute;, trải nghiệm 6 khu check-in chủ đề hấp dẫn mang phong c&aacute;ch thổ d&acirc;n huyền b&iacute;.<br><br><strong>L&atilde;ng du tr&ecirc;n con đường Thị trấn Ho&agrave;ng H&ocirc;n</strong> - được thiết kế với kiến tr&uacute;c độc đ&aacute;o, rực rỡ sắc m&agrave;u của một thị trấn ch&acirc;u &Acirc;u cổ k&iacute;nh: check-in&nbsp;<strong>&lsquo;1001&rsquo; kiểu bậc thang, Quảng trường La M&atilde;,&nbsp;</strong>&ldquo;kỳ quan&rdquo;&nbsp;<strong>Khải Ho&agrave;n M&ocirc;n,</strong> <strong>Th&aacute;p đồng hồ cao 75m</strong> - lấy &yacute; tưởng từ th&aacute;p chu&ocirc;ng St. Mark&rsquo;s Campanile của &Yacute;; chi&ecirc;m ngưỡng&nbsp;<strong>Kiss Bridge</strong> - c&acirc;y cầu nổi l&ecirc;n như một dải lụa giữa bờ biển trong xanh, với hai nhịp cầu vươn m&igrave;nh &ocirc;m trọn bờ c&aacute;t trắng, hướng đến nhau, hợp v&agrave;o l&agrave;m một thể thống nhất nhưng kh&ocirc;ng chạm nhau.</p>\r\n                    <p>Buổi tối, Qu&yacute; kh&aacute;ch c&oacute; thể<strong>&nbsp;tự do&nbsp;</strong>kh&aacute;m ph&aacute; một số hạng mục nổi bật tại&nbsp;<strong>&ldquo;th&agrave;nh phố kh&ocirc;ng ngủ&rdquo; Grand World</strong> như:&nbsp;<br><strong>- C&ocirc;ng vi&ecirc;n Nghệ Thuật Đương Đại:&nbsp;</strong>sự giao thoa đặc sắc giữa nghệ thuật đương đại v&agrave; thi&ecirc;n nhi&ecirc;n Đảo Ngọc.<br><strong>- Huyền thoại Tre:</strong> c&ocirc;ng tr&igrave;nh tre lớn nhất Việt Nam.<br>- Tản bộ b&ecirc;n d&ograve;ng&nbsp;<strong>&ldquo;k&ecirc;nh đ&agrave;o Venice&rdquo;</strong> v&agrave; nh&igrave;n ngắm những chiếc thuyền Gondola, khu phố shophouse lộng lẫy sắc m&agrave;u, cổng l&acirc;u đ&agrave;i tr&aacute;ng lệ, ba c&acirc;y cầu v&ograve;m b&aacute;n nguyệt mang đậm kiến tr&uacute;c ch&acirc;u &Acirc;u ...<br>- Tham quan&nbsp;<strong>bảo t&agrave;ng Gấu Teddy:</strong> lưu giữ nhiều hiện vật qu&yacute; gi&aacute; v&agrave; t&aacute;i hiện chuyến phi&ecirc;u lưu h&agrave;i hước của &ldquo;nh&agrave; th&aacute;m hiểm t&agrave;i ba&rdquo; Teddy Jones&nbsp;<em>(chi ph&iacute; tự t&uacute;c)</em><br>- M&atilde;n nh&atilde;n với show diễn thực cảnh&nbsp;<strong>&ldquo;Tinh Hoa Việt Nam&rdquo;</strong> quy tụ hơn 200 diễn vi&ecirc;n&nbsp;<em>(chi ph&iacute; tự t&uacute;c)</em><br>- Thưởng thức bữa tiệc &aacute;nh s&aacute;ng đỉnh cao với show diễn&nbsp;<strong>&ldquo;Sắc m&agrave;u Venice&rdquo;</strong><br><br><strong>Nghỉ đ&ecirc;m tại Ph&uacute; Quốc.&nbsp;</strong><br>&nbsp;</p>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 2 - VUI CHƠI THỎA TH&Iacute;CH TẠI C&Ocirc;NG VI&Ecirc;N CHỦ ĐỀ VINWONDERS PH&Uacute; QUỐC - KHU BẢO TỒN THI&Ecirc;N NHI&Ecirc;N SAFARI (Ăn s&aacute;ng, trưa, chiều)</h3>\r\n    <div>\r\n        <div>\r\n            <p>Qu&yacute; kh&aacute;ch bắt đầu h&agrave;nh tr&igrave;nh trở về với thi&ecirc;n nhi&ecirc;n tại<strong>&nbsp;Vinpearl Safari Ph&uacute; Quốc</strong> - Vườn th&uacute; b&aacute;n hoang d&atilde; đầu ti&ecirc;n tại Việt Nam: với quy m&ocirc; 180 ha, hơn 130 lo&agrave;i động vật qu&yacute; hiếm, du kh&aacute;ch thưởng thức c&aacute;c chương tr&igrave;nh biểu diễn, chụp ảnh với động vật; trải nghiệm vườn th&uacute; mở trong rừng tự nhi&ecirc;n, gần gũi v&agrave; th&acirc;n thiện với con người.</p>\r\n            <p>Buổi chiều, Qu&yacute; kh&aacute;ch tham quan kh&aacute;m ph&aacute;<strong>&nbsp;Khu vui chơi giải tr&iacute; VinWonders - C&ocirc;ng vi&ecirc;n chủ đề lớn nhất Việt Nam, quy m&ocirc; h&agrave;ng đầu Ch&acirc;u &Aacute;</strong>, du kh&aacute;ch sẽ được:<br>- Trải nghiệm 12 nền văn minh nh&acirc;n loại từ cổ ch&iacute; kim;<br>- Kh&aacute;m ph&aacute; 06 ph&acirc;n khu với hơn 100 hoạt động giải tr&iacute; si&ecirc;u độc lạ;<br>- Cuồng nhiệt tại&nbsp;<strong>C&ocirc;ng Vi&ecirc;n Nước lớn nhất Đ&ocirc;ng Nam &Aacute;;</strong><br>- Chinh phục 20 tr&ograve; chơi mạo hiểm khủng nhất h&agrave;nh tinh như đường trượt nhanh nhất thế giới&nbsp;<strong>&ldquo;Cơn thịnh nộ của Zeus&rdquo;, đường trượt kh&ocirc; &ldquo;Cuộc chiến Chằn tinh&rdquo;&nbsp;</strong>cao 15m đầu ti&ecirc;n của Việt Nam, đường trượt trong nh&agrave; tối&nbsp;<strong>&ldquo;Chiến binh Đại b&agrave;ng&rdquo; v&agrave; &ldquo;Lời nguyền &Aacute;c long&rdquo;&nbsp;</strong>- tr&ograve; chơi tương t&aacute;c trong nh&agrave; tối đầu ti&ecirc;n của Việt Nam,&hellip;<br>- Ngỡ ng&agrave;ng với&nbsp;<strong>show diễn triệu đ&ocirc; Once</strong> đẳng cấp thế giới;<br>- Cho&aacute;ng ngợp trước thế giới đại dương huyền ảo với tầm nh&igrave;n v&ocirc; cực qua tấm k&iacute;nh acrylic khổng lồ 8x25m b&ecirc;n trong&nbsp;<strong>Cung điện Hải vương - thủy cung h&igrave;nh r&ugrave;a lớn nhất thế giới.</strong><br><br>Qu&yacute; kh&aacute;ch thưởng thức bữa tối tại&nbsp;<strong>VinWonders.&nbsp;</strong><br><br><strong>Nghỉ đ&ecirc;m tại Ph&uacute; Quốc.</strong><br>&nbsp;</p>\r\n        </div>\r\n    </div>\r\n</div>\r\n<div>\r\n    <h3>Ng&agrave;y 3 - PH&Uacute; QUỐC - TP HỒ CH&Iacute; MINH (Ăn s&aacute;ng)</h3>\r\n    <div>\r\n        <div>\r\n            <p>Q&uacute;y kh&aacute;ch d&ugrave;ng bữa s&aacute;ng, thư gi&atilde;n tại kh&aacute;ch sạn đến giờ trả ph&ograve;ng. Sau đ&oacute;, xe đưa đo&agrave;n tham quan v&agrave; mua sắm đặc sản nổi tiếng tại&nbsp;<strong>Nh&agrave; th&ugrave;ng nước mắm Hồng Đức 1, Đặc sản Đức Thạnh, Cơ sở sản xuất ngọc trai Ph&uacute; Quốc&nbsp;</strong>- Trưng b&agrave;y c&aacute;c trang sức bằng ngọc trai ch&iacute;nh hiệu được nu&ocirc;i cấy tại Ph&uacute; Quốc.<br><br>Xe đưa Qu&yacute; kh&aacute;ch ra s&acirc;n bay đ&aacute;p chuyến bay trở về TP Hồ Ch&iacute; Minh. Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh&nbsp;<a href="https://travel.com.vn/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">du lịch</a> tại s&acirc;n bay T&acirc;n Sơn Nhất.<br>&nbsp;</p>\r\n            <p><strong>*Lưu &yacute;:&nbsp;<br>Kh&aacute;ch c&oacute; quốc tịch nước ngo&agrave;i (t&ecirc;n nước ngo&agrave;i &amp; việt kiều đ&atilde; đổi t&ecirc;n ho&agrave;n to&agrave;n)&nbsp;<br>- Phụ thu 850,000 VNĐ/kh&aacute;ch * 2 kh&aacute;ch/ph&ograve;ng<br>- Trường hợp 1 kh&aacute;ch quốc tịch Việt Nam + 1 kh&aacute;ch quốc tịch nước ngo&agrave;i vẫn phụ thu 850,000 VNĐ/kh&aacute;ch * 2 kh&aacute;ch/ph&ograve;ng</strong><br><br><strong>Phụ thu gi&aacute; v&eacute; tham quan đối với trẻ em dưới 12 tuổi cao tr&ecirc;n 1m4&nbsp;<br>- Combo H&ograve;n Thơm: phụ thu 300,000 VNĐ/kh&aacute;ch<br>- Combo Vinwonder + Safari: phụ thu 300,000 VNĐ/kh&aacute;ch<br>Đối với c&aacute;c kh&aacute;ch kh&ocirc;ng phụ thu khi mua tour, khi c&oacute; ph&aacute;t sinh tại điểm tham quan, kh&aacute;ch tự chịu chi ph&iacute; ph&aacute;t sinh tại điểm tham quan.</strong></p>\r\n            <p><br><em>- Giờ bay phụ thuộc v&agrave;o h&atilde;ng h&agrave;ng kh&ocirc;ng, số bữa ăn phụ thuộc v&agrave;o giờ bay. C&aacute;c bữa ăn c&oacute; thể ho&aacute;n đổi để ph&ugrave; hợp với chương tr&igrave;nh.<br>- H&agrave;nh tr&igrave;nh c&oacute; thể thay đổi thứ tự điểm đến t&ugrave;y v&agrave;o điều kiện thực tế<br>- Đ&acirc;y l&agrave; chương tr&igrave;nh du lịch trọn g&oacute;i,&nbsp;<a href="https://www.vietravel.com/?utm_source=internalbl&utm_medium=click&utm_campaign=ATLinks" target="_blank">Vietravel</a> kh&ocirc;ng c&oacute; tr&aacute;ch nhiệm ho&agrave;n trả chi ph&iacute; ch&ecirc;nh lệch cho c&aacute;c kh&aacute;ch h&agrave;ng thuộc diện miễn giảm hoặc đối tượng ưu ti&ecirc;n như trẻ nhỏ, người lớn tuổi, người c&oacute; c&ocirc;ng với c&aacute;ch mạng&hellip; Ch&iacute;nh s&aacute;ch miễn giảm, ưu ti&ecirc;n (nếu c&oacute;) chỉ d&agrave;nh cho kh&aacute;ch lẻ của c&aacute;c điểm tham quan</em></p>\r\n        </div>\r\n    </div>\r\n</div>', 147, '2023-08-10 00:00:00.000000', 'TP. Hồ Chí Minh', 'Máy bay', 1, 'Phú Quốc: VinWonders - Vinpearl Safari - Hòn Thơm Nature Park - Cáp Treo Vượt Biển - Công Viên Nước Aquatopia | Kích cầu du lịch', '3 ngày 2 đêm', 'Quanh năm', 'Đã bao gồm ưu đãi trong giá tour');
